import React, { Component } from 'react'
import './newslide.css';
import auth from '../../../services/AuthServices';
import {
    Form,
    Input,
    Button,
    Upload,
    Icon,

    //Select
} from 'antd';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import { connect } from 'react-redux';
import { 
    ChangeSlideConfirmDirty,
    ChangeSlideModalState,
    ChangeSlideTableData
} from '../../../actions/chapterAction';
import Alert from '../../../components/common/alert';
//const { Option } = Select;
class NewSlide extends Component {
    constructor(props){
        super(props);
        this.state={
            materialId:null,
            slideDetails:{
                slideimage: this.props.chapter.slidedetails.slimage,
            },
            submitDisabled:false,
        }
    }

    componentDidMount(){
        this.setState({
            materialId:this.props.mid
        });
    }


    SlideImageonChange = (f)=>{
        var newSlide = this.state.slideDetails.slideimage
        if(!f){
            newSlide=null
        }
        else{
            newSlide=`${apis.BASE}/${f.link}`
            
        }
        this.setState({
            submitDisabled:false
        })
        if(newSlide==='undefined' || newSlide===undefined || newSlide===null || newSlide==='null') {
            //(newOptions[i].body==='undefined' || newOptions[i].body===undefined || newOptions[i].body==='null' || newOptions[i].body==='' || newOptions[i].body===null)){
                newSlide=this.state.slideDetails.slideimage;    
        }
        this.setState((ps,pp)=>{
            return({
                slideDetails:{
                    ...ps.slideDetails,
                    slideimage:newSlide
                }
            })
        })
    }

    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
                console.log(this.state.materialId);
                console.log('Полученные значения формы: ', values);
                
                    SecurePost({
                        url : `${apis.CREATE_SLIDES}`,
                        data : {
                            _id : this.props.chapter.slideId,
                            slbody : values.slbody,                            
                            slimage : this.state.slideDetails.slideimage,
                            material : this.state.materialId
                        }
                    }).then((response)=>{
                        if(response.data.success){
                            this.props.ChangeSlideModalState(false,null,'Регистрация');
                            Alert('success','Успешно',response.data.message);
                            this.props.ChangeSlideTableData();
                        }
                        else{
                            console.log(response.data);
                            this.props.ChangeSlideModalState(false,null,'Регистрация');
                            return Alert('warning','Предупреждение!',response.data.message);
                        }
                    }).catch((error)=>{
                        this.props.ChangeSlideModalState(false,null,'Регистрация');
                        return Alert('error','Ошибка!','Ошибка сервера');
                    })
                
            }
        });
    };

    changeqImage = (f)=>{
        this.setState((ps,pp)=>{
            return({
                slideDetails:{
                    ...ps.slideDetails,
                    slideimage:(f.link ?`${apis.BASE}/${f.link}`:null)
                },
                submitDisabled:false
            })
        })
    }

    upl=()=>{
        this.setState({
            submitDisabled: false
        })
    }

    render() {
        const { getFieldDecorator } = this.props.form;
        const { TextArea } = Input;
        var SlideImageprops={
            name: 'file',
            action: `${apis.BASE}${apis.FILE_UPLOAD}?Token=${auth.retriveToken()}`,
            listType: 'picture',
        }
        return (
            <div className="register-slide-form">
                <div className="register-slide-form-body">
                    <Form  onSubmit={this.handleSubmit}>
                        <Form.Item label="Тело" hasFeedback className="input-admin-slide">
                            {getFieldDecorator('slbody', {
                                initialValue : this.props.chapter.slidedetails.slbody,
                                rules: [{ required: true, message: 'Пожалуйста, введите название подразделения!', whitespace: true }],
                            })(<TextArea rows={5} />)}
                        </Form.Item>
                    
                        <Form.Item label="Слайд">
                            <Upload {...SlideImageprops} beforeUpload={this.upl} onRemove={(f)=>this.SlideImageonChange(null)} onSuccess={(f)=>this.SlideImageonChange(f)}>
                                {getFieldDecorator('slimage', {
                                    initialValue : this.props.chapter.slidedetails.slimage,
                                    rules: [{ required: false, message: 'Пожалуйста, загрузите ваш аватар!'}],
                                })(<img src={this.props.chapter.slidedetails.slimage} style={{width: "60px",height: "80px",}} alt=""/>)
                                }
                                
                                <Button style={{marginLeft: '100px'}}>
                                    <Icon type="upload" /> Загрузить
                                </Button>
                                
                            </Upload>
                        </Form.Item>

                        
                        <Form.Item>
                            <Button type="primary" htmlType="submit" block>
                                {this.props.chapter.Slidemode}
                            </Button>
                        </Form.Item>
                    </Form>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    admin : state.admin,
    chapter : state.chapter
});



const NewSlideForm = Form.create({ name: 'register' })(NewSlide);

export default connect(mapStateToProps,{
    ChangeSlideConfirmDirty,
    ChangeSlideModalState,
    ChangeSlideTableData
})(NewSlideForm);
