import React, { Component, useState, useEffect } from 'react';
import { Table, Button, Divider, Modal } from 'antd';
import { connect } from 'react-redux';
import { 
    ChangeMaterialSearchText,
    ChangeMaterialTableData,
    ChangeMaterialModalState,
    ChangeAnswersSearchText,
    ChangeAnswersTableData,
    ChangeSlideTableData
} from '../../../actions/chapterAction';
import './chapters.css';
import { SecureGet, SecurePost } from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import SliderShow from './slider';
import Test from '../Testing/test';
import Result from '../Testing/result';
//import Alert from '../../../components/common/alert';
import  LocalizedStrings  from  'react-localization' ;

let strings = new LocalizedStrings({
  ru:{
    Names:"Наименование",
    Actions:"Действие",
    Learning:"Урок",
    Tests:"Тест",
    Results:"Результаты",
  },
  kz: {
    
  }
 });

function Mat(props){
// class Materials extends Component {
  // constructor(props){
  //   super(props);
  //   this.state={
  //     materialId : null,
  //     slideModelVisible : false,
  //     testModelVisible : false,
  //     resultModelVisible : false,
  //     details: null,
  //     loading:true,
  //     disabled:false,
  //   }
  // }
  const [materialId, setMaterialId] = useState(null);
  const [slideModelVisible, setSlideModelVisible] = useState(false);
  const [testModelVisible, setTestModelVisible] = useState(false);
  const [resultModelVisible, setResultModelVisible] = useState(false);
  const [details, setDetails] = useState(null);
  const [disabled, setDisabled] = useState(props.chapter.slideTableData.flag);
  const [flag, setFlag] = useState(false);
  const [testflag, setTestFlag] = useState(false);

  const OpendetailsModal = (id)=>{
    setMaterialId(id);
    setSlideModelVisible(true);
    setTestFlag(true);
    console.log("Flag",testflag)       
    SecurePost({
        url : `${apis.UPDATE_MATERIAL}`,
        data : {
            _id : id,
            flag:true,
            testflag:true,
        }
    })
  }
  console.log("Flag",testflag) 
  const ClosedetailsModal = ()=>{
    setMaterialId(null);
    setSlideModelVisible(false);
  }

  const OpentestModal = (id)=>{
    setMaterialId(id);
    setTestModelVisible(true);
    // setTestFlag(true);
    console.log("ASDF47", props.chapter.testTableData)
  }

  const ClosetestModal = (id)=>{
    setMaterialId(null);
    setTestModelVisible(false);
    console.log("ASFD", id)
    SecurePost({
        url : `${apis.UPDATE_MATERIAL}`,
        data : {
            _id : id,
            flag:true,
            testflag:true,
            resflag:true
        }
    }) 
  }

  
  const OpenresultModal = (id)=>{
    setMaterialId(id);
    setResultModelVisible(true);
  }
  
  const CloseresultModal = ()=>{
    setMaterialId(null);
    setResultModelVisible(false);
  }


  useEffect(()=>{
    props.ChangeSlideTableData(props.key1);
    console.log("SlideTable",props.chapter.slideTableData);


  }, [])


console.log()
    return(
        <div className="admin-table-container">

            {/* {console.log("MATD:", props.matd)}
            {props.matd.map((d,i)=>{console.log("AAAAAAA", d._id)})}
            {console.log("BBBBBBBBBBB:", props.key1)}
            {console.log(props.matd.filter(d=>d._id==props.key1))} */}


        <Button disabled={!props.matd.filter(d=>d._id==props.key1)[0].flag} type="primary" icon="" onClick={()=>OpendetailsModal(props.key1)}>{strings.Learning}</Button>
        <Divider type="vertical" />
        <Button disabled={!props.matd.filter(d=>d._id==props.key1)[0].testflag} type="primary" icon="" onClick={()=>OpentestModal(props.key1)}>{strings.Tests}</Button>
        <Divider type="vertical" />
        <Button disabled={!props.matd.filter(d=>d._id==props.key1)[0].resflag} type="primary" icon="" onClick={()=>OpenresultModal(props.key1)}>{strings.Results}</Button>

        {/* <Table 
          bordered={true} 
          columns={columns} 
          dataSource={props.chapter.materialTableData} 
          size="medium" 
          pagination={{ pageSize: 5 }}
          loading={props.chapter.materialTableLoadingStatus}
          rowKey="_id" 
        /> */}

        <Modal
          visible={slideModelVisible}
          title="Урок"
          onCancel={ClosedetailsModal}
          style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
          width="85%"
          destroyOnClose={true}
          footer={[]}
        >
          <SliderShow id={materialId} answerDetails={props.answerDetails} flag={flag}/>
        </Modal>

        <Modal
          visible={testModelVisible}
          title="Тест"
          onCancel={()=>ClosetestModal(materialId)}
          style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
          width="80%"
          destroyOnClose={true}
          footer={[]}
        >
          {/* {console.log("Aktilek:", this.props)} */}
          <Test materialDetails={props.matd} id={materialId} chid={props.id}
          // asn={this.props.chapter.AnswerTableData}
          />
        </Modal>

        <Modal
          visible={resultModelVisible}
          title="Результат"
          onCancel={CloseresultModal}
          style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
          width="80%"
          destroyOnClose={true}
          footer={[]}
        >
          <Result answerDetails={props.answerDetails}/>
        </Modal>
      </div>
    )
  
}
const mapStateToProps = state => ({
    chapter : state.chapter
  });
  
  export default connect(mapStateToProps,{
      ChangeMaterialSearchText,
      ChangeMaterialTableData,
      ChangeMaterialModalState,
      ChangeAnswersSearchText,
      ChangeAnswersTableData,
      ChangeSlideTableData
  })(Mat);
