import React, { Component, useState, useEffect } from 'react';
import { Table, Button, Typography, Icon, Divider, Modal, Popconfirm, Tabs} from 'antd';
// import Highlighter from 'react-highlight-words';
import { connect } from 'react-redux';
import { 
    ChangeMaterialSearchText,
    ChangeMaterialTableData,
    ChangeMaterialModalState
} from '../../../actions/chapterAction';
import '../Chapter/allchapter.css';
import Alert from '../../../components/common/alert';
import {SecurePost, SecureGet} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import NewMaterialForm from '../Materials/newmaterials';
// import {updateQuestiosnActiveTest } from '../../../actions/trainerAction';
//import SingleQuestion from '../../trainee/examPortal/singleQuestion';
import AllQuestions from '../Questions/allquestion';
import AllSlides from '../Slides/allslides';
 //import AllTests from '../../trainer/alltests/alltest';
/// import NewTest from '../../trainer/newtest/newtest';

// const {TabPane} = Tabs;


class AllMaterial extends Component {

  constructor(props){
    super(props);
    this.state={
      materialId : null,
      materialModelVisible:false,
     // questionId : null,
      slideModelVisible : false,
      testModelVisible : false,
      MaterialtableLoading : false,
      details: null,
      loading:true,
      chapterId:null
    }
  }
  tabChange = (key)=>{
    console.log(key)
  }

  OpenQuestiondetailsModal = (id)=>{

    this.setState((previousState,previousProps)=>{
      return{
        materialId:id,
        materialModelVisible:true
      }
    })
  }

  CloseQuestiondetailsModal = ()=>{
    this.setState((previousState,previousProps)=>{
      return{
        materialId:null,
        materialModelVisible:false
      }
    })
  }

  OpenSlidedetailsModal = (id)=>{
    this.setState((previousState,previousProps)=>{
      return{
        materialId:id,
        slideModelVisible:true
      }
    })
  }

  CloseSlidedetailsModal = ()=>{
    this.setState((previousState,previousProps)=>{
      return{
        materialId:null,
        slideModelVisible:false
      }
    })
  }

  OpenTestdetailsModal = (id)=>{
    this.setState((previousState,previousProps)=>{
      console.log(id)
      return{
        materialId:id,
        testModelVisible:true
      }
    })
  }

  CloseTestdetailsModal = ()=>{
    this.setState((previousState,previousProps)=>{
      return{
        materialId:null,
        testModelVisible:false
      }
    })
  }


  componentDidMount(){
    var ID = this.props.id;
    this.props.ChangeMaterialTableData(ID);
        // SecureGet({
        //     url: `${apis.GET_SINGLE_CHAPTER_DETAILS}/${ID}`,
        // }).then((response)=>{
        //     console.log(response.data.data[0]);
        //     this.setState({
        //         details : response.data.data[0],
        //         loading : false
        //     })
        // }).catch((error)=>{
        //     console.log(error);
        // })
  }


  openModal = (id,mode)=>{
    this.props.ChangeMaterialModalState(true, id, mode);
  }

  closeModal = ()=>{
    this.props.ChangeMaterialModalState(false, null, 'Создать');
  }


  deleteMaterial = (id)=>{
    SecurePost({
      url : `${apis.DELETE_MATERIAL}`,
      data : {
          _id : id
      }
    }).then((response)=>{
      if(response.data.success){
        Alert('success','Успешно',response.data.message);
      }
      else{
        return Alert('warning','Предупреждение!',response.data.message);
      }
    }).catch((error)=>{
      return Alert('error','Ошибка!','Ошибка сервера');
    })
  }


    render() {
      // const { Title } = Typography;
      const columns = [
        {
          title: '№',
          dataIndex: 'num',
          key: 'num',
          width: '5%',
        },   
        {
          title: 'Наименование',
          dataIndex: 'topic',
          key: 'topic',
          width: '5%',
        }, 
        {
          title: 'Продолжительность теста',
          dataIndex: 'duration',
          key: 'duration',
          width: '5%',
        },
        {
          title: 'Количество попыток',
          dataIndex: 'attempts',
          key: 'attempts',
          width: '5%',
        },
        
        {
          title: 'Период ожидания, дней',
          dataIndex: 'period',
          key: 'period',
          width: '5%',
        },
        {
          title: 'Количество вопросов для отображения пользователю',
          dataIndex: 'numQuestions',
          key: 'numQuestions',
          width: '5%',
        },  
        
        {
          title: 'Действие',
          key: '_id',
          dataIndex: '_id',
          width: '25%',
          render: (key) => (
            <span>
              <Button type="primary" shape="circle" icon="edit" onClick={()=>this.openModal(key,'Сохранить' )}/>
                <Divider type="vertical" />
                <Popconfirm
                  title="Вы уверены？"
                  cancelText="Нет"
                  okText="Да"
                  onConfirm={()=>{this.deleteMaterial(key)}}
                  icon={<Icon type="delete" style={{ color: 'red' }} />}
                >
                  <Button type="danger" shape="circle" icon="delete" />
                </Popconfirm>
               <Divider type="vertical" />
                <Button type="primary" icon="" onClick={()=>this.OpenSlidedetailsModal(key)}>Слайды</Button>
                <Divider type="vertical" />
                <Button type="primary" icon="" onClick={()=>this.OpenQuestiondetailsModal(key)}>Вопросы</Button>
            </span>
          ),
        },
      ];
      return(
        <div className="admin-table-container">
              
                <Button type="primary" icon="user-add" style={{marginBottom:'10px'}} onClick={()=>this.openModal(null,'Создать')}>
                  Создать новый материал
                </Button>
                <Table 
                bordered={true} 
                columns={columns} 
                dataSource={this.props.chapter.materialTableData} 
                size="medium" 
                pagination={{ pageSize: 10 }}
                loading={this.props.chapter.materialTableLoadingStatus}
                rowKey="_id" 
              ></Table>
                <Modal
                      visible={this.props.chapter.MaterialmodalOpened}
                      title={false}
                      onOk={this.handleOk}
                      onCancel={this.closeModal}
                      style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
                      width="40%"
                      destroyOnClose={true}
                      footer={[]}
                  >{console.log('OOOOOOOOOOOOOOOOOOOOOOOOOO:',this.props.id)}
                  <NewMaterialForm cid={this.props.id}/>
                </Modal> 
            <Modal
                visible={this.state.slideModelVisible}
                title="Слайды"
                onCancel={this.CloseSlidedetailsModal}
                style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
                width="70%"
                destroyOnClose={true}
                footer={[]}
              >
                <AllSlides id={this.state.materialId} />
            </Modal>

            <Modal
                visible={this.state.materialModelVisible}
                title="Вопросы"
                onCancel={this.CloseQuestiondetailsModal}
                style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
                width="70%"
                destroyOnClose={true}
                footer={[]}
              >
                <AllQuestions id={this.state.materialId} />
            </Modal>
{/* 
            <Modal
                visible={this.state.testModelVisible}
                title="Вопросы"
                onCancel={this.CloseTestdetailsModal}
                style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
                width="70%"
                destroyOnClose={true}
                footer={[]}
              >
                <AllTests id={this.state.materialId} />
            </Modal> */}
 

        </div>
      )}
}

const mapStateToProps = state => ({
  chapter : state.chapter,
  trainer : state.trainer
});

export default connect(mapStateToProps,{
    ChangeMaterialSearchText,
    ChangeMaterialTableData,
    ChangeMaterialModalState,
})(AllMaterial);
