import React, { Component } from 'react';
import { Table, Input, Button, Typography, Divider,Icon, Modal, Popconfirm } from 'antd';
import Highlighter from 'react-highlight-words';
import { connect } from 'react-redux';
import { 
    ChangeCompanySearchText,
    ChangeCompanyTableData,
    ChangeCompanyModalState
} from '../../../actions/adminAction';
import './allcompany.css'
import Alert from '../../../components/common/alert';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import moment from 'moment';
import NewCompanyForm from './newcompany';


class AllCompany extends Component {

  constructor(props){
    super(props);
    this.state={
      CompanytableLoading : false
    }
  }

  openModal = (id,mode)=>{
    this.props.ChangeCompanyModalState(true, id, mode);
  }

  closeModal = ()=>{
    this.props.ChangeCompanyModalState(false,null,'Регистрация');
  }

  componentDidMount(){
    this.props.ChangeCompanyTableData();
  }

  deleteCompany = (id)=>{
    SecurePost({
      url : `${apis.DELETE_COMPANY}`,
      data : {
          _id : id
      }
    }).then((response)=>{
      if(response.data.success){
        Alert('success','Успешно',response.data.message);
        this.props.ChangeCompanyTableData();
      }
      else{
        return Alert('warning','Предупреждение!',response.data.message);
      }
    }).catch((error)=>{
      return Alert('error','Ошибка!','Ошибка сервера');
    })
  }

    getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.props.admin.CompanysearchText]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });
    
      handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.props.ChangeCompanySearchText(selectedKeys[0])
      };
    
      handleReset = clearFilters => {
        clearFilters();
        this.props.ChangeCompanySearchText('')
      };
      handleDivisionChange =(s)=>{
        //this.props.ChangeSelectedDivisions(s);
        this.props.ChangeCompanyTableData(s);
      }

    render() {
      const { Title } = Typography;
      const columns = [
      
        {
          title: 'Наименование',
          dataIndex: 'name',
          key: 'name',
          width: '5%',
          ...this.getColumnSearchProps('name'),
        },
        {
            title: 'Логотип',
            dataIndex: 'logo',
            key: 'logo',
            render: (key) => (
  
              <div>
              <img src={key} style={{height: "60px",}} alt=""/>
                           
              </div>
            )
          },

        {
          title: 'Описание',
          dataIndex: 'descr',
          key: 'descr',
          width: '20%',
          ...this.getColumnSearchProps('descr'),
        },  
        
        {
          title: 'Дата начала действия лицензии',
          dataIndex: 'licFromDate',
          key: 'licFromDate',
          width: '15%',
          
          render: (key) => (
            <span>
              
              <Button status="success">{Date.parse(key)}{moment(Date.parse(key)).format("DD.MM.YYYY")}</Button>
              
            </span>
          ),
          // filterComponent: (props) => <CustomDatePicker {...props} />
        },

        {
          title: 'Дата окончания действия лицензии',
          dataIndex: 'licToDate',
          key: 'licToDate',
          width: '15%',
          ...this.getColumnSearchProps('licToDate'),
        }, 
        {
          title: 'Количество лицензированных пользователей',
          dataIndex: 'numOfLic',
          key: 'numOfLic',
          width: '10%',
          ...this.getColumnSearchProps('numOfLic'),
        },
          
        {
          title: 'Количество использованных учетных записей в рамках лицензии',
          dataIndex: 'numOfLicAcc',
          key: 'numOfLicAcc',
          width: '10%',
          ...this.getColumnSearchProps('numOfLicAcc'),
        }, 
          
        {
          title: 'Контактное лицо',
          dataIndex: 'contact',
          key: 'contact',
          width: '10%',
          ...this.getColumnSearchProps('contact'),
        }, 
        {
          title: 'Действие',
          key: '_id',
          dataIndex: '_id',
          width: '25%',
          render: (key) => (
            <span>
              <Button type="primary" shape="circle" icon="edit" onClick={()=>this.openModal(key,'Сохранить')}/>
                <Divider type="vertical" />
                <Popconfirm
                  title="Вы уверены？"
                  cancelText="Нет"
                  okText="Да"
                  onConfirm={()=>{this.deleteCompany(key)}}
                  icon={<Icon type="delete" style={{ color: 'red' }} />}
                >
                  <Button type="danger" shape="circle" icon="delete" />
                </Popconfirm>
              
            </span>
          ),
        },
      ];
        return (
            <div className="admin-table-container">
              <Button type="primary" icon="user-add" style={{marginBottom:'10px'}} onClick={()=>this.openModal(null,'Регистрация')}>
                Добавить
              </Button> 
              
              <div className="register-company-form-header">
                <Title level={4} style={{color:'#fff',textAlign:'center'}}>Список компании</Title>
              </div>
              <Table 
                bordered={true} 
                columns={columns} 
                dataSource={this.props.admin.companyTableData} 
                size="medium" 
                pagination={{ pageSize: 5 }}
                loading={this.props.admin.companyTableLoadingStatus}
                rowKey="_id" 
              >
                
            </Table>;
              <Modal
                visible={this.props.admin.CompanymodalOpened}
                title={false}
                onOk={this.handleOk}
                onCancel={this.closeModal}
                style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
                width="40%"
                destroyOnClose={true}
                footer={[
                  
                ]}
              >
                <NewCompanyForm />
              </Modal>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    admin : state.admin
});

export default connect(mapStateToProps,{
    ChangeCompanySearchText,
    ChangeCompanyTableData,
    ChangeCompanyModalState,
})(AllCompany);
