import React, { Component } from 'react';
import { Table, Button, Divider, Modal } from 'antd';
import { connect } from 'react-redux';
import { 
    ChangeMaterialSearchText,
    ChangeMaterialTableData,
    ChangeMaterialModalState,
    ChangeAnswersSearchText,
    ChangeAnswersTableData,
    ChangeTestSearchText,
    ChangeTestTableData,
    ChangeTestModalState
} from '../../../actions/chapterAction';
import { 
  ChangeQuestionSearchText,
  ChangeQuestionTableData,
  ChangeQuestionModalState,
} from '../../../actions/trainerAction';
import './chapters.css';
import { SecureGet, SecurePost } from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import SliderShow from './slider';
import Test from '../Testing/test';
import Alert from '../../../components/common/alert';
class Materials extends Component {
  constructor(props){
    super(props);
    this.state={
      materialId : null,
      slideModelVisible : false,
      testModelVisible : false,
      details: null,
      loading:true
    }
  }

  OpendetailsModal = (id)=>{
    console.log("OpendetailsModal");
    this.props.ChangeQuestionTableData(id);
    this.props.ChangeAnswersTableData(id);
    this.setState((previousState,previousProps)=>{
      return{
        materialId:id,
        slideModelVisible:true
      }
    })
  }

  ClosedetailsModal = ()=>{
    this.setState((previousState,previousProps)=>{
      return{
        materialId:null,
        slideModelVisible:false
      }
    })
  }

  OpentestModal = (id)=>{
    console.log("OpentestModal");
    this.setState((previousState,previousProps)=>{
      return{
        materialId:id,
        testModelVisible:true
      }
    })

    console.log("1  CREATE_TEST")
    SecurePost({
      url : `${apis.CREATE_TEST}`,
      data : {
        _id: this.props.chapter.testid,
          chapterid : this.props.chid,
          materialid : this.props.id,
          startDate : Date().toLocaleString(),
          complationDate : Date().toLocaleString(),
          startTest: true,
          endTest: false,
          result: 0
        }
      })
      .then((response)=>{
        if(response.data.success){
            this.props.ChangeTestModalState(false,null, 'Save'); 
            Alert('success','Успешно',response.data.message);
        }
        else{
            this.props.ChangeTestModalState(false,null, 'Save');
            return Alert('warning','Предупреждение!',response.data.message);
        }
      })
      .catch((error)=>{
          this.props.ChangeTestModalState(false,null, 'Save');
          return Alert('error','Ошибка!','Ошибка сервера');
      });
  }

  ClosetestModal = ()=>{
    this.setState((previousState,previousProps)=>{
      return{
        materialId:null,
        testModelVisible:false
      }
    })
  }


  componentDidMount(){
    var ID = this.props.id;
    console.log('ChangeMaterialTableData',this.props)
    this.props.ChangeMaterialTableData(ID);

    SecureGet({
       url: `${apis.GET_SINGLE_CHAPTER_DETAILS}/${ID}`,
    }).then((response)=>{
      console.log(response.data.data[0]);
      this.setState({
        details : response.data.data[0],
        loading : false
      })
    }).catch((error)=>{
      console.log(error);
    })

  }


  render() {
    // const { Title } = Typography;
    const columns = [
      {
        title: '№',
        dataIndex: 'num',
        key: 'num',
        width: '5%',
      },   
      {
        title: 'Наименование',
        dataIndex: 'topic',
        key: 'topic',
        width: '5%',
      },   
      {
        title: 'Действие',
        key: '_id',
        dataIndex: '_id',
        width: '25%',
        render: (key) => (
          <span>
            <Button type="primary" icon="" onClick={()=>this.OpendetailsModal(key)}>Уроки</Button>
            <Divider type="vertical" />
            <Button type="primary" icon="" onClick={()=>this.OpentestModal(key)}>Тест</Button>
          </span>
        ),
      },
    ];

    return(
      <div className="admin-table-container">
        <Table 
          bordered={true} 
          columns={columns} 
          dataSource={this.props.chapter.materialTableData} 
          size="medium" 
          pagination={{ pageSize: 5 }}
          loading={this.props.chapter.materialTableLoadingStatus}
          rowKey="_id" 
        />

        <Modal
          visible={this.state.slideModelVisible}
          title="Уроки"
          onCancel={this.ClosedetailsModal}
          style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
          width="85%"
          destroyOnClose={true}
          footer={[]}
        >
          <SliderShow id={this.state.materialId} />
        </Modal>

        <Modal
          visible={this.state.testModelVisible}
          title="Тест"
          onCancel={this.ClosetestModal}
          style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
          width="80%"
          destroyOnClose={true}
          footer={[]}
        >{console.log('ffffffffggggggg', this.props.trainer.QuestionTableData)}
          <Test details1={this.props.trainer.QuestionTableData} id={this.state.materialId} chid={this.props.id} asn={this.props.chapter.AnswerTableData}/>
        </Modal>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  chapter : state.chapter,
  trainer : state.trainer
});

export default connect(mapStateToProps,{
    ChangeMaterialSearchText,
    ChangeMaterialTableData,
    ChangeMaterialModalState,
    ChangeAnswersSearchText,
    ChangeAnswersTableData,
    ChangeQuestionSearchText,
    ChangeQuestionTableData,
    ChangeQuestionModalState,
})(Materials);
