class Test extends Component {
    constructor(props){
      super(props);
      this.state={
        //questions:[],
        loading:false,
        screen: true,
        count: 0,
        //clickedLabel: [],
        answer: 0,
        //error:false,
        // screenFinish:false,
        //second:60,
       // time:{},
        //checked:false,
        disableButton: false,
        testStarted:false,
        testEnded:false,
        testDidNotStart:true,
        matId : this.props.id,
       // details:[],
      }
    }
  
    componentDidMount(){
      console.log("1 - componentDidMount", this.state.matId)
      this.props.ChangeTestTableData(this.props.id);
      this.props.ChangeAnswersTableData(this.props.id);
    }


    renderInitial = () => {
      console.log("renderInitial")
      const { count } = this.state;

      this.setState({
        screen: false,
        loading: false,
        count: 1,
        testStarted: true,
        testEnded: false,
        testDidNotStart: false,
      });

      
    };

    renderScreenInitial = () => {
      // console.log('THIS:',this.props.chapter)
      if((this.props.chapter.testTableData === undefined)){
        console.log("1 CREATE_TEST", this.props.chapter.testId)
        SecurePost({
          url : `${apis.CREATE_TEST}`,
          data : {
            _id: this.props.chapter.testId,
              chapterid : this.props.chid,
              materialid : this.props.id,
              startDate : Date().toLocaleString(),
              complationDate : Date(),
              startTest: false,
              endTest: false,
              result: 0
            }
          })
          .then((response)=>{
            if(response.data.success){
                // this.props.ChangeTestModalState(false,null, 'Save'); 
                Alert('success','Успешно',response.data.message);
            }
            else{
                // this.props.ChangeTestModalState(false,null, 'Save');
                return Alert('warning','Предупреждение!',response.data.message);
            }
          })
          .catch((error)=>{
              // this.props.ChangeTestModalState(false,null, 'Save');
              return Alert('error','Ошибка!','Ошибка сервера');
          })
      }
        console.log("CREATE_ANSWERS", this.props.chapter.testid) 
        console.log("this.props.chapter.testTableData:", this.props.chapter.testTableData)
      if(this.props.chapter.testTableData!==null || this.props.chapter.testTableData!==undefined || this.props.chapter.testTableData!=='null' || this.props.chapter.testTableData!=='undefined'){ 
        console.log("this.props.chapter.testTableData222:", this.props.chapter.testTableData)
        SecurePost({
          url : `${apis.CREATE_ANSWERS}`,
          data : {
            materialId : this.props.id,
            // testid : this.props.chapter.testTableData._id,
          }
        });
      }
      if (this.state.testStarted==false){
        return (
          <Container>
            <ScreenQuiz screen={this.state.screen}>
              <Title>Тестирование</Title>
              {/* {console.log('ASDFGH:', new Date(this.props.chapter.testTableData.complationDate).getTime())}
              {console.log('ZXCVBN:', new Date().getTime())} */}
              <Button 
                onClick={
                  this.renderInitial
                } 
                style={{
                  fontSize: "1.2rem", 
                  fontWeight: "bold",
                  width: "40%",
                  height: "3rem",
                  border: "none",
                  borderRadius: "5px",
                  boxShadow: "0px 3px 3px 0px #666"
                }}
                >
                  Начать тест
              </Button>
              
            </ScreenQuiz>
          </Container>
        );
      } 
      else if (this.state.testStarted==true && this.state.endTest==false){
        return (
          <Container>
            <ScreenQuiz screen={this.state.screen}>
              <Title>Тестирование</Title>
              <Button 
                onClick={this.renderInitial} 
                style={{
                  fontSize: "1.2rem", 
                  fontWeight: "bold",
                  width: "40%",
                  height: "3rem",
                  border: "none",
                  borderRadius: "5px",
                  boxShadow: "0px 3px 3px 0px #666"
                }}
                >
                  Продолжить тест
              </Button>
            </ScreenQuiz>
          </Container>
        );
      }
    };




    render() {
      const { questions, screen, count, answer, disableButton } = this.state;

      console.log("SCREEN: ", screen);
      // var date1= this.props.chapter.testTableData ? this.props.chapter.testTableData.complationDate : null;
      return screen ? this.renderScreenInitial() : 
        // (new Date(date1).getTime() < new Date().getTime() || date1 !== null) ?
        //   Alert('warning','Тест закончен!', 'fdsfdgf')
        // :
          <TestPage q={this.props.chapter.AnswerTableData} id={this.props.id} count={count} screen={screen} answer={answer}/>
    }
  }
  
  const mapStateToProps = state => ({
      chapter : state.chapter,
      trainer : state.trainer
  });
  
  export default connect(mapStateToProps,{
      ChangeTestSearchText,
      ChangeTestTableData,
      // ChangeTestModalState,
      ChangeAnswersSearchText,
      ChangeAnswersTableData,
      // ChangeAnswersModalState
  })(Test);




