import React, { Component } from 'react';
import { Table, Input, Button, Typography, Divider, Icon, Modal, Popconfirm } from 'antd';
import Highlighter from 'react-highlight-words';
import { connect } from 'react-redux';
import { 
    ChangeSlideSearchText,
    ChangeSlideTableData,
    ChangeSlideModalState
} from '../../../actions/chapterAction';
import '../Division/alldivision.css'
import Alert from '../../../components/common/alert';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import { Carousel } from 'react-carousel-minimal';

class Slider extends Component {
  constructor(props){
    super(props);
    this.state={
      SlidetableLoading : false,
      carouselData : [
          {
              image : null,
              caption : null,
          }
      ]
    }
  }

//   openModal = (id,mode)=>{
//     this.props.ChangeSlideModalState(true,id,mode);
//   }

//   closeModal = ()=>{
//     this.props.ChangeSlideModalState(false,null,'Регистрация');
//   }

  componentDidMount(){
    this.props.ChangeSlideTableData(this.props.id);
  }

    
    render() {
        const data1 = this.props.chapter.slideTableData;
        // const data = [...this.state.carouselData];
        // {
        //     Object.entries(data).map((d,i)=>
        //         `${d.image} : dsds`,
        //     )
        // }
        // console.log('dsd',this.state.carouselData);
        // console.log('as', data1);

        const data = [
            {
            image: data1.slimage,
            caption: "San Francisco"
            },
            {
            image: "https://cdn.britannica.com/s:800x450,c:crop/35/204435-138-2F2B745A/Time-lapse-hyper-lapse-Isle-Skye-Scotland.jpg",
            caption: "Scotland"
            },
            {
            image: "https://static2.tripoto.com/media/filter/tst/img/735873/TripDocument/1537686560_1537686557954.jpg",
            caption: "Darjeeling"
            },
            {
            image: "https://upload.wikimedia.org/wikipedia/commons/thumb/1/16/Palace_of_Fine_Arts_%2816794p%29.jpg/1200px-Palace_of_Fine_Arts_%2816794p%29.jpg",
            caption: "San Francisco"
            }
        ];
        const captionStyle = {
            fontSize: '2em',
            fontWeight: 'bold',
        }
        const slideNumberStyle = {
            fontSize: '20px',
            fontWeight: 'bold',
        }
        return (
            <div className="App">
            <div style={{ textAlign: "center" }}>
                <h2>React Carousel Minimal</h2>
                <p>Easy to use, responsive and customizable carousel component for React Projects.</p>
                <div style={{
                padding: "0 20px"
                }}>
                <Carousel
                    data={data}
                    time={2000}
                    width="850px"
                    height="500px"
                    captionStyle={captionStyle}
                    radius="10px"
                    slideNumber={true}
                    slideNumberStyle={slideNumberStyle}
                    captionPosition="bottom"
                    automatic={true}
                    dots={true}
                    pauseIconColor="white"
                    pauseIconSize="40px"
                    slideBackgroundColor="darkgrey"
                    slideImageFit="cover"
                    thumbnails={true}
                    thumbnailWidth="100px"
                    style={{
                    textAlign: "center",
                    maxWidth: "850px",
                    maxHeight: "500px",
                    margin: "40px auto",
                    }}
                />
                </div>
            </div>
            </div>
        );
        // return (
        //     <div className="admin-table-container">
        //       {/* <Table 
        //         bordered={true} 
        //         dataSource={this.props.chapter.slideTableData} 
        //         size="medium" 
        //         pagination={{ pageSize: 5 }}
        //         loading={this.props.chapter.slideTableLoadingStatus}
        //         rowKey="_id" 
        //       />; */}
        //     </div>
        // )
    }
}

const mapStateToProps = state => ({
    admin : state.admin,
    chapter : state.chapter
});

export default connect(mapStateToProps,{
    ChangeSlideSearchText,
    ChangeSlideTableData,
    ChangeSlideModalState
})(Slider);
