import React, { Component, useState } from "react";
import { Skeleton, Row, Col, Icon, Tabs, Descriptions, Button } from 'antd';
// import './questiondetails.css';
import { 
    ChangeQuestionModalState,
    ChangeQuestionTableData,
    ChangeQuestionSearchText,
  } from '../../../actions/trainerAction';
import apis from '../../../services/Apis';
import { SecureGet } from '../../../services/axiosCall';
import moment from 'moment';
import { connect } from 'react-redux';
const { TabPane } = Tabs;

class Test extends Component {
    constructor(props){
        super(props);
        this.state={
            QuestionTableLoading : false,
        }
    }

    componentDidMount(){
        this.props.ChangeQuestionTableData(this.props.id);
    }

    render() {
        console.log(this.props.trainer.QuestionTableData);
        var questionss = this.props.trainer.QuestionTableData;
        return (
            <div>
                <Skeleton loading={this.state.loading} active avatar>
                    <Test2 details={questionss} />
                </Skeleton>                 
            </div>
        )
    }
}
