import React, { Component } from 'react';
import {
  MasterDetailsViewItem,
  MasterDetailsViewItemMaster,
  MasterDetailsView,
  MasterDetailsViewItemDetails
} from 'react-desktop/windows';
import { Skeleton, Row, Col, Tabs, Descriptions, Upload, Table, Input, Button, Typography, Divider, Icon, Modal, Popconfirm } from 'antd';
//import './questiondetails.css';
import Highlighter from 'react-highlight-words';
import { connect } from 'react-redux';
import { 
    ChangeChapterSearchText,
    ChangeChapterTableData,
    ChangeChapterModalState
} from '../../../actions/chapterAction';
//import './alldivision.css'
import Alert from '../../../components/common/alert';
//import NewChapterForm from '../newDivision/newdivision';
import apis from '../../../services/Apis';
import { SecurePost, SecureGet } from '../../../services/axiosCall';
import moment from 'moment';
import NewMaterialForm from './newmaterials';
import AllMaterial from './allmaterials';

const { TabPane } = Tabs;



export default class ChapterDetails extends Component {
    constructor(props){
        super(props);
        this.state={
            //loading : true,
            details:null
        }
    }

    tabChange = (key)=>{
        console.log(key)
    }

    componentDidMount(){
        var ID = this.props.id;
        SecureGet({
            url: `${apis.GET_SINGLE_CHAPTER_DETAILS}/${ID}`,
        }).then((response)=>{
            console.log(response.data.data[0]);
            this.setState({
                details : response.data.data[0],
                //loading:false
            })
        }).catch((error)=>{
            console.log(error);
        })
    }
    
    render() {
        return (
            <div>
              <Upload>{/*{...CustomerImageprops} beforeUpload={this.upl} onRemove={(f)=>this.CustomerImageonChange(null)} onSuccess={(f)=>this.CustomerImageonChange(f)}>
                                {getFieldDecorator('avatar', {
                                    initialValue : this.props.admin.customerdetails.avatar,
                                    rules: [{ required: false, message: 'Пожалуйста, загрузите ваш аватар!'}],
                                })(<img src={this.props.admin.customerdetails.avatar} style={{width: "60px",height: "80px",}}/>)
                                }*/}
                                
                <Button style={{marginLeft: '100px'}}>
                  <Icon type="upload" /> Загрузить
                </Button>
                                
              </Upload>
                <Skeleton loading={this.state.loading} active avatar>
                    <Tabs defaultActiveKey="1" onChange={ (e)=>this.tabChange(e)}>
                        <TabPane tab={ <span><Icon type="home" />Basic Info</span> } key="1">
                          <Tab1 id={this.props.id} details={this.state.details}/>
                          
                        </TabPane>
                        <TabPane tab={ <span><Icon type="question-circle" />Question</span> } key="2">
                            Marcy
                        </TabPane>
                    </Tabs>   
                    
                </Skeleton>   

            </div>
        )
    }
}

function Tab1(props) {
  return (
    <div style={{
      display: 'block', width: 1300, paddingLeft: 30
    }}>
      <MasterDetailsView>
        <MasterDetailsViewItem>
          <MasterDetailsViewItemMaster color='silver'>
          <Table 
                bordered={true} 
                columns={columns} 
                dataSource={this.props.chapter.materialTableData} 
                size="medium" 
                pagination={{ pageSize: 5 }}
                loading={this.props.chapter.materialTableLoadingStatus}
                rowKey="_id" 
              >
                
            </Table>;
             <Button>Test</Button>
          </MasterDetailsViewItemMaster>
          <MasterDetailsViewItemDetails background='silver'>
          <AllMaterial></AllMaterial>
          </MasterDetailsViewItemDetails>
        </MasterDetailsViewItem>
        <MasterDetailsViewItem>
          <MasterDetailsViewItemMaster push>
            Material 2
          </MasterDetailsViewItemMaster>
          <MasterDetailsViewItemDetails background>
          <AllMaterial></AllMaterial>
          </MasterDetailsViewItemDetails>
        </MasterDetailsViewItem>
        
        
          
      </MasterDetailsView>
      <Button>Добавить</Button>
      <Button>Редактировать</Button>
    </div>
  )
}
