import React, { Component } from 'react';
import { Table, Button, Divider, Modal } from 'antd';
import { connect } from 'react-redux';
import { 
    ChangeMaterialSearchText,
    ChangeMaterialTableData,
    ChangeMaterialModalState,
    ChangeAnswersSearchText,
    ChangeAnswersTableData
} from '../../../actions/chapterAction';
import './chapters.css';
import { SecureGet } from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import SliderShow from './slider';
import Test from '../Testing/test';
import Result from '../Testing/result';
//import Alert from '../../../components/common/alert';
import  LocalizedStrings  from  'react-localization' ;

let strings = new LocalizedStrings({
  ru:{
    Names:"Наименование",
    Actions:"Действие",
    Learning:"Урок",
    Tests:"Тест",
    Results:"Результаты",
  },
  kz: {
    
  }
 });


class Materials extends Component {
  constructor(props){
    super(props);
    this.state={
      materialId : null,
      slideModelVisible : false,
      testModelVisible : false,
      resultModelVisible : false,
      details: null,
      loading:true,
      disabled:false,
    }
  }

  OpendetailsModal = (id)=>{
    // this.props.ChangeQuestionTableData(id);
    // this.props.ChangeAnswersTableData(id);
    this.setState((previousState,previousProps)=>{
      return{
        materialId:id,
        slideModelVisible:true
      }
    })

  }
  


  
  ClosedetailsModal = ()=>{
    this.setState((previousState,previousProps)=>{
      return{
        materialId:null,
        slideModelVisible:false
      }
    })
  }

  OpentestModal = (id)=>{
    this.setState((previousState,previousProps)=>{
      return{
        materialId:id,
        testModelVisible:true
      }
    })
  }

  ClosetestModal = ()=>{
    this.setState((previousState,previousProps)=>{
      return{
        materialId:null,
        testModelVisible:false
      }
    })
  }

  
  OpenresultModal = (id)=>{
    this.setState((previousState,previousProps)=>{
      return{
        materialId:id,
        resultModelVisible:true
      }
    })
  }
  
  CloseresultModal = ()=>{
    this.setState((previousState,previousProps)=>{
      return{
        materialId:null,
        resultModelVisible:false
      }
    })
  }

  componentDidMount(){

    var ID = this.props.id;
    this.props.ChangeMaterialTableData(ID);
    SecureGet({
       url: `${apis.GET_SINGLE_CHAPTER_DETAILS}/${ID}`,
    }).then((response)=>{
      console.log(response.data.data[0]);
      this.setState({
        details : response.data.data[0],
        loading : false
      })
    }).catch((error)=>{
      console.log(error);
    });

    this.props.ChangeTestTableData(this.props.materialTableData._id);
    // this.props.ChangeAnswersTableData("62b459718278d60bb54cae1d");
  }


  render() {
    // const { Title } = Typography;
    const columns = [
      {
        title: '№',
        dataIndex: 'num',
        key: 'num',
        width: '5%',
      },   
      {
        title: strings.Names,
        dataIndex: 'topic',
        key: 'topic',
        width: '5%',
      },   
      {
        title: strings.Actions,
        key: '_id',
        dataIndex: '_id',
        width: '25%',
        render: (key) => (
          <span>
            <Button disabled={this.state.disabled} type="primary" icon="" onClick={()=>this.OpendetailsModal(key)}>{strings.Learning}</Button>
            <Divider type="vertical" />
            <Button disabled={this.state.disabled} type="primary" icon="" onClick={()=>this.OpentestModal(key)}>{strings.Tests}</Button>
            <Divider type="vertical" />
            <Button disabled={this.state.disabled} type="primary" icon="" onClick={()=>this.OpenresultModal(key)}>{strings.Results}</Button>
          </span>
        ),
      },
    ];

    return(
      <div className="admin-table-container">
        <Table 
          bordered={true} 
          columns={columns} 
          dataSource={this.props.chapter.materialTableData} 
          size="medium" 
          pagination={{ pageSize: 5 }}
          loading={this.props.chapter.materialTableLoadingStatus}
          rowKey="_id" 
        />

        <Modal
          visible={this.state.slideModelVisible}
          title="Урок"
          onCancel={this.ClosedetailsModal}
          style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
          width="85%"
          destroyOnClose={true}
          footer={[]}
        >
          <SliderShow id={this.state.materialId}/>
        </Modal>

        <Modal
          visible={this.state.testModelVisible}
          title="Тест"
          onCancel={this.ClosetestModal}
          style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
          width="80%"
          destroyOnClose={true}
          footer={[]}
        >
          {/* {console.log("Aktilek:", this.props)} */}
          <Test materialDetails={this.props.chapter.materialTableData} id={this.state.materialId} chid={this.props.id}
          // asn={this.props.chapter.AnswerTableData}
          />
        </Modal>

        <Modal
          visible={this.state.resultModelVisible}
          title="Результат"
          onCancel={this.CloseresultModal}
          style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
          width="80%"
          destroyOnClose={true}
          footer={[]}
        >
          <Result answerDetails={this.props.chapter.AnswerTableData}/>
        </Modal>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  chapter : state.chapter
});

export default connect(mapStateToProps,{
    ChangeMaterialSearchText,
    ChangeMaterialTableData,
    ChangeMaterialModalState,
    ChangeAnswersSearchText,
    ChangeAnswersTableData,
})(Materials);
