                    { answerText: d.answer3, isCorrect: d.trueans3 },
                    { answerText: d.answer4, isCorrect: d.trueans4 },
                    { answerText: d.answer5, isCorrect: d.trueans5}
                  ]
              }
          )
          
      })
        console.log("fg", questions)
          return (
              <Test3 details={this.props.trainer.QuestionTableData}/>
            
          )
      }
  }
  
  const mapStateToProps = state => ({
      chapter : state.chapter,
      trainer : state.trainer
  });
  
  export default connect(mapStateToProps,{
      ChangeQuestionSearchText,
      ChangeQuestionTableData,
      ChangeQuestionModalState
  })(Test);
