    const [result, setResult] = useState(0);
    const [isLoading, setLoading] = useState(true);
    const [details, setDetails] = useState();
    useEffect(()=>{
        props.ChangeAnswersTableData(props.mid);
        console.log('SDFV',props.chapter.AnswerTableData)
