import React, { Component, useState, useEffect } from "react";
import { 
  ChangeTestSearchText,
  ChangeTestTableData,
  ChangeTestModalState,
  ChangeMaterialSearchText,
  ChangeMaterialTableData,
  ChangeMaterialModalState,
} from '../../../actions/chapterAction';
import { connect } from 'react-redux';
// import classes from "./Quiz.module.css";

function Counter(props) {
  const [time, setTime] = useState({});
  const [seconds, setSeconds] = useState(0);
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);
  const timer=0;
  console.log(props)

  // constructor() {
  //   super();
  //   this.state = { 
  //     time: {}, 
  //     seconds: 0,
  //     startDate : null,
  //     endDate: null,
  //   };
  //   this.timer = 0;
  // }

  const secondsToTime = secs => {
    let hours = Math.floor(secs / (60 * 60));

    let divisor_for_minutes = secs % (60 * 60);
    let minutes = Math.floor(divisor_for_minutes / 60);

    let divisor_for_seconds = divisor_for_minutes % 60;
    let seconds = Math.ceil(divisor_for_seconds);

    let obj = {
      h: hours,
      m: minutes,
      s: seconds
    };
    return obj;
  };

  useEffect (()=>{
    props.ChangeTestTableData(props.id);
    props.ChangeMaterialTableData(props.id);
    let timeLeftVar = secondsToTime(seconds);
    // this.setState({ time: timeLeftVar });
    setTime(timeLeftVar);

    // console.log("IIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIII:",this.props.chapter.materialTableData)

    props.chapter.materialTableData.map((d, i) =>{
      if(props.id === d._id){
        var minutesToAdd=d.duration;
        var currentDate = new Date();
        console.log('currentDate', currentDate);
        var futureDate = new Date(props.chapter.testTableData.complationDate);
        console.log("futureDate", futureDate);
        seconds = (futureDate.getTime() - currentDate.getTime())/1000;
        console.log("Seconds:",seconds)
      }
    })
  }, [])

  const startTimer = () => {
    if (timer == 0 && seconds > 0) {
      timer = setInterval(countDown, 1000);
    }
  };

  const countDown = () => {
    let seconds = seconds - 1;
    
      setTime(secondsToTime(seconds));
      setSeconds(seconds);
    if (seconds == 0) {
      clearInterval(timer);
      console.log("Time Up");
    }
  };
    return (
      <div>
        {startTimer()}
        <h3>
          {time.h} : {time.m} :{" "}
          {time.s}
        </h3>
      </div>
    );
}

const mapStateToProps = state => ({
  chapter : state.chapter,
  trainer : state.trainer
});

export default connect(mapStateToProps,{
  ChangeTestSearchText,
  ChangeTestTableData,
  ChangeTestModalState,
  ChangeMaterialSearchText,
  ChangeMaterialTableData,
  ChangeMaterialModalState,
})(Counter);
