import React, { Component, useState, useEffect } from 'react'
import { connect } from 'react-redux';
import { Button } from 'antd';
// import Highlighter from 'react-highlight-words';
import  { css } from 'styled-components/macro'
import { 
  ChangeAnswersSearchText,
  ChangeAnswersTableData,
} from '../../../actions/chapterAction';
import Alert from '../../../components/common/alert';
import { SecurePost, SecureGet } from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import styled from "styled-components";
import "react-multi-carousel/lib/styles.css";
import Counter from './Counter';

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 80vh;
  width: 100%;
  /*background: #7159c1;*/
`;

const ScreenQuiz = styled.div`
  display: flex;
  justify-content: ${props => (props.screen ? "space-between" : "center")};
  align-items: center;
  flex-direction: column;
  max-width: 1200px;
  padding: ${props => (props.screen ? "5rem" : ".5rem")};
  width: 100%;
  height: 80vh;
  background: #fff;
  border-radius: 5px;
  box-shadow: 0px 5px 40px -20px #000;

`;

const Title = styled.h1`
  font-size: 2.5rem;
  // color: #7159c1;
  margin-bottom: 0.5rem;
  text-align: center;
  max-width: 90%;
`;

const Paragraph = styled.p`
  font-size: 1.5rem;
`;

// const Button = styled.button`
//   font-size: 1.2rem;
//   font-weight: bold;
//   width: 40%;
//   height: 3rem;
//   // color: #fff;
//   border: none;
//   // background: #7159c1;
//   border-radius: 5px;
//   box-shadow: 0px 3px 3px 0px #666;

//   @-webkit-keyframes pulsate {
//     0% {transform: scale(0.1, 0.1); opacity: 0.0;}
//     50% {opacity: 1.0;}
//     100% {transform: scale(7, 7); opacity: 0.0;}
// }
// `;

const ButtonScr = styled.button`
  font-size: 1.4rem;
  z-index: 10;
  position: fixed;
  top: 15%;
  ${(props) =>
    props.right
      ? css`
          right: 25rem;
        `
      : css`
          left: 25rem;
        `}
`

const ContainerLabel = styled.div`
  display: flex;
  align-items: flex-start;
  flex-direction: column;
  margin: 0 auto 0.5rem auto;
`;

const Label = styled.label`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Radio = styled.input.attrs({ type: 'checkbox' })`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 15px;
  height: 15px;
  border-radius: 50%;
  border: 7.5px solid black;
  margin: 0 0.5rem 0 0;
  background: ${props => (props.clickedLabel ? "#7159c1" : "#fff")};
`;


export default function TestPage2(props) {
    const [questions, setQuestions] = useState([])
    const [count, setCount] = useState(props.count);
    const [checked, setChecked] = useState(false)
    const [clickedLabel, setClickedLabel] = useState([]);
    const [answer, setAnswer] = useState(props.answer);
    const [screenFinish, setScreenFinish] = useState(props.screenFinish);
    const [screen, setScreen] = useState(props.screen);
    const materialId = props.id;
    const renderResult = () => {
        //   const { answer,questions, screenFinish } = this.state;
      
          return (
            <Container>
              <ScreenQuiz screen={screenFinish}>
                <Title>Вы правильно ответили {answer} из {questions.length} вопросов</Title>
                <Button
                  onClick={() =>
                    setCount(0),
                    setScreenFinish(false),
                    setScreen(true),
                    setAnswer(0)
                  }
    
                  style={{
                    fontSize: "1.2rem", 
                    fontWeight: "bold",
                    width: "40%",
                    height: "3rem",
                    border: "none",
                    borderRadius: "5px",
                    boxShadow: "0px 3px 3px 0px #666"
                  }}
                >
                  Тестировать заново
                </Button>
              </ScreenQuiz>
            </Container>
          );
        };
}
