import React, { Component, useState,useEffect } from 'react'
import { connect } from 'react-redux';
import { Button } from 'antd';
// import Highlighter from 'react-highlight-words';
import  { css } from 'styled-components/macro'
import { 
  //LoginInfo,
  ChangeTestSearchText,
  ChangeTestTableData,
  ChangeTestModalState,
  ChangeAnswersSearchText,
  ChangeAnswersTableData,
  // ChangeAnswersModalState,
} from '../../../actions/chapterAction';

import Alert from '../../../components/common/alert';
import { SecurePost } from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import TestPage from './testpage';
import styled from "styled-components";
import "react-multi-carousel/lib/styles.css";
//import Counter from './Counter';

import  LocalizedStrings  from  'react-localization' ;
let strings = new LocalizedStrings({
  ru:{
    Succes:"Успешно",
    Warning:"Предупреждение!",
    Err:"Ошибка!",
    ErrorServer:"Ошибка сервера",
    Testing:"Тестирование",
    GoTest:"Начать тест",
    ContTest:"Продолжить тест",
  },
  kz: {
    Succes:"Сәтті",
    Warning:"Ескерту!",
    Err:"Қате!",
    ErrorServer:"Сервердің қатесі",
    Testing:"Тестілеу",
    GoTest:"Тесттi бастау",
    ContTest:"Тесттi жалғастыру",
  }
 });
const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 80vh;
  width: 100%;
  /*background: #7159c1;*/
`;

const ScreenQuiz = styled.div`
  display: flex;
  justify-content: ${props => (props.screen ? "space-between" : "center")};
  align-items: center;
  flex-direction: column;
  max-width: 1200px;
  padding: ${props => (props.screen ? "5rem" : ".5rem")};
  width: 100%;
  height: 80vh;
  background: #fff;
  border-radius: 5px;
  box-shadow: 0px 5px 40px -20px #000;

`;

const Title = styled.h1`
  font-size: 2.5rem;
  // color: #7159c1;
  margin-bottom: 0.5rem;
  text-align: center;
  max-width: 90%;
`;

// const Paragraph = styled.p`
//   font-size: 1.5rem;
// `;

// const Button = styled.button`
//   font-size: 1.2rem;
//   font-weight: bold;
//   width: 40%;
//   height: 3rem;
//   // color: #fff;
//   border: none;
//   // background: #7159c1;
//   border-radius: 5px;
//   box-shadow: 0px 3px 3px 0px #666;

//   @-webkit-keyframes pulsate {
//     0% {transform: scale(0.1, 0.1); opacity: 0.0;}
//     50% {opacity: 1.0;}
//     100% {transform: scale(7, 7); opacity: 0.0;}
// }
// `;

const ButtonScr = styled.button`
  font-size: 1.4rem;
  z-index: 10;
  position: fixed;
  top: 15%;
  ${(props) =>
    props.right
      ? css`
          right: 25rem;
        `
      : css`
          left: 25rem;
        `}
`

const ContainerLabel = styled.div`
  display: flex;
  align-items: flex-start;
  flex-direction: column;
  margin: 0 auto 0.5rem auto;
`;

const Label = styled.label`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Radio = styled.input.attrs({ type: 'checkbox' })`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 15px;
  height: 15px;
  border-radius: 50%;
  border: 7.5px solid black;
  margin: 0 0.5rem 0 0;
  background: ${props => (props.clickedLabel ? "#7159c1" : "#fff")};
`;

// const IntoRadio = styled.div`
//   width: 5px;
//   height: 5px;
//   border-radius: 3px;
//   background: #fff;
// `;

// const Error = styled.div`
//   top: 22rem;
//   color: red;
//   position: absolute;
//   font-weight: bold;
// `;


function Test(props){
  const [loading, setLoading] = useState(false)
  const [screen, setScreen] = useState(true);
  const [count, setCount] = useState(0)
  const [answer, setAnswer] = useState(0);
  const [matId, setMatId] = useState(props.id);
  const [disableButton, setDisableButton] = useState(false);
  const [testStarted, setTestStarted] = useState(false);
  const [testEnded, setTestEnded] = useState(false);
  const [testCreated, setTestCreated] = useState(false);
  const [startDate, setStartDate] = useState(Date());

  useEffect(()=>{
    console.log("matId", matId)
    props.ChangeTestTableData(props.id);
    props.ChangeAnswersTableData(props.id);
  }, [])

  const renderInitial = () => {
    if(props.chapter.testTableData!==null || props.chapter.testTableData!==undefined) {
      console.log('else if')
      SecurePost({
        url : `${apis.CREATE_TEST}`,
        data : {
          _id: props.chapter.testTableData._id,
            // chapterid : props.chid,
            // materialid : props.id,
            // startDate : startDate,
            // complationDate : startDate,
            startTest: true,
            endTest: testEnded,
            result: 0
          }
        })
        .then((response)=>{
          if(response.data.success){
              props.ChangeTestModalState(false,null, 'Save'); 
              Alert('success','Успешно',response.data.message);
          }
          else{
              props.ChangeTestModalState(false,null, 'Save');
              return Alert('warning','Предупреждение!',response.data.message);
          }
        })
        .catch((error)=>{
            props.ChangeTestModalState(false,null, 'Save');
            return Alert('error','Ошибка!','Ошибка сервера');
        })
    }
    setScreen(false);
    console.log("screen", screen)
    setLoading(false);
    console.log("loading", loading)
    setCount(1);
    console.log("count", count)
    setTestStarted(true);
    console.log("testStarted", testStarted)
    setTestEnded(false);
    console.log("testEnded", testEnded)
    setTestDidNotStart(false);
    console.log("TestDidNotStart", testDidNotStart)
    setStartDate(new Date());
    console.log("startDate", startDate)
    //  return renderScreenInitial();
  };

  const renderScreenInitial = () => {
    console.log("renderScreenInitial")
    //var tid=props.chapter.testTableData._id || null;
    console.log("testStarted", testStarted)
    if((props.chapter.testTableData === undefined)){
      console.log("1 CREATE_TEST", props.chapter.testId)
      console.log("Before CREATE_TEST", startDate, testStarted, testEnded)
      SecurePost({
        url : `${apis.CREATE_TEST}`,
        data : {
          _id: props.chapter.testId,
            chapterid : props.chid,
            materialid : props.id,
            startDate : startDate,
            complationDate : startDate,
            startTest: false,
            endTest: false,
            result: 0
          }
        })
        .then((response)=>{
          if(response.data.success){
              props.ChangeTestModalState(false,null, 'Save'); 
              Alert('success','Успешно',response.data.message);
          }
          else{
              props.ChangeTestModalState(false,null, 'Save');
              return Alert('warning','Предупреждение!',response.data.message);
          }
        })
        .catch((error)=>{
            props.ChangeTestModalState(false,null, 'Save');
            return Alert('error','Ошибка!','Ошибка сервера');
        })
    }
      console.log("CREATE_ANSWERS", props.chapter.testid) 
      console.log("this.props.chapter.testTableData:", props.chapter.testTableData)
        SecurePost({
          url : `${apis.CREATE_ANSWERS}`,
          data : {
            materialId : props.id,
            testid : (props.chapter.testTableData!==null || props.chapter.testTableData!==undefined) ? null : props.chapter.testTableData._id,
          }
      });
  
    if (testStarted==false){
      return (
        <Container>
          <ScreenQuiz screen={screen}>
            <Title>Тестирование</Title>
            {/* {console.log('ASDFGH:', new Date(this.props.chapter.testTableData.complationDate).getTime())}
            {console.log('ZXCVBN:', new Date().getTime())} */}
            <Button 
              onClick={
                renderInitial
              } 
              style={{
                fontSize: "1.2rem", 
                fontWeight: "bold",
                width: "40%",
                height: "3rem",
                border: "none",
                borderRadius: "5px",
                boxShadow: "0px 3px 3px 0px #666"
              }}
              >
                Начать тест
            </Button>
            
          </ScreenQuiz>
        </Container>
      );
    } 
    else if (testStarted==true && testEnded==false){
      return (
        <Container>
          <ScreenQuiz screen={screen}>
            <Title>Тестирование</Title>
            <Button 
              onClick={renderInitial} 
              style={{
                fontSize: "1.2rem", 
                fontWeight: "bold",
                width: "40%",
                height: "3rem",
                border: "none",
                borderRadius: "5px",
                boxShadow: "0px 3px 3px 0px #666"
              }}
              >
                Продолжить тест
            </Button>
          </ScreenQuiz>
        </Container>
      );
    }
  };

  return screen ? 
    renderScreenInitial() 
  : 
    <TestPage t={props.chapter.testTableData} q={props.chapter.AnswerTableData} id={props.id} count={count} screen={screen} answer={answer} chid={props.chid} tend={testEnded}/>
}

const mapStateToProps = state => ({
    chapter : state.chapter
});

export default connect(mapStateToProps,{
    ChangeTestSearchText,
    ChangeTestTableData,
    ChangeTestModalState,
    ChangeAnswersSearchText,
    ChangeAnswersTableData,
    // ChangeAnswersModalState
})(Test);
