import React, { Component } from 'react';
import { Table, Input, Button, Typography, Icon, Divider, Modal, Popconfirm, Select, Row, Col, Skeleton, Tabs, } from 'antd';
import {
  MasterDetailsViewItem,
  MasterDetailsViewItemMaster,
  MasterDetailsView,
  MasterDetailsViewItemDetails,
  Text
} from 'react-desktop/windows';
import Highlighter from 'react-highlight-words';
import { connect } from 'react-redux';
import { 
    ChangeMaterialSearchText,
    ChangeMaterialTableData,
    ChangeMaterialModalState
} from '../../../actions/chapterAction';
import '../Chapter/allchapter.css';
import Alert from '../../../components/common/alert';
import {SecurePost, SecureGet} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import NewMaterialForm from './newmaterials';
import {updateQuestiosnActiveTest } from '../../../actions/trainerAction';
import Questions from '../../trainer/conducttest/questions';
import AllQuestions from '../../trainer/allquestions/allquestion';
import AllSlides from '../Slides/allslides';

const {TabPane} = Tabs;

class AllMaterial extends Component {

  constructor(props){
    super(props);
    this.state={
      //materialId : null,
      //materialModelVisible:false,
      MaterialtableLoading : false,
      details: null,
      loading:true
    }
  }
  tabChange = (key)=>{
    console.log(key)
  }

  OpenTestModal = (id)=>{
    this.setState((previousState,previousProps)=>{
      return{
        testId:id,
        testModelVisible:true
      }
    })
  }

  CloseTestModal = ()=>{
    this.setState((previousState,previousProps)=>{
      return{
        testId:null,
        testModelVisible:false
      }
    })
  }

  componentDidMount(){
    this.props.ChangeMaterialTableData();
    var ID = this.props.id;
        SecureGet({
            url: `${apis.GET_SINGLE_CHAPTER_DETAILS}/${ID}`,
        }).then((response)=>{
            console.log(response.data.data[0]);
            this.setState({
                details : response.data.data[0],
                loading : false
            })
        }).catch((error)=>{
            console.log(error);
        })
  }


  openModal = (id,mode)=>{
    this.props.ChangeMaterialModalState(true, id, mode);
  }

  closeModal = ()=>{
    this.props.ChangeMaterialModalState(false, null, 'Регистрация');
  }


  deleteMaterial = (id)=>{
    SecurePost({
      url : `${apis.DELETE_MATERIAL}`,
      data : {
          _id : id
      }
    }).then((response)=>{
      if(response.data.success){
        Alert('success','Успешно',response.data.message);
        this.props.ChangeMaterialTableData();
      }
      else{
        return Alert('warning','Предупреждение!',response.data.message);
      }
    }).catch((error)=>{
      return Alert('error','Ошибка!','Ошибка сервера');
    })
  }

    getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.props.chapter.MaterialsearchText]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });
    
      handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.props.ChangeMaterialSearchText(selectedKeys[0])
      };
    
      handleReset = clearFilters => {
        clearFilters();
        this.props.ChangeMaterialSearchText('')
      };

    render() {
      const { Title } = Typography;
      const columns = [
      
        {
          title: 'Наименование',
          dataIndex: 'topic',
          key: 'topic',
          width: '5%',
          ...this.getColumnSearchProps('topic'),
        },   
        {
            title: 'Материалы',
            dataIndex: 'slides',
            key: 'slides',
            width: '25%',
            render: (key) => (
  
              <div>
                <img src={key} style={{width: "50%"}}/>   
              </div>
            )
          },
        
        {
          title: 'Действие',
          key: '_id',
          dataIndex: '_id',
          width: '25%',
          render: (key) => (
            <span>
              <Button type="primary" shape="circle" icon="edit" onClick={()=>this.openModal(key,'Сохранить')}/>
                <Divider type="vertical" />
                <Popconfirm
                  title="Вы уверены？"
                  cancelText="Нет"
                  okText="Да"
                  onConfirm={()=>{this.deleteMaterial(key)}}
                  icon={<Icon type="delete" style={{ color: 'red' }} />}
                >
                  <Button type="danger" shape="circle" icon="delete" />
                </Popconfirm>
                <Divider type="vertical" />
                
            </span>
          ),
        },
      ];
      return(
        <div className="admin-table-container">
          <Skeleton loading={this.state.loading} active avatar>
            <Tabs defaultActiveKey="1" onChange={ (e)=>this.tabChange(e)}>
              <TabPane tab={ <span><Icon type="home" />Материалы</span> } key="1">
                <Button type="primary" icon="user-add" style={{marginBottom:'10px'}} onClick={()=>this.openModal(null,'Регистрация')}>
                  Добавить
                </Button>
                <div>
                
                    <MasterDetailsView >
                      { this.props.chapter.materialTableData.map((d,i)=>
                      <MasterDetailsViewItem key={i}>
                        <MasterDetailsViewItemMaster push width='300px'>
                          {d.topic}
                          <Button type="primary" icon="edit" style={{float: 'right'}} onClick={()=>this.OpendetailsModal(d._id)}>Тест</Button>
                          <Button type="primary" shape="circle" style={{float: 'right'}} icon="edit" onClick={()=>this.openModal(d._id,'Сохранить')}/>
                        </MasterDetailsViewItemMaster>

                        <MasterDetailsViewItemDetails background>
                        
                          {/* <Table 
                            bordered={true} 
                            columns={columns} 
                            dataSource={this.props.chapter.materialTableData.filter((el)=>d.topic === el.topic)} 
                            size="medium" 
                            pagination={{ pageSize: 5 }}
                            loading={this.props.chapter.materialTableLoadingStatus}
                            rowKey="_id" 
                          /> */}
                          <AllSlides></AllSlides>

                        </MasterDetailsViewItemDetails>
                      </MasterDetailsViewItem>
                    )}
                      </MasterDetailsView >
          
                </div>
                <Modal
                      visible={this.props.chapter.MaterialmodalOpened}
                      title={false}
                      onOk={this.handleOk}
                      onCancel={this.closeModal}
                      style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
                      width="40%"
                      destroyOnClose={true}
                      footer={[
                        
                  ]}
                  >
                  <NewMaterialForm />
                </Modal>           
              </TabPane>
              <TabPane tab={ <span><Icon type="question-circle" />Вопросы</span> } key="2">
                <AllQuestions></AllQuestions>
              {/*<Questions id={this.props.trainer.DataActiveTestDetails.testDetailsId} questionsOfTest={this.props.trainer.DataActiveTestDetails.testquestions} updateQuestiosnTest={this.props.updateQuestiosnActiveTest}/>*/}
              </TabPane>
            </Tabs>   
                    
          </Skeleton>

        </div>
      )}
}

const mapStateToProps = state => ({
  chapter : state.chapter,
  trainer : state.trainer
});

export default connect(mapStateToProps,{
    ChangeMaterialSearchText,
    ChangeMaterialTableData,
    ChangeMaterialModalState,
})(AllMaterial);
