import React, { Component } from 'react';
import { Table, Input, Button, Typography, Icon, Divider, Modal, Popconfirm, Select, Row, Col, Skeleton, Tabs, } from 'antd';
import {
  MasterDetailsViewItem,
  MasterDetailsViewItemMaster,
  MasterDetailsView,
  MasterDetailsViewItemDetails,
  Text
} from 'react-desktop/windows';
import Highlighter from 'react-highlight-words';
import { connect } from 'react-redux';
import { 
    ChangeMaterialSearchText,
    ChangeMaterialTableData,
    ChangeMaterialModalState
} from '../../../actions/chapterAction';
import '../Chapter/allchapter.css';
import Alert from '../../../components/common/alert';
import {SecurePost, SecureGet} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import {updateQuestiosnActiveTest } from '../../../actions/trainerAction';
import AllQuestions from '../Questions/allquestion';
import Slider from './slider';

const {TabPane} = Tabs;

class Materials extends Component {

  constructor(props){
    super(props);
    this.state={
      materialId : null,
      materialModelVisible:false,
      slideModelVisible : false,
      MaterialtableLoading : false,
      details: null,
      loading:true,
      chapterId:null
    }
  }
  tabChange = (key)=>{
    console.log(key)
  }

  OpendetailsModal = (id)=>{
    this.setState((previousState,previousProps)=>{
      console.log(id)
      return{
        materialId:id,
        slideModelVisible:true
      }
    })
  }

  ClosedetailsModal = ()=>{
    this.setState((previousState,previousProps)=>{
      return{
        materialId:null,
        slideModelVisible:false
      }
    })
  }


  componentDidMount(){
    var ID = this.props.id;
    this.props.ChangeMaterialTableData(ID);
        SecureGet({
            url: `${apis.GET_SINGLE_CHAPTER_DETAILS}/${ID}`,
        }).then((response)=>{
            console.log(response.data.data[0]);
            this.setState({
                details : response.data.data[0],
                loading : false
            })
        }).catch((error)=>{
            console.log(error);
        })
  }


  openModal = (id,mode)=>{
    this.props.ChangeMaterialModalState(true, id, mode);
  }

  closeModal = ()=>{
    this.props.ChangeMaterialModalState(false, null, 'Регистрация');
  }



    render() {
      const { Title } = Typography;
      const columns = [
        {
          title: '№',
          dataIndex: 'num',
          key: 'num',
          width: '5%',
        },   
        {
          title: 'Наименование',
          dataIndex: 'topic',
          key: 'topic',
          width: '5%',
        },   
        
        {
          title: 'Действие',
          key: '_id',
          dataIndex: '_id',
          width: '25%',
          render: (key) => (
            <span>
                <Button type="primary" icon="" onClick={()=>this.OpendetailsModal(key)}>Уроки</Button>
            </span>
          ),
        },
      ];
      return(
        <div className="admin-table-container">

            <Table 
                bordered={true} 
                columns={columns} 
                dataSource={this.props.chapter.materialTableData} 
                size="medium" 
                pagination={{ pageSize: 5 }}
                loading={this.props.chapter.materialTableLoadingStatus}
                rowKey="_id" 
            ></Table>

            <Modal
                visible={this.state.slideModelVisible}
                title="Материалы"
                onCancel={this.ClosedetailsModal}
                style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
                width="70%"
                destroyOnClose={true}
                footer={[]}
              >
                <Slider id={this.state.materialId} />
            </Modal>
        </div>
      )}
}

const mapStateToProps = state => ({
  chapter : state.chapter,
  trainer : state.trainer
});

export default connect(mapStateToProps,{
    ChangeMaterialSearchText,
    ChangeMaterialTableData,
    ChangeMaterialModalState,
})(Materials);
