import React, { Component, useState, useEffect } from 'react'
import { connect } from 'react-redux';
import { Button } from 'antd';
// import Highlighter from 'react-highlight-words';
import  { css } from 'styled-components/macro'
// import { 
//     ChangeQuestionSearchText,
//     ChangeQuestionTableData,
//     ChangeQuestionModalState,
// } from '../../../actions/trainerAction';
import { 
  LoginInfo,
  ChangeTestSearchText,
  ChangeTestTableData,
  ChangeTestModalState,
  ChangeAnswersSearchText,
  ChangeAnswersTableData,
  // ChangeAnswersModalState,
} from '../../../actions/chapterAction';

import Alert from '../../../components/common/alert';
import { SecurePost, SecureGet } from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import TestPage from './testpage';
import styled from "styled-components";
import "react-multi-carousel/lib/styles.css";
import Counter from './Counter';

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 80vh;
  width: 100%;
  /*background: #7159c1;*/
`;

const ScreenQuiz = styled.div`
  display: flex;
  justify-content: ${props => (props.screen ? "space-between" : "center")};
  align-items: center;
  flex-direction: column;
  max-width: 1200px;
  padding: ${props => (props.screen ? "5rem" : ".5rem")};
  width: 100%;
  height: 80vh;
  background: #fff;
  border-radius: 5px;
  box-shadow: 0px 5px 40px -20px #000;

`;

const Title = styled.h1`
  font-size: 2.5rem;
  // color: #7159c1;
  margin-bottom: 0.5rem;
  text-align: center;
  max-width: 90%;
`;

const Paragraph = styled.p`
  font-size: 1.5rem;
`;

// const Button = styled.button`
//   font-size: 1.2rem;
//   font-weight: bold;
//   width: 40%;
//   height: 3rem;
//   // color: #fff;
//   border: none;
//   // background: #7159c1;
//   border-radius: 5px;
//   box-shadow: 0px 3px 3px 0px #666;

//   @-webkit-keyframes pulsate {
//     0% {transform: scale(0.1, 0.1); opacity: 0.0;}
//     50% {opacity: 1.0;}
//     100% {transform: scale(7, 7); opacity: 0.0;}
// }
// `;

const ButtonScr = styled.button`
  font-size: 1.4rem;
  z-index: 10;
  position: fixed;
  top: 15%;
  ${(props) =>
    props.right
      ? css`
          right: 25rem;
        `
      : css`
          left: 25rem;
        `}
`

const ContainerLabel = styled.div`
  display: flex;
  align-items: flex-start;
  flex-direction: column;
  margin: 0 auto 0.5rem auto;
`;

const Label = styled.label`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Radio = styled.input.attrs({ type: 'checkbox' })`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 15px;
  height: 15px;
  border-radius: 50%;
  border: 7.5px solid black;
  margin: 0 0.5rem 0 0;
  background: ${props => (props.clickedLabel ? "#7159c1" : "#fff")};
`;

// const IntoRadio = styled.div`
//   width: 5px;
//   height: 5px;
//   border-radius: 3px;
//   background: #fff;
// `;

// const Error = styled.div`
//   top: 22rem;
//   color: red;
//   position: absolute;
//   font-weight: bold;
// `;


class Test extends Component {
    constructor(props){
      super(props);
      this.state={
        //questions:[],
        loading:false,
        screen: true,
        count: 0,
        //clickedLabel: [],
        answer: 0,
        //error:false,
        // screenFinish:false,
        //second:60,
       // time:{},
        //checked:false,
        disableButton: false,
        testStarted:false,
        testEnded:false,
        testDidNotStart:true,
        matId : this.props.id,
       // details:[],
      }
    }
  
    componentDidMount(){
      console.log("1 - componentDidMount", this.state.matId)
      this.props.ChangeTestTableData(this.props.id);
      this.props.ChangeAnswersTableData(this.props.id);
    }


    renderInitial = () => {
      console.log("renderInitial")
      const { count } = this.state;

      this.setState({
        screen: false,
        loading: false,
        count: 1,
        testStarted: true,
        testEnded: false,
        testDidNotStart: false,
      });

      
    };

    renderScreenInitial = () => {
      console.log('THIS:',this.props.chapter)
      if((this.props.chapter.testTableData === undefined)){
        console.log("1 CREATE_TEST", this.props.chapter.testid)
        SecurePost({
          url : `${apis.CREATE_TEST}`,
          data : {
            _id: this.props.chapter.testid,
              chapterid : this.props.chid,
              materialid : this.props.id,
              startDate : Date().toLocaleString(),
              complationDate : Date(),
              startTest: false,
              endTest: false,
              result: 0
            }
          })
          .then((response)=>{
            if(response.data.success){
                this.props.ChangeTestModalState(false,null, 'Save'); 
                Alert('success','Успешно',response.data.message);
            }
            else{
                this.props.ChangeTestModalState(false,null, 'Save');
                return Alert('warning','Предупреждение!',response.data.message);
            }
          })
          .catch((error)=>{
              this.props.ChangeTestModalState(false,null, 'Save');
              return Alert('error','Ошибка!','Ошибка сервера');
          })
          
        console.log("CREATE_ANSWERS", this.props.chapter.testid)  
        SecurePost({
          url : `${apis.CREATE_TST}`,
          data : {
            materialId : this.props.id,
            // testid : this.props.chapter.testTableData._id,
          }
        });
      }
      if (this.state.testStarted==false){
        return (
          <Container>
            <ScreenQuiz screen={this.state.screen}>
              <Title>Тестирование</Title>
              <Counter id={this.state.matId}/>
              <Button 
                onClick={this.renderInitial} 
                style={{
                  fontSize: "1.2rem", 
                  fontWeight: "bold",
                  width: "40%",
                  height: "3rem",
                  border: "none",
                  borderRadius: "5px",
                  boxShadow: "0px 3px 3px 0px #666"
                }}
                >
                  Начать тест
              </Button>
            </ScreenQuiz>
          </Container>
        );
      } 
      else if (this.state.testStarted==true && this.state.endTest==false){
        return (
          <Container>
            <ScreenQuiz screen={this.state.screen}>
              <Title>Тестирование</Title>
              <Button 
                onClick={this.renderInitial} 
                style={{
                  fontSize: "1.2rem", 
                  fontWeight: "bold",
                  width: "40%",
                  height: "3rem",
                  border: "none",
                  borderRadius: "5px",
                  boxShadow: "0px 3px 3px 0px #666"
                }}
                >
                  Продолжить тест
              </Button>
            </ScreenQuiz>
          </Container>
        );
      }
    };




    render() {
      const { questions, screen, count, answer, disableButton } = this.state;
      return screen ? this.renderScreenInitial() : 
        console.log(this.props.chapter.testTableData.userid);
        console.log(this.props.login._id);
        <TestPage q={this.props.chapter.AnswerTableData} id={this.props.id} count={count} screen={screen} answer={answer}/>
       ;
    }
  }
  
  const mapStateToProps = state => ({
      chapter : state.chapter,
      trainer : state.trainer
  });
  
  export default connect(mapStateToProps,{
      // ChangeQuestionSearchText,
      // ChangeQuestionTableData,
      // ChangeQuestionModalState,
      ChangeTestSearchText,
      ChangeTestTableData,
      ChangeTestModalState,
      ChangeAnswersSearchText,
      ChangeAnswersTableData,
      // ChangeAnswersModalState
  })(Test);
