import React, { Component } from 'react'
//import './newquestion.css';
import auth from '../../../services/AuthServices';
import {
    Form,
    Input,
    Button,
    Select,
    Row,
    Col,
    Checkbox,
    Modal,
    Upload,
    Icon
} from 'antd';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import { connect } from 'react-redux';
import { 
    ChangeQuestionConfirmDirty,
    ChangeQuestionTableData,
    ChangeQuestionModalState
} from '../../../actions/trainerAction';
import Alert from '../../../components/common/alert';
//const { Option } = Select;
import NewOptionForm from '../options/newoptions';
class NewQuestion extends Component {
    constructor(props){
        super(props);
        this.state={
            questionDetails:{
                questionimage:null,
            },
            adding:false,
            submitDisabled:false,
        }
    }

    componentDidMount(){
        this.setState({
            materialId:this.props.qnid
        });
    }

    Customalert = ()=>{
        Modal.confirm({
            title: 'Подтверждать',
            content: 'пустой параметр не может быть установлен в качестве ответа',
            okText: 'Ok',
            cancelText: null,
        });
    }

    QuestionImageonChange = (f)=>{
        var newQuestion = this.state.questionDetails.questionimage
        if(!f){
            newQuestion=null
        }
        else{
            newQuestion=`${apis.BASE}/${f.link}`
            
        }
        this.setState({
            submitDisabled:false
        })
        if(newQuestion==='undefined' || newQuestion===undefined || newQuestion===null || newQuestion==='null') {
            //(newOptions[i].body==='undefined' || newOptions[i].body===undefined || newOptions[i].body==='null' || newOptions[i].body==='' || newOptions[i].body===null)){
                newQuestion=this.state.questionDetails.questionimage;    
        }
        this.setState((ps,pp)=>{
            return({
                questionDetails:{
                    ...ps.questionDetails,
                    questionimage:newQuestion
                }
            })
        })
    }

    OptionTextChange =(e)=>{
        var newOption1 = [...this.state.questioneditdetails.answer1]
        newOption1={
            ...this.state.questioneditdetails.answer1,
            answer1 : e.target.value
        }
        if((newOption1==='undefined' || newOption1===undefined || newOption1==='null' || newOption1==='' || newOption1===null)){
                newOption1={
                    ...this.state.questioneditdetails,
                    trueans1 : false
                }
                this.setState((ps,pp)=>{
                    return({
                        questioneditsetails:{
                            ...ps.questioneditdetails,
                            answer1:newOption1
                        }
                    })
                })
        }
        this.setState((ps,pp)=>{
            return({
                questioneditdetails:{
                    ...ps.questioneditdetails,
                    answer1:newOption1
                }
            })
        })     
    }

    AnswerOptionSwitch1 = (e)=>{
        if((this.props.trainer.questioneditdetails.answer1!=='' && this.props.trainer.questioneditdetails.answer1!==null)){
            var newOption1 = [...this.props.trainer.questioneditdetails]
            newOption1={
                ...this.props.trainer.questioneditdetails,
                trueans1 : e.target.checked
            }
            this.setState((ps,pp)=>{
                return({
                    questioneditdetails:{
                        ...pp.trainer.questioneditdetails,
                        answer1:newOption1
                    }
                })
            })
            
        }
        else{   
            this.Customalert()
            return;
        }
        
    }

    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
                console.log(this.state.materialId);
                console.log('Полученные значения формы: ', values);
                
                    SecurePost({
                        url : `${apis.CREATE_QUESTIONS}`,
                        data : {
                            _id : this.props.trainer.questionId,
                            body : values.questionbody,                            
                            quesimg : this.state.questionDetails.quesimg,
                            material : this.state.materialId,
                            answer1 : values.answer1,
                            trueans1 : values.trueans1,
                            answer2 : values.answer2,
                            trueans2 : values.trueans2,
                            answer3 : values.answer3,
                            trueans3 : values.trueans3,
                            answer4 : values.answer4,
                            trueans4 : values.trueans4,
                            answer5 : values.answer5,
                            trueans5 : values.trueans5,
                        }
                    }).then((response)=>{
                        if(response.data.success){
                            this.props.ChangeQuestionModalState(false,null,'Регистрация');
                            Alert('success','Успешно',response.data.message);
                            // this.props.ChangeSlideTableData();
                        }
                        else{
                            console.log(response.data);
                            this.props.ChangeQuestionModalState(false,null,'Регистрация');
                            return Alert('warning','Предупреждение!',response.data.message);
                        }
                    }).catch((error)=>{
                        this.props.ChangeQuestionModalState(false,null,'Регистрация');
                        return Alert('error','Ошибка!','Ошибка сервера');
                    })
                
            }
        });
    };

    changeqImage = (f)=>{
        this.setState((ps,pp)=>{
            return({
                questionDetails:{
                    ...ps.questionDetails,
                    questionimage:(f.link ?`${apis.BASE}/${f.link}`:null)
                },
                submitDisabled:false
            })
        })
    }

    upl=()=>{
        this.setState({
            submitDisabled: true
        })
    }

    render() {
        const { getFieldDecorator } = this.props.form;
        const { Option } = Select;
        const { TextArea } = Input;
        console.log('QAS',this.props.trainer.questioneditdetails);
        var QuestionImageprops={
            name: 'file',
            action: `${apis.BASE}${apis.FILE_UPLOAD}?Token=${auth.retriveToken()}`,
            listType: 'picture',
        }
        
        return (
            <div className="register-subject-form" >
                <div className="register-trainer-form-body">
                    <Form  onSubmit={this.handleSubmit}>
                        <div>
                            
                            <Row>
                                <Col span={18}>
                                    <Form.Item label="Вопрос" hasFeedback>
                                        {getFieldDecorator('questionbody', {
                                            initialValue : this.props.trainer.questioneditdetails.body,
                                            rules: [{ required: true, message: 'Пожалуйста, введите вопрос!' }],
                                        })(
                                            <TextArea rows={5} />
                                        )}
                                    </Form.Item>
                                </Col>
                                <Col span={6} style={{padding : '0px 20px'}}>
                                    <Form.Item label="Изображение вопроса">
