import React, { Component } from 'react';
import { Table, Input, Button, Typography, Divider, Icon, Modal, Popconfirm } from 'antd';
import Highlighter from 'react-highlight-words';
import { connect } from 'react-redux';
import { 
    ChangeCustomerSearchText,
    ChangeCustomerTableData,
    ChangeCustomerModalState,
    ChangeDivisionTableData,
    ChangeSelectedDivisions
} from '../../../actions/adminAction';
import {
  ChangeCompanyTableData,
  ChangeSelectedCompany,
} from '../../../actions/adminAction';
import './allcustomer.css'
import Alert from '../../../components/common/alert';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import NewCustomerForm from './newcustomer';


class AllCustomer extends Component {

  constructor(props){
    super(props);
    this.state={
      CustomertableLoading : false
    }
  }

  openModal = (id,mode)=>{
    this.props.ChangeCustomerModalState(true, id, mode);
  }

  closeModal = ()=>{
    this.props.ChangeCustomerModalState(false,null,'Регистрация');
  }

  componentDidMount(){
    // this.props.ChangeDivisionTableData();
    this.props.ChangeCompanyTableData();
    this.props.ChangeCustomerTableData(this.props.admin.selectedCompany);
  }

  deleteCustomer = (id)=>{
    SecurePost({
      url : `${apis.DELETE_CUSTOMER}`,
      data : {
          _id : id
      }
    }).then((response)=>{
      if(response.data.success){
        Alert('success','Успешно',response.data.message);
        //this.props.ChangeCompanyTableData(this.props.admin.selectedDivisions);
        this.props.ChangeCustomerTableData(this.props.admin.selectedCompany);
      }
      else{
        return Alert('warning','Предупреждение!',response.data.message);
      }
    }).catch((error)=>{
      return Alert('error','Ошибка!','Ошибка сервера');
    })
  }

    getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.props.admin.CustomersearchText]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });
    
      handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.props.ChangeCustomerSearchText(selectedKeys[0])
      };
    
      handleReset = clearFilters => {
        clearFilters();
        this.props.ChangeCustomerSearchText('')
      };
      // handleDivisionChange =(s)=>{
      //   this.props.ChangeSelectedDivisions(s);
      //   this.props.ChangeCustomerTableData(s);
      // }

    render() {
      const { Title } = Typography;
      const columns = [
        
        {
          title: 'ФИО',
          dataIndex: 'name',
          key: 'name',
          width: '25%',
          ...this.getColumnSearchProps('name'),
        },
        {
          title: 'Аватар',
          dataIndex: 'avatar',
          key: 'avatar',
          width: '5%',
          render: (key) => (

            <div>
            <img src={key} style={{width: "60px",height: "60px",}} alt=""/>
                         
            </div>
          )
        },
        {
          title: 'Эл. почта',
          dataIndex: 'emailid',
          key: 'emailid',
          width: '25%',
          ...this.getColumnSearchProps('emailid'),
        },  
        
        {
          title: 'Отдел',
          dataIndex: 'division.name',
          key: 'division.name',
          width: '15%',
        },
        
        {
          title: 'Компания',
          dataIndex: 'company.name',
          key: 'company.name',
          width: '15%',
        },
        

        {
          title: 'Действие',
          key: '_id',
          dataIndex: '_id',
          render: (key) => (
            <span>
              <Button type="primary" shape="circle" icon="edit" onClick={()=>this.openModal(key,'Сохранить')}/>
                <Divider type="vertical" />
                <Popconfirm
                  title="Вы уверены？"
                  cancelText="Нет"
                  okText="Да"
                  onConfirm={()=>{this.deleteCustomer(key)}}
                  icon={<Icon type="delete" style={{ color: 'red' }} />}
                >
                  <Button type="danger" shape="circle" icon="delete" />
                </Popconfirm>
              
            </span>
          ),
        },
      ];
        return (
            <div className="admin-table-container">
              <Button type="primary" icon="user-add" style={{marginBottom:'10px'}} onClick={()=>this.openModal(null,'Регистрация')}>
              Добавить
              </Button> 
              
              <div className="register-customer-form-header">
                <Title level={4} style={{color:'#fff',textAlign:'center'}}>Список пользователей</Title>
              </div>
              <Table 
                bordered={true} 
                columns={columns} 
                dataSource={this.props.admin.customerTableData} 
                size="medium" 
                pagination={{ pageSize: 5 }}
                loading={this.props.admin.customerTableLoadingStatus}
                rowKey="_id" 
              >
                
            </Table>;
              <Modal
                visible={this.props.admin.CustomermodalOpened}
                title={false}
                onOk={this.handleOk}
                onCancel={this.closeModal}
                style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
                width="40%"
                destroyOnClose={true}
                footer={[
                  
                ]}
              >
                <NewCustomerForm />
              </Modal>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    admin : state.admin
});

export default connect(mapStateToProps,{
    ChangeCustomerSearchText,
    ChangeCustomerTableData,
    ChangeCustomerModalState,
    ChangeSelectedDivisions,
    ChangeDivisionTableData,
    ChangeCompanyTableData,
    ChangeSelectedCompany
})(AllCustomer);
