import React, { Component } from 'react'
import { connect } from 'react-redux';
import { Form, InputNumber , Input, Button,Select  } from 'antd';
import { changeStep,changeBasicNewTestDetails } from '../../../actions/testAction';
import { SecurePost } from '../../../services/axiosCall';
import './newtest.css';
import apis from '../../../services/Apis'
// const { Option } = Select;


class BasicTestFormO extends Component {
    constructor(props){
        super(props);
        this.state={
            checkingName:""
        }
    }

    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                console.log(values)
                this.props.changeBasicNewTestDetails({
                    testTitle: values.title,
                    testDuration : values.duration,
                    testAttempts : values.attempts,
                    testPeriod : values.period,
                    testNumberQuestions : values.numQuestions
                })
                this.props.changeStep(1);
            }
        });
    };

    validateTestName = (rule, value, callback) => {
        if(value.length>=5){
            this.setState({
                checkingName:"validating"
            })
            SecurePost({
                url:apis.CHECK_TEST_NAME,
                data:{
                    testname:value
                }
            }).then((data)=>{
                console.log(data);
                if(data.data.success){
                    if(data.data.can_use){
                        this.setState({
                            checkingName:"success"
                        })
                        callback();
                    }
                    else{
                        this.setState({
                            checkingName:"error"
                        })
                        callback('Another test exist with same name.');
                    }
                }
                else{
                    this.setState({
                        checkingName:"success"
                    })
                    callback()
                }
            }).catch((ee)=>{
                console.log(ee);
                this.setState({
                    checkingName:"success"
                })
                callback()
            })
        }
        else{
            callback();
        }        
    };


    render() {
        const { getFieldDecorator } = this.props.form;
        return (
            <div className="basic-test-form-outer">
                <div className="basic-test-form-inner">
                    <Form onSubmit={this.handleSubmit}>
                        <Form.Item label="Название теста"  hasFeedback validateStatus={this.state.checkingName}>
                            {getFieldDecorator('title', {
                                initialValue : this.props.test.newtestFormData.testTitle,
                                rules: [
                                    { required: true, message: 'Пожалуйста, укажите название теста' },
                                    { min:5, message: 'Заголовок должен содержать не менее 5 символов' },
                                    { validator: this.validateTestName }
                                ],
                                
                            })(
                                <Input placeholder="Название теста" />
                            )}
                        </Form.Item>
                        {/* <Form.Item label="Раздел"  hasFeedback>
                            {getFieldDecorator('chapter', {
                                initialValue : this.props.test.newtestFormData.testChapter,
                                rules: [{ required: true, message: 'Пожалуйста, выберите раздел' }],
                            })(
                                <Select
                                mode="multiple"
                                placeholder="Выберите одну или несколько глав"
                                style={{ width: '100%' }}
                                allowClear={true}
                                optionFilterProp="s"
                                >
                                    {this.props.chapter.chapterTableData.map(item => (
                                        <Select.Option key={item._id} value={item._id} s={item.name}>
                                        {item.name}
                                        </Select.Option>
                                    ))}
                                </Select>
                            )}
                        </Form.Item> */}
                        <Form.Item label="Продолжительность теста" hasFeedback>
                            {getFieldDecorator('duration', {
                                initialValue : this.props.test.newtestFormData.testDuration,
                                rules: [{ required: true, message: 'Пожалуйста, укажите продолжительность теста' }],
                            })(
                                <InputNumber style={{width:'100%'}}  placeholder="Продолжительность теста" min={0} max={180}/>
                            )}
                        </Form.Item> 

                        <Form.Item label="Количество попыток" hasFeedback>
                            {getFieldDecorator('attempts', {
                                initialValue : this.props.test.newtestFormData.testAttempts,
                                rules: [{ required: true, message: 'Пожалуйста, укажите количество попыток' }],
                            })(
                                <InputNumber style={{width:'100%'}}  placeholder="Количество попыток"/>
                            )}
                        </Form.Item> 

                        <Form.Item label="Период ожидания, дней" hasFeedback>
                            {getFieldDecorator('period', {
                                initialValue : this.props.test.newtestFormData.testPeriod,
                                rules: [{ required: true, message: 'Пожалуйста, укажите период ожидания' }],
                            })(
                                <InputNumber style={{width:'100%'}}  placeholder="Период ожидания"/>
                            )}
                        </Form.Item> 
                        <Form.Item label="Количество вопросов для отображения пользователю" hasFeedback>
                            {getFieldDecorator('numQuestions', {
                                initialValue : this.props.test.newtestFormData.testNumberQuestions,
                                rules: [{ required: true, message: 'Количество вопросов для отображения пользователю' }],
                            })(
                                <InputNumber style={{width:'100%'}}  placeholder="Количество вопросов"/>
                            )}
                        </Form.Item> 

                        <Form.Item>
                            <Button type="primary" htmlType="submit" block>
                                Следующий
                            </Button>
                        </Form.Item>
                    </Form>
                </div>
            </div>
        )
    }
}
const BasicTestForm = Form.create({ name: 'Основная форма' })(BasicTestFormO);

const mapStateToProps = state => ({
    test : state.test,
    admin:state.admin,
    chapter : state.chapter
});

export default connect(mapStateToProps,{
    changeStep,
    changeBasicNewTestDetails
})(BasicTestForm);
