import React, { Component, useState } from 'react'
import Slider from 'react-touch-drag-slider'
import { connect } from 'react-redux';
import styled, { createGlobalStyle, css } from 'styled-components/macro'


const GlobalStyles = createGlobalStyle`
  * {
    box-sizing: border-box;
  }
  html,body {
    padding: 0;
    margin: 0;
  }
`
const AppStyles = styled.main`
  height: 82vh;
  width: 82vw;
`

const Button = styled.button`
  font-size: 2rem;
  z-index: 10;
  position: fixed;
  top: 50%;
  ${(props) =>
    props.right
      ? css`
          right: 0.5rem;
        `
      : css`
          left: 0.5rem;
        `}
`

function Test2(props) {
    const [index, setIndex] = useState(0)
          var questions=[];
          // console.log(props.trainer.QuestionTableData)
          console.log('hgj',props.trainer)
          props.trainer.QuestionTableData.map((d,i)=>{
              
              questions[i]=(
                  {
                      questionText : d.body,
                  // image: d.slimage,
                  // caption: d.slbody
                  }
              )
          })
          // console.log(questions);
          const increment = () => {
              if (index < questions.length - 1) setIndex(index + 1)
          }
  
          const decrement = () => {
              if (index > 0) setIndex(index - 1)
          }
  
    return (
      <>
        <GlobalStyles />
        <AppStyles>
          <Button onClick={decrement} left disabled={index === 0}>
            〈
          </Button>
          <Button
            onClick={increment}
            right
            disabled={index === questions.length - 1}
          >
            〉
          </Button>
          <Slider
            onSlideComplete={setIndex}
            onSlideStart={(i) => {
              console.log('started dragging on slide', i)
            }}
            activeIndex={index}
            threshHold={100}
            transition={0.3}
            scaleOnDrag={true}
          >
            {questions.map(({ image, caption }, index) => (
              <img src={image} key={index} alt={caption} />
            ))}
          </Slider>
        </AppStyles>
      </>
    )
  }
