import React, { Component } from 'react'
// import Slider from 'react-touch-drag-slider'
import { connect } from 'react-redux';
// import { Table, Input, Button, Typography, Divider, Icon, Modal, Popconfirm } from 'antd';
// import Highlighter from 'react-highlight-words';
// import styled, { createGlobalStyle, css } from 'styled-components/macro'
import { 
    ChangeQuestionSearchText,
    ChangeQuestionTableData,
    ChangeQuestionModalState
} from '../../../actions/trainerAction';
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
// import Alert from '../../../components/common/alert';
// import {SecurePost} from '../../../services/axiosCall';
// import apis from '../../../services/Apis';
// import Test2 from './test2';
// import Test3 from './test3';


class Test extends Component {
    constructor(props){
      super(props);
      this.state={
        QuestiontableLoading : false,
        details : null,
        testId : null,
        testModelVisible:false
      }
    }
  
    componentDidMount(){
      this.props.ChangeQuestionTableData(this.props.id);
    }



    render() {
      let responsive = {
        desktop: {
          breakpoint: { max: 3000, min: 1024 },
          items: 1,
          slidesToSlide: 1 // optional, default to 1.
        },
        tablet: {
          breakpoint: { max: 1024, min: 464 },
          items: 1,
          slidesToSlide: 1 // optional, default to 1.
        },
        mobile: {
          breakpoint: { max: 464, min: 0 },
          items: 1,
          slidesToSlide: 1 // optional, default to 1.
        }
      };
      var questions=[];
      this.props.trainer.QuestionTableData.map((d,i)=>{
          questions[i]=(
              {
                  question : d.body,
                  questionImage : d.quesimg,
                  optionA : d.answer1,
                  optionB : d.answer2,
                  optionC : d.answer3,
                  optionD : d.answer4,
                  optionE : d.answer5,
                  answer : (d.trueans1===true ? d.answer1 : null) || (d.trueans2===true ? d.answer2 : null) || (d.trueans3===true ? d.answer3 : null) ||
                           (d.trueans4===true ? d.answer4 : null) || (d.trueans5===true ? d.answer5 : null)
                  // answerOptions: [
                  //   { answerText: d.answer1, isCorrect: d.trueans1 },
                  //   { answerText: d.answer2, isCorrect: d.trueans2 },
                  //   { answerText: d.answer3, isCorrect: d.trueans3 },
                  //   { answerText: d.answer4, isCorrect: d.trueans4 },
                  //   { answerText: d.answer5, isCorrect: d.trueans5}
                  // ]
              }
          )
          
      })
      console.log('ptngjf:', questions);
        return (
            <Carousel
            swipeable={false}
            draggable={false}
            showDots={true}
            responsive={responsive}
            ssr={true} // means to render carousel on server-side.
            infinite={true}
            focusOnSelect={true}
            // autoPlay={this.props.deviceType !== "mobile" ? true : false}
            autoPlaySpeed={1000}
            keyBoardControl={true}
            customTransition="all .5"
            transitionDuration={500}
            containerClass="carousel-container"
            // removeArrowOnDeviceType={["tablet", "mobile"]}
            // deviceType={this.props.deviceType}
            dotListClass="custom-dot-list-style"
            itemClass="carousel-item-padding-left-400-px"
          >
          {questions.map((d,i) => (
            <div style={{paddingLeft:'300px'}}>   
              <h3>{d.question}</h3>
              <div><img src={d.questionImage} style={{width: "60px",height: "60px",}} alt=""/></div>
              <div><input type="checkbox"/> <label>{d.optionA}</label></div>
              <div><input type="checkbox"/> <label>{d.optionB}</label></div>
              <div><input type="checkbox"/> <label>{d.optionC}</label></div>
              <div><input type="checkbox"/> <label>{d.optionD}</label></div>
              <div><input type="checkbox"/> <label>{d.optionE}</label></div>
            </div>
          ))}
            {/* <div>ASDFGHJKL</div>
            <div>123456789</div>
            <div>qwertyuio</div>
            <div>ZXCVBNM</div>
            <div>dsghyjujhd</div>
            <div>Item 3</div> */}
          </Carousel>
    
            
        )
      }


      //     return (
            
      //             <Test2 details={questions}/>

      //     )
      // }
  }
  
  const mapStateToProps = state => ({
      chapter : state.chapter,
      trainer : state.trainer
  });
  
  export default connect(mapStateToProps,{
      ChangeQuestionSearchText,
      ChangeQuestionTableData,
      ChangeQuestionModalState
  })(Test);
