import React, { Component } from 'react'
import { connect } from 'react-redux';
// import { Table, Input,  Typography, Divider, Icon, Modal, Popconfirm } from 'antd';
// import Highlighter from 'react-highlight-words';
import  { css } from 'styled-components/macro'
import { 
    ChangeQuestionSearchText,
    ChangeQuestionTableData,
    ChangeQuestionModalState
} from '../../../actions/trainerAction';
// import Alert from '../../../components/common/alert';
// import {SecurePost} from '../../../services/axiosCall';
// import apis from '../../../services/Apis';
// import Test2 from './test2';
// import Test3 from './test3';
import styled from "styled-components";
// import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import classnames from 'classnames';
import Counter from './Counter';

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 80vh;
  width: 100%;
  /*background: #7159c1;*/
`;

const ScreenQuiz = styled.div`
  display: flex;
  justify-content: ${props => (props.screen ? "space-between" : "center")};
  align-items: center;
  flex-direction: column;
  max-width: 1200px;
  padding: ${props => (props.screen ? "5rem" : ".5rem")};
  width: 100%;
  height: 80vh;
  background: #fff;
  border-radius: 5px;
  box-shadow: 0px 5px 40px -20px #000;

`;

const Title = styled.h1`
  font-size: 2.5rem;
  // color: #7159c1;
  margin-bottom: 0.5rem;
  text-align: center;
  max-width: 90%;
`;

const Paragraph = styled.p`
  font-size: 1.5rem;
`;

const Button = styled.button`
  font-size: 1.2rem;
  font-weight: bold;
  width: 40%;
  height: 3rem;
  // color: #fff;
  border: none;
  // background: #7159c1;
  border-radius: 5px;
  box-shadow: 0px 3px 15px 0px #666;
`;

const ButtonScr = styled.button`
  font-size: 2rem;
  z-index: 10;
  position: fixed;
  top: 50%;
  ${(props) =>
    props.right
      ? css`
          right: 0.5rem;
        `
      : css`
          left: 0.5rem;
        `}
`

const ContainerLabel = styled.div`
  display: flex;
  align-items: flex-start;
  flex-direction: column;
  margin: 0 auto 0.5rem auto;
`;

const Label = styled.label`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Radio = styled.input.attrs({ type: 'checkbox' })`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 15px;
  height: 15px;
  // border-radius: 7.5px;
  border: 0.5px solid black;
  margin: 0 0.5rem 0 0;
  background: ${props => (props.clickedLabel ? "#7159c1" : "#fff")};
`;

// const IntoRadio = styled.div`
//   width: 5px;
//   height: 5px;
//   border-radius: 3px;
//   background: #fff;
// `;

const Error = styled.div`
  top: 22rem;
  color: red;
  position: absolute;
  font-weight: bold;
`;

class Test extends Component {
    constructor(props){
      super(props);
      this.state={
        questions:[],
        screen: true,
        count: 0,
        clickedLabel: {},
        answer: 0,
        error:false,
        screenFinish:false,
        second:60,
        time:{},
        checked:true,
        temp:[],
        t:0
      }
    }
  
    componentDidMount(){
      this.props.ChangeQuestionTableData(this.props.id);
      var questions=[];
      this.props.trainer.QuestionTableData.map((d,i)=>{
        questions[i]=(
          {
            question : d.body,
            // questionImage : d.quesimg,
            answerOptions: [
              { answerText: d.answer1, correctAnswer: d.trueans1 },
              { answerText: d.answer2, correctAnswer: d.trueans2 },
              { answerText: d.answer3, correctAnswer: d.trueans3 },
              { answerText: d.answer4, correctAnswer: d.trueans4 },
              { answerText: d.answer5, correctAnswer: d.trueans5}
            ]
          }
        )
      });
      this.setState({
        questions:questions
      })
    }

    handleNextClick=()=>{
      const { count, questions, clickedLabel, answer} = this.state;
      if (questions.length >= count) {
        this.setState({
          count: count + 1,
          checked:false,
        });
      } 
    }

    handlePrevClick=()=>{
      const { count,questions, clickedLabel, answer} = this.state;
      if (questions.length >= count && count>0) {
        this.setState({
          count: count - 1,
          checked:false,
        });
      } 
    }

    handleClick = ()=> {
      const { count,questions, clickedLabel, answer} = this.state;
      if (questions.length >= count && clickedLabel.answerText) {
        this.setState({
          count: count + 1,
          clickedLabel: {},
          answer: clickedLabel.correctAnswer ? answer + 1 : answer,
          checked:false,
          error: false
        });
      } 
      else {
        this.setState({
          count: count,
          error: true
        });
      }


      if (questions.length === count) {
        this.setState({
          screenFinish: true
        });
      }
      // this.handleCountDown();
    }

    // handleCountDown = () => {
    //   const { second, count } = this.state;
    //   clearInterval(this.timer);
  
    //   let counter = 1;
    //   this.timer = setInterval(() => {
    //     if (counter >= 10) {
    //       clearInterval(this.timer);
    //       if (second >= 10) {
    //         this.setState({
    //           count: count + 1
    //         });
    //       }
    //     }
  
    //     this.setState({
    //       second: counter
    //     });
    //     // console.log(counter++);
    //   }, 1000);
    // };

    renderInitial = () => {
      const { second, count } = this.state;
  
      this.setState({
        screen: false,
        count: 1
      });
      // this.handleCountDown();
    };

    renderScreenInitial = () => {
      return (
        <Container>
          <ScreenQuiz screen={this.state.screen}>
            <Title>Тестирование</Title>
            {/* <Paragraph>Click to start</Paragraph> */}
            <Button onClick={this.renderInitial}>Начать тест</Button>
          </ScreenQuiz>
        </Container>
      );
    };
  
    handleOptions = answerOptions => {
      const { clickedLabel, count, checked, t } = this.state;
      console.log('click:',clickedLabel);
      console.log('answer1:',answerOptions);
      return answerOptions.map((a, i) => {
        return (
          <Label
            key={i}
            onClick={() =>
              this.setState({
                t: i,
                clickedLabel: a,
                checked : false,
                error: false
              })
             
            }
            
          >
            {console.log("Ak")}
            <Radio clickedLabel={clickedLabel.answerText === a.answerText} checked={!checked} onChange={e => this.handleChange(e, i)}/>
            <Paragraph>{a.answerText}</Paragraph>
          </Label>
          
        );
      });
    };

    handleChange = (e, i) => {
      const {checked} = this.state
      // this.setState({checked: !checked});
      console.log('check', e.target.checked);
      console.log('I',i)
    };


  //   startTimer = () => {
  //     const countDownTime = Date.now() + 180000;
  //     this.interval = setInterval(() => {
  //         const now = new Date();
  //         const distance = countDownTime - now;

  //         const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
  //         const seconds = Math.floor((distance % (1000 * 60)) / 1000);

  //         if (distance < 0) {
  //             clearInterval(this.interval);
  //             this.setState({
  //                 time: {
  //                     minutes: 0,
  //                     seconds: 0
  //                 }
  //             }, () => {
  //                 this.endGame();
  //             });
  //         } else {
  //             this.setState({
  //                 time: {
  //                     minutes,
  //                     seconds,
  //                     distance
  //                 }
  //             });
  //         }
  //     }, 1000);
  // }

  
  
    renderQuestions = () => {
      const { count,questions, error, second, time, ind } = this.state;
      const NewQuestions = Array.from(questions);
      const removeQuestions = NewQuestions.slice(count - 1, count);
    
      return removeQuestions.map((q, i) => (

        <Container key={i}>
          <ScreenQuiz>
            
            {/* <div className="timer-container">
              <p>
                <span className={classnames('right valid', {
                  'warning': time.distance <= 120000,
                  'invalid': time.distance < 30000
                  })} style={{paddingLeft:'10px'}}>
                  {time.minutes}:{time.seconds}
                  <span  className="mdi mdi-clock-outline mdi-24px"></span></span>
              </p>
            </div> */}
            <Counter/>

            <h3>{count}/{questions.length}</h3>
            <Title>{q.question}</Title>
            <ContainerLabel>
              {this.handleOptions(q.answerOptions)}
            </ContainerLabel>
            <Button onClick={this.handleClick}>Сохранить</Button>
            {error && <Error>Please check an Options.</Error>}
            <ButtonScr onClick={this.handleNextClick} right disabled={count === questions.length}>〉</ButtonScr>
            <ButtonScr onClick={this.handlePrevClick} left disabled={count===1}>〈</ButtonScr>
            {console.log('Count:',count)}
          </ScreenQuiz>
        </Container>

      ));
    };
  
    renderResult = () => {
      const { answer,questions, screenFinish } = this.state;
  
      return (
        <Container>
          <ScreenQuiz screen={screenFinish}>
            <Title>Вы правильно ответили {answer} из {questions.length} вопросов</Title>
            <Button
              onClick={() =>
                this.setState({
                  count: 0,
                  screenFinish: false,
                  screen: true,
                  answer: 0
                })
              }
            >
              Тестировать заново
            </Button>
          </ScreenQuiz>
        </Container>
      );
    };


    render() {
      const { screenFinish, questions, screen } = this.state;

      return !screenFinish && questions ? (
        <>{screen ? this.renderScreenInitial() : this.renderQuestions()}</>
      ) : (
        this.renderResult()
      );
    }

