import React, { Component, useState } from 'react'
import '../newCompany/newcompany.css';
import {
    Form,
    Input,
    Button,
    Select,
    Icon,
    Upload
} from 'antd';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import { connect } from 'react-redux';
import { 
    ChangeMaterialConfirmDirty,
    ChangeMaterialModalState,
    ChangeMaterialTableData
} from '../../../actions/chapterAction';
import Alert from '../../../components/common/alert';
import auth from '../../../services/AuthServices';
const { Option } = Select;



class NewMaterial extends Component {

    constructor(props){
        super(props);
        this.state={
            materialDetails : {
                //materialimage: this.props.chapter.materialdetails.slides,
                slides :[
                    {
                        slidebody : null,
                        image :null,
                    },
                ], 
            },
            submitDisabled:false,
        }
    }

    // MaterialImageonChange = (f)=>{
    //     var newslide = this.state.materialDetails.materialimage
    //     if(!f){
    //         newslide=null
    //     }
    //     else{
    //         newslide=`${apis.BASE}/${f.link}`
    //     }
