import React, { Component } from 'react'
//import './newquestion.css';
import auth from '../../../services/AuthServices';
import {
    Form,
    Input,
    Button,
    Select,
    Row,
    Col,
    Checkbox,
    Modal,
    Upload,
    Icon
} from 'antd';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import { connect } from 'react-redux';
import { 
    ChangeQuestionConfirmDirty,
    ChangeQuestionTableData,
    ChangeQuestionModalState
} from '../../../actions/trainerAction';
import Alert from '../../../components/common/alert';
//const { Option } = Select;
import NewOptionForm from '../Options/newoptions';
class NewQuestion extends Component {
    constructor(props){
        super(props);
        this.state={
            questionDetails:{
                questionimage:null,
            },
            adding:false,
            submitDisabled:false,
        }
    }

    componentDidMount(){
        this.setState({
            materialId:this.props.qnid
        });
    }

    Customalert = ()=>{
        Modal.confirm({
            title: 'Подтверждать',
            content: 'пустой параметр не может быть установлен в качестве ответа',
            okText: 'Ok',
            cancelText: null,
        });
    }

    SlideImageonChange = (f)=>{
        var newSlide = this.state.slideDetails.slideimage
        if(!f){
            newSlide=null
        }
        else{
            newSlide=`${apis.BASE}/${f.link}`
            
        }
        this.setState({
            submitDisabled:false
        })
        if(newSlide==='undefined' || newSlide===undefined || newSlide===null || newSlide==='null') {
            //(newOptions[i].body==='undefined' || newOptions[i].body===undefined || newOptions[i].body==='null' || newOptions[i].body==='' || newOptions[i].body===null)){
                newSlide=this.state.slideDetails.slideimage;    
        }
        this.setState((ps,pp)=>{
            return({
                slideDetails:{
                    ...ps.slideDetails,
                    slideimage:newSlide
                }
            })
        })
    }

    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
                console.log(this.state.materialId);
                console.log('Полученные значения формы: ', values);
                
                    SecurePost({
                        url : `${apis.CREATE_SLIDES}`,
                        data : {
                            _id : this.props.trainer.questionId,
                            body : values.questionbody,                            
                            quesimg : this.steate.questionDetails.quesimg,
                            material : this.state.materialId
                        }
                    }).then((response)=>{
                        if(response.data.success){
                            this.props.ChangeQuestionModalState(false,null,'Регистрация');
                            Alert('success','Успешно',response.data.message);
                            // this.props.ChangeSlideTableData();
                        }
                        else{
                            console.log(response.data);
                            this.props.ChangeQuestionModalState(false,null,'Регистрация');
                            return Alert('warning','Предупреждение!',response.data.message);
                        }
                    }).catch((error)=>{
                        this.props.ChangeQuestionModalState(false,null,'Регистрация');
                        return Alert('error','Ошибка!','Ошибка сервера');
                    })
                
            }
        });
    };

    changeqImage = (f)=>{
        this.setState((ps,pp)=>{
            return({
                questionDetails:{
                    ...ps.questionDetails,
                    questionimage:(f.link ?`${apis.BASE}/${f.link}`:null)
                },
                submitDisabled:false
            })
        })
    }

    upl=()=>{
        this.setState({
            submitDisabled: true
        })
    }

    render() {
        const { getFieldDecorator } = this.props.form;
        const { Option } = Select;
        const { TextArea } = Input;
        var QuestionImageprops={
            name: 'file',
            action: `${apis.BASE}${apis.FILE_UPLOAD}?Token=${auth.retriveToken()}`,
            listType: 'picture',
        }
        
        return (
            <div className="register-subject-form" >
                <div className="register-trainer-form-body">
                    <Form  onSubmit={this.handleSubmit}>
                        <div>
                            
                            <Row>
                                <Col span={18}>
                                    <Form.Item label="Вопрос" hasFeedback>
                                        {getFieldDecorator('questionbody', {
                                            initialValue : this.props.trainer.questioneditdetails.body,
                                            rules: [{ required: true, message: 'Пожалуйста, введите вопрос!' }],
                                        })(
                                            <TextArea rows={5} />
                                        )}
                                    </Form.Item>
                                </Col>
                                <Col span={6} style={{padding : '0px 20px'}}>
                                    <Form.Item label="Изображение вопроса">
                                        <Upload {...QuestionImageprops} beforeUpload={this.upl} onRemove={this.changeqImage} onSuccess={this.changeqImage}>
                                            <Button>
                                                <Icon type="upload" /> Загрузить
                                            </Button>
                                        </Upload>
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row>
                                <Col>
                                    <NewOptionForm />
                                </Col>
                            </Row>
                            <Row>
                                <Col offset={20}  span={4}>
                                    <Form.Item>
                                        <Button type="primary" htmlType="submit" block>
                                            {this.props.trainer.Questionmode}
                                        </Button>
                                    </Form.Item>
                                </Col>
                            </Row>
                            
                        </div>
                    </Form>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    admin : state.admin,
    trainer : state.trainer,
    chapter : state.chapter
});



const NewQuestionForm = Form.create({ name: 'newQuestion' })(NewQuestion);

export default connect(mapStateToProps,{
    ChangeQuestionConfirmDirty,
    ChangeQuestionModalState,
    ChangeQuestionTableData
})(NewQuestionForm);
