import React, { Component, useState, useEffect } from 'react'
import { connect } from 'react-redux';
import { Button, Modal } from 'antd';
// import Highlighter from 'react-highlight-words';
import  { css } from 'styled-components/macro'
import { 
  ChangeAnswersSearchText,
  ChangeAnswersTableData,
} from '../../../actions/chapterAction';
import Alert from '../../../components/common/alert';
import { SecurePost, SecureGet } from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import styled from "styled-components";
//import "react-multi-carousel/lib/styles.css";
import Counter from './Counter';

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 80vh;
  width: 100%;
  /*background: #7159c1;*/
`;

const ScreenQuiz = styled.div`
  display: flex;
  justify-content: ${props => (props.screen ? "space-between" : "center")};
  align-items: center;
  flex-direction: column;
  max-width: 1200px;
  padding: ${props => (props.screen ? "5rem" : ".5rem")};
  width: 100%;
  height: 80vh;
  background: #fff;
  border-radius: 5px;
  box-shadow: 0px 5px 40px -20px #000;

`;

const Title = styled.h1`
  font-size: 2.5rem;
  // color: #7159c1;
  margin-bottom: 0.5rem;
  text-align: center;
  max-width: 90%;
`;

const Paragraph = styled.p`
  font-size: 1.5rem;
`;

// const Button = styled.button`
//   font-size: 1.2rem;
//   font-weight: bold;
//   width: 40%;
