import React, { Component } from "react";
import { 
  ChangeTestSearchText,
  ChangeTestTableData,
  ChangeTestModalState,
} from '../../../actions/chapterAction';
import { connect } from 'react-redux';
// import classes from "./Quiz.module.css";
class Counter extends Component {
  constructor() {
    super();
    this.state = { 
      time: {}, 
      seconds: 7200,
      startDate : null,
      endDate: null,
    };
    this.timer = 0;
  }

  secondsToTime = secs => {
    let hours = Math.floor(secs / (60 * 60));

    let divisor_for_minutes = secs % (60 * 60);
    let minutes = Math.floor(divisor_for_minutes / 60);

    let divisor_for_seconds = divisor_for_minutes % 60;
    let seconds = Math.ceil(divisor_for_seconds);

    let obj = {
      h: hours,
      m: minutes,
      s: seconds
    };
    return obj;
  };

  componentDidMount = () => {
    this.props.ChangeTestTableData(this.props.id);
    let timeLeftVar = this.secondsToTime(this.state.seconds);
    this.setState({ time: timeLeftVar });

    console.log("IIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIII:",this.props.chapter.materialTableData)

    this.props.chapter.materialTableData.map((d, i) =>{
      if(this.props.id === d._id){
        var minutesToAdd=d.duration;
        var currentDate = new Date(Date.now());
        console.log('currentDate', currentDate);
        var futureDate = new Date(currentDate.getTime() + minutesToAdd*60000);
        console.log("futureDate", futureDate);
        this.state.seconds = (futureDate.getTime() - currentDate.getTime())/1000;
        console.log("Seconds:",this.state.seconds)
      }
    })
  };

  startTimer = () => {
    if (this.timer == 0 && this.state.seconds > 0) {
      this.timer = setInterval(this.countDown, 1000);
    }
  };

  countDown = () => {
    let seconds = this.state.seconds - 1;
    this.setState({
      time: this.secondsToTime(seconds),
      seconds: seconds
    });
    if (seconds == 0) {
      clearInterval(this.timer);
      console.log("Time Up");
    }
  };
  render() {
    // const {startDate, endDate} = this.state;
    // this.state.startDate = this.props.chapter.testTableData.startDate;
    // var minutesToAdd=30;
    //   var currentDate = new Date(this.props.chapter.testTableData.complationDate);
    //   var futureDate = new Date(currentDate.getTime() + minutesToAdd*60000)
    // // this.state.endDate = new Date(this.props.chapter.testTableData.complationDate.getTime() + 30*60000);

    // this.state.seconds = (futureDate.getTime() - currentDate.getTime())/1000;
    // console.log(this.state.seconds)
    // console.log(this.props.chapter.testTableData.startDate)
    // console.log(currentDate.getTime());
    // console.log(startDate);
    // console.log(futureDate);
    return (
      <div>
        {this.startTimer()}
        <h3>
          {this.state.time.h} : {this.state.time.m} :{" "}
          {this.state.time.s}
        </h3>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  chapter : state.chapter,
  trainer : state.trainer
});

export default connect(mapStateToProps,{
  ChangeTestSearchText,
  ChangeTestTableData,
  ChangeTestModalState,
})(Counter);
