import React, { Component } from 'react'
import '../Division/newdivision.css';
import {
    Form,
    Input,
    Button,
    Icon,
    Select,
    Upload
} from 'antd';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import { connect } from 'react-redux';
import { 
    ChangeSlideConfirmDirty,
    ChangeSlideModalState,
    ChangeSlideTableData
} from '../../../actions/chapterAction';
import Alert from '../../../components/common/alert';
import auth from '../../../services/AuthServices';
const { Option } = Select;
class NewSlide extends Component {

    constructor(props){
        super(props);
        this.state={
            materialDetails : {
                //materialimage: this.props.chapter.materialdetails.slides,
                slides :[
                    {
                        body : null,
                        image :null,
                    },
                ],
            },
            submitDisabled:false,
        }
    }
     SlideImageonChange = (f,i)=>{
        var newSlides = [...this.state.materialDetails.slides]
        if(!f){
            delete newSlides[i].image
            newSlides[i].image=null
        }
        else{
            newSlides[i]={
                ...this.state.materialDetails.slides[i],
                image :`${apis.BASE}/${f.link}`
            }
        }
        this.setState({
            submitDisabled:false
        })
        if((newSlides[i].image==='undefined' || newSlides[i].image===undefined || newSlides[i].image===null || newSlides[i].image==='null') && 
            (newSlides[i].body==='undefined' || newSlides[i].body===undefined || newSlides[i].body==='null' || newSlides[i].body==='' || newSlides[i].body===null)){
                newSlides[i]={
                    ...this.state.materialDetails.slides[i],
                }
        }
        this.setState((ps,pp)=>{
            return({
                materialDetails:{
                    ...ps.materialDetails,
                    slides:newSlides
                }
            })
        })
    }

    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
                console.log('Полученные значения формы: ', values);
                
                    SecurePost({
                        url : `${apis.CREATE_SLIDE}`,
                        data : {
                            _id : this.props.chapter.slideId,
                            slbody : values.slbody,                            
                            slimage : values.slimage
                        }
                    }).then((response)=>{
                        if(response.data.success){
                            this.props.ChangeSlideModalState(false,null,'Регистрация');
                            Alert('success','Успешно',response.data.message);
                            this.props.ChangeDivisionTableData();
                        }
                        else{
                            console.log(response.data);
                            this.props.ChangeSlideModalState(false,null,'Регистрация');
                            return Alert('warning','Предупреждение!',response.data.message);
                        }
                    }).catch((error)=>{
                        this.props.ChangeSlideModalState(false,null,'Регистрация');
                        return Alert('error','Ошибка!','Ошибка сервера');
                    })
                
            }
        });
    };

    render() {
        const { getFieldDecorator } = this.props.form;
        var SlideImageprops={
            name: 'file',
            action: `${apis.BASE}${apis.FILE_UPLOAD}?Token=${auth.retriveToken()}`,
            listType: 'picture',
        }
        return (
            <div className="register-division-form">
                <div className="register-division-form-body">
                    <Form  onSubmit={this.handleSubmit}>
                        <Form.Item label="Название" hasFeedback className="input-admin-division">
                            {getFieldDecorator('slbody', {
                                initialValue : this.props.chapter.slidedetails.slbody,
                                rules: [{ required: true, message: 'Пожалуйста, введите название подразделения!', whitespace: true }],
                            })(<Input />)}
                        </Form.Item>
                    
                        <Form.Item label="Слайд">
                        <Upload {...SlideImageprops} beforeUpload={this.upl} onRemove={(f)=>this.SlideImageonChange(null)} onSuccess={(f)=>this.SlideImageonChange(f)}>
                                {getFieldDecorator('slimage', {
                                    initialValue : this.props.chapter.slidedetails.slimage,
                                    rules: [{ required: false, message: 'Пожалуйста, загрузите логотип вашей компании!', whitespace: true }],
                                })(<img src={this.props.chapter.slidedetails.slimage} style={{width: "60px",height: "60px",}}/>)
                                }
                                
                                <Button style={{marginLeft: '100px'}}>
                                    <Icon type="upload" /> Загрузить
                                </Button>
                                
                            </Upload>
                        </Form.Item>

                        
                        <Form.Item>
                            <Button type="primary" htmlType="submit" block>
                                {this.props.chapter.Slidemode}
                            </Button>
                        </Form.Item>
                    </Form>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    admin : state.admin,
    chapter : state.chapter
});



const NewSlideForm = Form.create({ name: 'register' })(NewSlide);

export default connect(mapStateToProps,{
    ChangeSlideConfirmDirty,
    ChangeSlideModalState,
    ChangeSlideTableData
})(NewSlideForm);
