import React, {useState, useEffect} from 'react';
import { connect } from 'react-redux';
import { Badge, Descriptions } from 'antd';
import { 
    ChangeCompanySearchText,
    ChangeCompanyTableData,
    ChangeCompanyModalState
} from '../../../actions/adminAction';

function CompanyList(props) {
    // const [data, setData] = useState(null)
    var data;
    useEffect(()=>{
        props.ChangeCompanyTableData();

        data = props.admin.companyTableData.filter(k=>k._id === props.userDetails.company)[0];
    }, [data])

    return(
        <Descriptions title="User Info" bordered>
            {/* {data = props.admin.companyTableData.filter(k=>k._id === props.userDetails.company)[0]} */}
            {console.log(data)}
            <Descriptions.Item label="Product"></Descriptions.Item>
            <Descriptions.Item label="Billing Mode">Prepaid</Descriptions.Item>
            <Descriptions.Item label="Automatic Renewal">YES</Descriptions.Item>
            <Descriptions.Item label="Order time">2018-04-24 18:00:00</Descriptions.Item>
            <Descriptions.Item label="Usage Time" span={2}>
            2019-04-24 18:00:00
            </Descriptions.Item>
            <Descriptions.Item label="Status" span={3}>
            <Badge status="processing" text="Running" />
            </Descriptions.Item>
            <Descriptions.Item label="Negotiated Amount">$80.00</Descriptions.Item>
            <Descriptions.Item label="Discount">$20.00</Descriptions.Item>
            <Descriptions.Item label="Official Receipts">$60.00</Descriptions.Item>
            <Descriptions.Item label="Config Info">
            Data disk type: MongoDB
            <br />
            Database version: 3.4
            <br />
            Package: dds.mongo.mid
            <br />
            Storage space: 10 GB
            <br />
            Replication factor: 3
            <br />
            Region: East China 1<br />
            </Descriptions.Item>
        </Descriptions>
        
    )
}

const mapStateToProps = state => ({
    admin : state.admin
});

export default connect(mapStateToProps,{
    ChangeCompanySearchText,
    ChangeCompanyTableData,
    ChangeCompanyModalState
})(CompanyList);
