            newOptions[i]={
                ...this.state.questionDetails.options[i],
                isAnswer : e.target.checked
            }
            this.setState((ps,pp)=>{
                return({
                    questionDetails:{
                        ...ps.questionDetails,
                        options:newOptions
                    }
                })
            })
            
        }
        else{   
            this.Customalert()
            return;
        }
        
    }

    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
                console.log(this.state.materialId);
                console.log('Полученные значения формы: ', values);
                this.state.questionDetails.options.forEach((element,i) => {
                    SecurePost({
                        url : `${apis.CREATE_QUESTIONS}`,
                        
                        data : {
                            _id : this.props.trainer.questionId,
                            body : values.questionbody,                            
                            quesimg : this.state.questionDetails.quesimg,
                            material : this.state.materialId,
                            answer1 : values.answer1,
                            trueans1 : element.isAnswer,
                            answer2 : values.answer2,
                            trueans2 : element.isAnswer,
                            answer3 : values.answer3,
                            trueans3 : element.isAnswer,
                            answer4 : values.answer4,
                            trueans4 : element.isAnswer,
                            answer5 : values.answer5,
                            trueans5 : element.isAnswer,
                        }
                    }).then((response)=>{
                        if(response.data.success){
                            this.props.ChangeQuestionModalState(false,null,'Регистрация');
                            Alert('success','Успешно',response.data.message);
                            // this.props.ChangeSlideTableData();
                        }
                        else{
                            console.log(response.data);
                            this.props.ChangeQuestionModalState(false,null,'Регистрация');
                            return Alert('warning','Предупреждение!',response.data.message);
                        }
                    }).catch((error)=>{
                        this.props.ChangeQuestionModalState(false,null,'Регистрация');
                        return Alert('error','Ошибка!','Ошибка сервера');
                    })
                })
            }
        });
    };

