import React, { Component, useState, useEffect } from 'react';
import { Table, Button, Divider, Modal } from 'antd';
import { connect } from 'react-redux';
import { 
    ChangeMaterialSearchText,
    ChangeMaterialTableData,
    ChangeMaterialModalState,
    ChangeAnswersSearchText,
    ChangeAnswersTableData
} from '../../../actions/chapterAction';
import './chapters.css';
import { SecureGet } from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import SliderShow from './slider';
import Test from '../Testing/test';
import Result from '../Testing/result';
//import Alert from '../../../components/common/alert';
import  LocalizedStrings  from  'react-localization' ;

let strings = new LocalizedStrings({
  ru:{
    Names:"Наименование",
    Actions:"Действие",
    Learning:"Урок",
    Tests:"Тест",
    Results:"Результаты",
  },
  kz: {
    
  }
 });


 function Materials(props){
// class Materials extends Component {
  // constructor(props){
  //   super(props);
  //   this.state={
  //     materialId : null,
  //     slideModelVisible : false,
  //     testModelVisible : false,
  //     resultModelVisible : false,
  //     details: null,
  //     loading:true,
  //     disabled:false,
  //   }
  // }
  const [materialId, setMaterialId] = useState(null);
  const [slideModelVisible, setSlideModelVisible] = useState(false);
  const [testModelVisible, setTestModelVisible] = useState(false);
  const [resultModelVisible, setResultModelVisible] = useState(false);


  const OpendetailsModal = (id)=>{
    setMaterialId(id);
    setSlideModelVisible(true);
  }
  
  const ClosedetailsModal = ()=>{
    setMaterialId(null);
    setSlideModelVisible(false);
  }

  const OpentestModal = (id)=>{
    setMaterialId(id);
    setTestModelVisible(true);
  }

  const ClosetestModal = ()=>{
    setMaterialId(null);
    setTestModelVisible(false);
  }

  
  const OpenresultModal = (id)=>{
    setMaterialId(id);
    setResultModelVisible(true);
  }
  
  const CloseresultModal = ()=>{
    setMaterialId(null);
    setResultModelVisible(false);
  }


  useEffect(()=>{
    var ID = this.props.id;
    this.props.ChangeMaterialTableData(ID);
    SecureGet({
       url: `${apis.GET_SINGLE_CHAPTER_DETAILS}/${ID}`,
    }).then((response)=>{
      console.log(response.data.data[0]);
      this.setState({
        details : response.data.data[0],
        loading : false
      })
    }).catch((error)=>{
      console.log(error);
    });
  }, [])

    const columns = [
      {
        title: '№',
        dataIndex: 'num',
        key: 'num',
        width: '5%',
      },   
      {
        title: strings.Names,
        dataIndex: 'topic',
        key: 'topic',
        width: '5%',
      },   
      {
        title: strings.Actions,
        key: '_id',
        dataIndex: '_id',
        width: '25%',
        render: (key) => (
          <span>
            <Button disabled={this.state.disabled} type="primary" icon="" onClick={()=>this.OpendetailsModal(key)}>{strings.Learning}</Button>
            <Divider type="vertical" />
            <Button disabled={this.state.disabled} type="primary" icon="" onClick={()=>this.OpentestModal(key)}>{strings.Tests}</Button>
            <Divider type="vertical" />
            <Button disabled={this.state.disabled} type="primary" icon="" onClick={()=>this.OpenresultModal(key)}>{strings.Results}</Button>
          </span>
        ),
      },
    ];

    return(
      <div className="admin-table-container">
        <Table 
          bordered={true} 
          columns={columns} 
          dataSource={props.chapter.materialTableData} 
          size="medium" 
          pagination={{ pageSize: 5 }}
          loading={props.chapter.materialTableLoadingStatus}
          rowKey="_id" 
        />

        <Modal
          visible={tslideModelVisible}
          title="Урок"
          onCancel={ClosedetailsModal}
          style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
          width="85%"
          destroyOnClose={true}
          footer={[]}
        >
          <SliderShow id={materialId}/>
        </Modal>

        <Modal
          visible={testModelVisible}
          title="Тест"
          onCancel={ClosetestModal}
          style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
          width="80%"
          destroyOnClose={true}
          footer={[]}
        >
          {/* {console.log("Aktilek:", this.props)} */}
          <Test materialDetails={props.chapter.materialTableData} id={materialId} chid={props.id}
          // asn={this.props.chapter.AnswerTableData}
          />
        </Modal>

        <Modal
          visible={tresultModelVisible}
          title="Результат"
          onCancel={CloseresultModal}
          style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
          width="80%"
          destroyOnClose={true}
          footer={[]}
        >
          <Result answerDetails={this.props.chapter.AnswerTableData}/>
        </Modal>
      </div>
    )
  
}

const mapStateToProps = state => ({
  chapter : state.chapter
});

export default connect(mapStateToProps,{
    ChangeMaterialSearchText,
    ChangeMaterialTableData,
    ChangeMaterialModalState,
    ChangeAnswersSearchText,
    ChangeAnswersTableData,
})(Materials);
