import React, { Component } from 'react'
// import './newdivision.css';
import {
    Form,
    Input,
    Button,
    Select,
    Checkbox,
    Modal,
    Row,
    Col
} from 'antd';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import { connect } from 'react-redux';
import { 
    ChangeOptionConfirmDirty,
    ChangeOptionModalState,
    ChangeOptionTableData
} from '../../../actions/trainerAction';
import auth from '../../../services/AuthServices';
import Alert from '../../../components/common/alert';
const { Option } = Select;
class NewOption extends Component {
    constructor(props){
        super(props);
        this.state={
            questionId : null,
        }
    }

    // componentDidMount(){
    //     console.log("Options props", this.props);
    //     this.setState({
    //         questionId:this.props.trainer.questionId
    //     });
    // }

    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
                console.log('Полученные значения формы: ', values);
                
                    SecurePost({
                        url : `${apis.CREATE_OPTION}`,
                        data : {
                            _id : this.props.admin.optionId,
                            optbody : values.optbody,                            
                            isAnswer : values.isAnswer,
                            question : this.props.trainer.questionId
                        }
                    }).then((response)=>{
                        if(response.data.success){
                            this.props.ChangeOptionModalState(false,null,'Создать вопрос');
                            Alert('success','Успешно',response.data.message);
                            this.props.ChangeDivisionTableData();
                        }
                        else{
                            console.log(response.data);
                            this.props.ChangeOptionModalState(false,null,'Создать вопрос');
                            return Alert('warning','Предупреждение!',response.data.message);
                        }
                    }).catch((error)=>{
                        this.props.ChangeOptionModalState(false,null,'Создать вопрос');
                        return Alert('error','Ошибка!','Ошибка сервера');
                    })
                
            }
        });
    };

    render() {
        const { getFieldDecorator } = this.props.form;
        const { TextArea } = Input;
        return (
            <div className="register-division-form">
                <div className="register-division-form-body">
                    <Form  onSubmit={this.handleSubmit}>
                        <Row className="">
                            <Col offset={1} span={13}>
                                <Form.Item label="Вариант" hasFeedback className="input-admin-division">
                                {getFieldDecorator('optbody', {
                                    initialValue : this.props.trainer.optiondetails.optbody,
                                    rules: [{ required: true, message: 'Пожалуйста, введите название подразделения!', whitespace: true }],
                                })(<TextArea rows={2} />)}
                                </Form.Item>
                            </Col>
                            <Col span={2} style={{padding : '55px 10px'}}>
                                <Form.Item>
                                    <Checkbox checked={this.props.trainer.optiondetails.isAnswer} />
                                </Form.Item>
                            </Col>
                        </Row>
                    </Form>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    admin : state.admin,
    trainer : state.trainer,
});

const NewOptionForm = Form.create({ name: 'newOption' })(NewOption);

export default connect(mapStateToProps,{
    ChangeOptionConfirmDirty,
    ChangeOptionModalState,
    ChangeOptionTableData
})(NewOptionForm);
