import React, { Component } from 'react'
import './newcustomer.css';
import {
    Form,
    Input,
    Button,
    Select,
    Icon,
    Upload
} from 'antd';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import { connect } from 'react-redux';
import { 
    ChangeCustomerConfirmDirty,
    ChangeCustomerModalState,
    ChangeCustomerTableData
} from '../../../actions/adminAction';
import Alert from '../../../components/common/alert';
import auth from '../../../services/AuthServices';
const { Option } = Select;
class NewCustomer extends Component {
    constructor(props){
        super(props);
        this.state={
            customerDetails:{
                customerimage: this.props.admin.customerdetails.avatar,
            },
            submitDisabled:false,
        }
    }

    compareToFirstPassword = (rule, value, callback) => {
        const form = this.props.form;
        if (value && value !== form.getFieldValue('password')) {
            callback('passwords are not same !');
        } else {
            callback();
        }
    };

    validateToNextPassword = (rule, value, callback) => {
        const form = this.props.form;
        if (value && this.props.admin.CustomerconfirmDirty) {
            form.validateFields(['confirm'], { force: true });
        }
        callback();
    };

    CustomerImageonChange = (f)=>{
        var newCusimg = this.state.customerDetails.customerimage
        if(!f){
            newCusimg=null
        }
        else{
            newCusimg=`${apis.BASE}/${f.link}`
            
        }
        this.setState({
            submitDisabled:false
        })
        if(newCusimg==='undefined' || newCusimg===undefined || newCusimg===null || newCusimg==='null') {
            //(newOptions[i].body==='undefined' || newOptions[i].body===undefined || newOptions[i].body==='null' || newOptions[i].body==='' || newOptions[i].body===null)){
                newCusimg=this.state.customerDetails.customerimage;    
        }
        this.setState((ps,pp)=>{
            return({
                customerDetails:{
                    ...ps.customerDetails,
                    customerimage:newCusimg
                }
            })
        })
    }

    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
                console.log('Полученные значения формы: ', values);
                
                    SecurePost({
                        url : `${apis.CREATE_CUSTOMER}`,
                        data : {
                            _id : this.props.admin.customerId,
                            name :values.name, 
                            password : values.password,                           
                            emailid : values.emailid,
                            avatar: this.state.customerDetails.customerimage,
                            division : values.division
                        }
                    }).then((response)=>{
                        if(response.data.success){
                            this.props.ChangeCustomerModalState(false,null,'Регистрация');
                            Alert('success','Успешно',response.data.message);
                            this.props.ChangeCustomerTableData(this.props.admin.selectedDivisions);
                        }
                        else{
                            console.log(response.data);
                            this.props.ChangeCustomerModalState(false,null,'Регистрация');
                            return Alert('warning','Предупреждение!',response.data.message);
                        }
                    }).catch((error)=>{
                        console.log(error);
                        this.setState({
                            customerDetails: {
                                customerimage: null,
                            }
                        })
                        this.props.ChangeCustomerModalState(false,null,'Регистрация');
                        return Alert('error','Ошибка!','Ошибка сервера');
                    })
                
            }
        });
    };

    changeqImage = (f)=>{
        this.setState((ps,pp)=>{
            return({
                customerDetails:{
                    ...ps.customerDetails,
                    customerimage:(f.link ?`${apis.BASE}/${f.link}`:null)
                },
                submitDisabled:false
            })
        })
    }

    upl=()=>{
        this.setState({
            submitDisabled: false
        })
    }
    render() {
        const { getFieldDecorator } = this.props.form;
        
          var CustomerImageprops={
            name: 'file',
            action: `${apis.BASE}${apis.FILE_UPLOAD}?Token=${auth.retriveToken()}`,
            listType: 'picture',
        }
        return (
            <div className="register-division-form">
                <div className="register-customer-form-body">
                    <Form  onSubmit={this.handleSubmit}>
                        <Form.Item label="Имя" hasFeedback className="input-admin-customer">
                            {getFieldDecorator('name', {
                                initialValue : this.props.admin.customerdetails.name,
                                rules: [{ required: true, message: 'Пожалуйста, введите свое имя!', whitespace: true }],
                            })(<Input />)}
                        </Form.Item>
                    
                        <Form.Item label="E-mail" hasFeedback className="input-admin-customer">
                            {getFieldDecorator('emailid', {
                                initialValue : this.props.admin.customerdetails.emailid,
                                rules: [
                                    {
                                        type: 'email',
                                        message: 'Введен неверный адрес эл. почты!',
                                    },
                                    {
                                        required: true,
                                        message: 'Пожалуйста, введите свой адрес эл. почты!',
                                    },
                                ],
                            })(<Input />)}
                        </Form.Item> 
                        

                       <Form.Item label="Аватар">
                       <Upload {...CustomerImageprops} beforeUpload={this.upl} onRemove={(f)=>this.CustomerImageonChange(null)} onSuccess={(f)=>this.CustomerImageonChange(f)}>
                                {getFieldDecorator('avatar', {
                                    initialValue : this.props.admin.customerdetails.avatar,
                                    rules: [{ required: false, message: 'Пожалуйста, загрузите ваш аватар!'}],
                                })(<img src={this.props.admin.customerdetails.avatar} style={{width: "60px",height: "80px",}} alt=""/>)
                                }
                                
                                <Button style={{marginLeft: '100px'}}>
                                    <Icon type="upload" /> Загрузить
                                </Button>
                                
                            </Upload>
                        </Form.Item>

                        <Form.Item label="Отдел" hasFeedback>
                            {getFieldDecorator('division', {
                                initialValue : this.props.admin.customerdetails.division,
                                rules: [{ required: false, message: 'Пожалуйста, введите название подразделения!', whitespace: true }],
                            })(
                            <Select 
                                showSearch
                                style = {{width:'100%'}}
                                placeholder="Выберите отдела"
                                optionFilterProp= "s"
                            >
                                {
                                    this.props.admin.divisionTableData.map((d,i)=><Option key={d._id} s={d.name} value={d._id}>{d.name}</Option>)
                                }

                            </Select>
                            )}
                        </Form.Item>
                    

                        
                        <Form.Item>
                            <Button type="primary" htmlType="submit" block>
                                {this.props.admin.Customermode}
                            </Button>
                        </Form.Item>
                    </Form>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    admin : state.admin
});



const NewCustomerForm = Form.create({ name: 'register' })(NewCustomer);

export default connect(mapStateToProps,{
    ChangeCustomerConfirmDirty,
    ChangeCustomerModalState,
    ChangeCustomerTableData
})(NewCustomerForm);
