import React, { Component } from 'react'
import './newmaterials.css';
import {
    Form,
    Input,
    Button,
    Select,
    InputNumber
} from 'antd';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import { connect } from 'react-redux';
import { 
    ChangeMaterialConfirmDirty,
    ChangeMaterialModalState,
    ChangeMaterialTableData,
    ChangeChapterModalState,
    ChangeChapterTableData
} from '../../../actions/chapterAction';
import Alert from '../../../components/common/alert';
import auth from '../../../services/AuthServices';
const { Option } = Select;



class NewMaterial extends Component {
    constructor(props){
        super(props);
        this.state={
            chapterId : null,
        }
        
    }
    componentDidMount(){
        this.setState({
            chapterId:this.props.cid
        });
    }

    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {

                console.log('Полученные значения формы: ', this.props);
                
                    SecurePost({
                        url : `${apis.CREATE_MATERIAL}`,
                        data : {
                            _id : this.props.chapter.materialId,
                            num :values.num,
                            topic :values.topic,
                            chapter: this.props.cid,
                            duration : values.duration,
                            atteptms : values.attempts,
                            period : values.period,
                            numQuestions : values.numQuestions
                        }
                    }).then((response)=>{
                        if(response.data.success){
                            this.props.ChangeMaterialModalState(false,null,'Регистрация');
                            Alert('success','Успешно',response.data.message);
                        }
                        else{
                            console.log(response.data);
                            this.props.ChangeMaterialModalState(false,null,'Регистрация');
                            return Alert('warning','Предупреждение!',response.data.message);
                        }
                    }).catch((error)=>{
                        this.props.ChangeMaterialModalState(false,null,'Регистрация');
                        return Alert('error','Ошибка!','Ошибка сервера');
                    })
                
            }
        });
    };

    render() {
        const { getFieldDecorator } = this.props.form;
        var MaterialImageprops={
            name: 'file',
            action: `${apis.BASE}${apis.FILE_UPLOAD}?Token=${auth.retriveToken()}`,
            listType: 'picture',
        }
        
        return (
            <div className="register-company-form">
                <div className="register-company-form-body">
                    <Form onSubmit={this.handleSubmit}>
                    <Form.Item label="№" className="input-admin-company">
                            {getFieldDecorator('num', {
                                initialValue : this.props.chapter.materialdetails.num,
                                rules: [{ required: false, message: 'Пожалуйста, введите название!', whitespace: true }],
                            })(<Input />)}
                        </Form.Item>
                        <Form.Item label="Название" className="input-admin-company" hasFeedback>
                            {getFieldDecorator('topic', {
                                initialValue : this.props.chapter.materialdetails.topic,
                                rules: [{ required: true, message: 'Пожалуйста, введите название!', whitespace: true }],
                            })(<Input />)}
                        </Form.Item>
                        <Form.Item label="Продолжительность теста" hasFeedback className="input-admin-company">
                            {getFieldDecorator('duration', {
                                initialValue : this.props.chapter.materialdetails.duration,
                                rules: [{ required: true, message: 'Пожалуйста, укажите продолжительность теста' }],
                            })(
                                <InputNumber style={{width:'100%'}}  placeholder="Продолжительность теста" min={0} max={180}/>
                            )}
                        </Form.Item> 

                        <Form.Item label="Количество попыток" hasFeedback className="input-admin-company">
                            {getFieldDecorator('attempts', {
                                initialValue : this.props.chapter.materialdetails.attempts,
                                rules: [{ required: true, message: 'Пожалуйста, укажите количество попыток' }],
                            })(
                                <InputNumber style={{width:'100%'}}  placeholder="Количество попыток"/>
                            )}
                        </Form.Item> 

                        <Form.Item label="Период ожидания, дней" hasFeedback className="input-admin-company">
                            {getFieldDecorator('period', {
                                initialValue : this.props.chapter.materialdetails.period,
                                rules: [{ required: true, message: 'Пожалуйста, укажите период ожидания' }],
                            })(
                                <InputNumber style={{width:'100%'}}  placeholder="Период ожидания"/>
                            )}
                        </Form.Item> 
                        <Form.Item label="Количество вопросов для отображения пользователю" hasFeedback className="input-admin-company">
                            {getFieldDecorator('numQuestions', {
                                initialValue : this.props.chapter.materialdetails.numQuestions,
                                rules: [{ required: true, message: 'Количество вопросов для отображения пользователю' }],
                            })(
                                <InputNumber style={{width:'100%'}}  placeholder="Количество вопросов"/>
                            )}
                        </Form.Item> 
                        
                        <Form.Item>
                            <Button type="primary" htmlType="submit" block>
                                {this.props.chapter.Materialmode}
                            </Button>
                        </Form.Item>
                    </Form>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    chapter : state.chapter
});



const NewMaterialForm = Form.create({ name: 'register' })(NewMaterial);

export default connect(mapStateToProps,{
    ChangeMaterialConfirmDirty,
    ChangeMaterialModalState,
    ChangeMaterialTableData,
    ChangeChapterModalState,
    ChangeChapterTableData
})(NewMaterialForm);
