import React, { Component } from 'react';
import { Skeleton, Tabs, Button, Icon, Upload, Row, Col } from 'antd';
//import './questiondetails.css';
import apis from '../../../services/Apis';
import { SecureGet } from '../../../services/axiosCall';
import AllMaterial from './allmaterials';
import {
    MasterDetailsViewItem,
    MasterDetailsViewItemMaster,
    MasterDetailsView,
    MasterDetailsViewItemDetails,
    Text
  } from 'react-desktop/windows';
const { TabPane } = Tabs;

export default class ChapterDetails extends Component {
    constructor(props){
        super(props);
        this.state={
            //loading : true,
            details:null
        }
    }

    tabChange = (key)=>{
        console.log(key)
    }

    componentDidMount(){
        var ID = this.props.id;
        SecureGet({
            url: `${apis.GET_SINGLE_CHAPTER_DETAILS}/${ID}`,
        }).then((response)=>{
            console.log(response.data.data[0]);
            this.setState({
                details : response.data.data[0],
                //loading:false
            })
        }).catch((error)=>{
            console.log(error);
        })
    }
    
    render() {
        return (
            <div>
              <Skeleton loading={this.state.loading} active avatar>
            <Tabs defaultActiveKey="1" onChange={ (e)=>this.tabChange(e)}>
              <TabPane tab={ <span><Icon type="home" />Basic Info</span> } key="1">
                <Button type="primary" icon="user-add" style={{marginBottom:'10px'}} onClick={()=>this.openModal(null,'Регистрация')}>
                  Добавить
                </Button>
                <div>
                
                    <MasterDetailsView >
                      { this.props.chapter.materialTableData.map((d,i)=>
                      <MasterDetailsViewItem key={i}>
                        <MasterDetailsViewItemMaster push width='300px'>
                          {d.topic}
                          <Button type="primary" icon="edit" style={{float: 'right'}} onClick={()=>this.OpendetailsModal(d._id)}>Тест</Button>
                          <Button type="primary" shape="circle" style={{float: 'right'}} icon="edit" onClick={()=>this.openModal(d._id,'Сохранить')}/>
                        </MasterDetailsViewItemMaster>

                        <MasterDetailsViewItemDetails background>
                        {/* <label>{d.topic}</label> */}
                          {/* {this.props.chapter.materialTableData.filter((el)=>d.topic === el.topic).map((dd,i)=> */}
                        
                          <Table 
                            bordered={true} 
                            columns={columns} 
                            dataSource={this.props.chapter.materialTableData.filter((el)=>d.topic === el.topic)} 
                            size="medium" 
                            pagination={{ pageSize: 5 }}
                            loading={this.props.chapter.materialTableLoadingStatus}
                            rowKey="_id" 
                          />

                        </MasterDetailsViewItemDetails>
                      </MasterDetailsViewItem>
                    )}
                      </MasterDetailsView >
          
                </div>
                <Modal
                      visible={this.props.chapter.MaterialmodalOpened}
                      title={false}
                      onOk={this.handleOk}
                      onCancel={this.closeModal}
                      style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
                      width="40%"
                      destroyOnClose={true}
                      footer={[
                        
                  ]}
                  >
                  <NewMaterialForm />
                </Modal>           
              </TabPane>
              <TabPane tab={ <span><Icon type="question-circle" />Question</span> } key="2">
                <Tab2 details={this.state.details} />
              </TabPane>
            </Tabs>   
                    
          </Skeleton>
            </div>
        )
    }
}


function Tab2(props){
    const optn =['A','B','C','D','E']
    const Optiondata = props.details;
    console.log(`details : ${Optiondata}`);
    return(
        <div className="mainQuestionDetailsContaine">
            <div className="questionDetailsBody">
                {Optiondata.body}
            </div>
            {Optiondata.quesimg?
                <div className="questionDetailsImageContainer">
                    <img alt="unable to load" className="questionDetailsImage" src={Optiondata.quesimg} />  
                </div>
                : null
            }
            <div>
                {Optiondata.options.map((d,i)=>{
                    return(
                        <div key={i}>
                            <Row type="flex" justify="center" className="QuestionDetailsOptions">
                                <Col span={2}>
                                    {
                                        d.isAnswer?<Button className="green" shape="circle">{optn[i]}</Button>:<Button type="primary" shape="circle">{optn[i]}</Button>
                                    }
                                    
                                </Col>
                                {d.optimg?
                                    <Col span={6} style={{padding:'5px'}}>
                                        <img alt="unable to load" className="questionDetailsImage" src={d.optimg} />
                                    </Col>
                                :
                                    null
                                }
                                {d.optimg?
                                    <Col span={14}>{d.optbody}</Col>
                                :
                                    <Col span={20}>{d.optbody}</Col>
                                }
                            </Row>
                           
                        </div>
                    )
                })}
            </div>
        </div>
        )
}
