import React, { Component, useState } from 'react'
import Slider from 'react-touch-drag-slider'
import { connect } from 'react-redux';
import { Table, Input, Button, Typography, Divider, Icon, Modal, Popconfirm } from 'antd';
import Highlighter from 'react-highlight-words';
import styled, { createGlobalStyle, css } from 'styled-components/macro'
import { 
    ChangeQuestionSearchText,
    ChangeQuestionTableData,
    ChangeQuestionModalState
} from '../../../actions/trainerAction';
import Alert from '../../../components/common/alert';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import Test2 from './test2';
import Test3 from './test3';


class Test extends Component {
    constructor(props){
      super(props);
      this.state={
        QuestiontableLoading : false,
        details : null,
        materialId : null,
        testModelVisible:false
      }
    }
  
    componentDidMount(){
      this.props.ChangeQuestionTableData(this.props.id);
    }
    OpentestModal = (id)=>{
      this.setState((previousState,previousProps)=>{
        return{
          materialId:id,
          testModelVisible:true
        }
      })
    }
  
    ClosetestModal = ()=>{
      this.setState((previousState,previousProps)=>{
        return{
          materialId:null,
          testModelVisible:false
        }
      })
    }
    
    getColumnSearchProps = dataIndex => ({
      filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
        <div style={{ padding: 8 }}>
          <Input
            ref={node => {
              this.searchInput = node;
            }}
            placeholder={`Search ${dataIndex}`}
            value={selectedKeys[0]}
            onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
            onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
            style={{ width: 188, marginBottom: 8, display: 'block' }}
          />
          <Button
            type="primary"
            onClick={() => this.handleSearch(selectedKeys, confirm)}
            icon="search"
            size="small"
            style={{ width: 90, marginRight: 8 }}
          >
            Search
          </Button>
          <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
            Reset
          </Button>
        </div>
      ),
      filterIcon: filtered => (
        <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
      ),
      onFilter: (value, record) =>
        record[dataIndex]
          .toString()
          .toLowerCase()
          .includes(value.toLowerCase()),
      onFilterDropdownVisibleChange: visible => {
        if (visible) {
          setTimeout(() => this.searchInput.select());
        }
      },
      render: text => (
        <Highlighter
          highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
          searchWords={[this.props.admin.DivisionsearchText]}
          autoEscape
          textToHighlight={text.toString()}
        />
      ),
    });
  
    handleSearch = (selectedKeys, confirm) => {
      confirm();
      this.props.ChangeDivisionSearchText(selectedKeys[0])
    };
  
    handleReset = clearFilters => {
      clearFilters();
      this.props.ChangeDivisionSearchText('')
    };

    render() {
      const columns = [
        {
          title: 'Название',
          dataIndex: 'body',
          key: 'body',
          width: '25%',
          ...this.getColumnSearchProps('name'),
        },
        {
          title: 'Описание',
          dataIndex: 'description',
          key: 'description',
          width: '25%',
          ...this.getColumnSearchProps('description'),
        },   
        {
          title: 'Компания',
          dataIndex: 'company.name',
          key: 'company.name',
          width: '25%',
          //...this.getColumnSearchProps('company.name'),
        },    
        {
          title: 'Действие',
          key: '_id',
          dataIndex: '_id',
          render: (key) => (
            <span>
              <Button type="primary" shape="circle" icon="edit" onClick={()=>this.openModal(key,'Сохранить')}/>
                <Divider type="vertical" />
                <Popconfirm
                  title="Вы уверены？"
                  cancelText="Нет"
                  okText="Да"
                  onConfirm={()=>{this.deleteDivision(key)}}
                  icon={<Icon type="delete" style={{ color: 'red' }} />}
                >
                  <Button type="danger" shape="circle" icon="delete" />
                </Popconfirm>
              
            </span>
          ),
        },
      ];
      var questions=[];
      const { Title } = Typography;
      this.props.trainer.QuestionTableData.map((d,i)=>{
          
          questions[i]=(
              {
                  question : d.body,
                  optionA : d.answer1,
                  optionB : d.answer2,
                  optionC : d.answer3,
                  optionD : d.answer4,
                  optionE : d.answer5,
                  answer : (d.trueans1===true ? d.answer1 : null) || (d.trueans2===true ? d.answer2 : null) || (d.trueans3===true ? d.answer3 : null) ||
                           (d.trueans4===true ? d.answer4 : null) || (d.trueans5===true ? d.answer5 : null)
                  // answerOptions: [
                  //   { answerText: d.answer1, isCorrect: d.trueans1 },
                  //   { answerText: d.answer2, isCorrect: d.trueans2 },
                  //   { answerText: d.answer3, isCorrect: d.trueans3 },
                  //   { answerText: d.answer4, isCorrect: d.trueans4 },
                  //   { answerText: d.answer5, isCorrect: d.trueans5}
                  // ]
              }
          )
          
      })
        console.log("fg", questions)
          return (
            <div className="admin-table-container">
                <Button type="primary" icon="user-add" style={{marginBottom:'10px'}} onClick={()=>this.OpentestModal(key)}>
                Добавить
                </Button> 
                <div className="register-division-form-header">
                  <Title level={4} style={{color:'#fff',textAlign:'center'}}>Список отделов</Title>
                </div>
                <Table 
                  bordered={true} 
                  columns={columns} 
                  dataSource={this.props.trainer.questionTableData} 
                  size="medium" 
                  pagination={{ pageSize: 5 }}
                  loading={this.props.trainer.questionTableLoadingStatus}
                  rowKey="_id" 
                />;
                <Modal
                  visible={this.props.trainer.QuestionmodalOpened}
                  title={false}
                  onOk={this.handleOk}
                  onCancel={this.closeModal}
                  style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
                  width="40%"
                  destroyOnClose={true}
                  footer={[
                    
                  ]}
                >
                  <Test3 details={questions}/>
                </Modal>
              </div>
              
          )
      }
  }
  
  const mapStateToProps = state => ({
      chapter : state.chapter,
      trainer : state.trainer
  });
  
  export default connect(mapStateToProps,{
      ChangeQuestionSearchText,
      ChangeQuestionTableData,
      ChangeQuestionModalState
  })(Test);




