import React, { Component } from 'react'
import './newchapter.css';
import {
    Form,
    Input,
    Button,
    //Select,
    Upload,
    Icon
} from 'antd';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import { connect } from 'react-redux';
import { 
    ChangeChapterConfirmDirty,
    ChangeChapterModalState,
    ChangeChapterTableData
} from '../../../actions/chapterAction';
import Alert from '../../../components/common/alert';
import auth from '../../../services/AuthServices';
//const { Option } = Select;
class NewChapter extends Component {

    constructor(props){
        super(props);
        this.state={
            chapterDetails:{
                chapterimage1: this.props.chapter.chapterdetails.icon1,
                chapterimage2: this.props.chapter.chapterdetails.icon2,
                chapterimage3: this.props.chapter.chapterdetails.icon3,
                chapterimage4: this.props.chapter.chapterdetails.icon4,
            },
            submitDisabled:false,
        }
    }

    ChapterImageonChange = (f)=>{
        var newchapimg = this.state.chapterDetails.chapterimage
        if(!f){
            newchapimg=null
        }
        else{
            newchapimg=`${apis.BASE}/${f.link}`
            
        }
        this.setState({
            submitDisabled:false
        })
        if(newchapimg==='undefined' || newchapimg===undefined || newchapimg===null || newchapimg==='null') {
            //(newOptions[i].body==='undefined' || newOptions[i].body===undefined || newOptions[i].body==='null' || newOptions[i].body==='' || newOptions[i].body===null)){
                newchapimg=this.state.chapterDetails.chapterimage;    
        }
        this.setState((ps,pp)=>{
            return({
                chapterDetails:{
                    ...ps.chapterDetails,
                    chapterimage:newchapimg
                }
            })
        })
    }

    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
                console.log('Полученные значения формы: ', values);
                
                    SecurePost({
                        url : `${apis.CREATE_CHAPTER}`,
                        data : {
                            _id : this.props.chapter.chapterId,
                            name :values.name,                            
                            descr : values.descr,
                            icon1: this.state.chapterDetails.chapterimage1,
                            icon2: this.state.chapterDetails.chapterimage2,
                            icon3: this.state.chapterDetails.chapterimage3,
                            icon4: this.state.chapterDetails.chapterimage4,
                            materials : this.state.materialId
                        }
                    }).then((response)=>{
                        if(response.data.success){
                            this.props.ChangeChapterModalState(false,null,'Регистрация');
                            Alert('success','Успешно',response.data.message);
                            this.props.ChangeChapterTableData();
                        }
                        else{
                            console.log(response.data);
                            this.props.ChangeChapterModalState(false,null,'Регистрация');
                            return Alert('warning','Предупреждение!',response.data.message);
                        }
                    }).catch((error)=>{
                        console.log(error);
                        this.setState({
                            chapterDetails: {
                                chapterimage: null,
                            }
                        })
                        this.props.ChangeChapterModalState(false,null,'Регистрация');
                        return Alert('error','Ошибка!','Ошибка сервера');
                    })
                
            }
        });
    };


    changeqImage = (f)=>{
        this.setState((ps,pp)=>{
            return({
                chapterDetails:{
                    ...ps.chapterDetails,
                    chapterimage:(f.link ?`${apis.BASE}/${f.link}`:null)
                },
                submitDisabled:false
            })
        })
    }

    upl=()=>{
        this.setState({
            submitDisabled: false
        })
    }
    render() {
        const { getFieldDecorator } = this.props.form;
        
          var ChapterImageprops={
            name: 'file',
            action: `${apis.BASE}${apis.FILE_UPLOAD}?Token=${auth.retriveToken()}`,
            listType: 'picture',
        }
        return (
            <div className="register-chapter-form">
                <div className="register-chapter-form-body">
                    <Form  onSubmit={this.handleSubmit}>
                        <Form.Item label="Наименование" hasFeedback className="input-admin-chapter">
                            {getFieldDecorator('name', {
                                initialValue : this.props.chapter.chapterdetails.name,
                                rules: [{ required: true, message: 'Пожалуйста, введите наименование!', whitespace: true }],
                            })(<Input />)}
                        </Form.Item>
                    
                        <Form.Item label="Описание" hasFeedback className="input-admin-chapter">
                            {getFieldDecorator('descr', {
                                initialValue : this.props.chapter.chapterdetails.descr,
                                rules: [{ required: true, message: 'Описание!', whitespace: true }],
                            })(<Input />)}
                        </Form.Item>
                        
                       <Form.Item label="Достижения">
                       <Upload {...ChapterImageprops} beforeUpload={this.upl} onRemove={(f)=>this.ChapterImageonChange(null)} onSuccess={(f)=>this.ChapterImageonChange(f)}>
                                {getFieldDecorator('icon1', {
                                    initialValue : this.props.chapter.chapterdetails.icon1,
                                    rules: [{ required: false, message: 'Пожалуйста, загрузите ваш аватар!'}],
                                })(<img src={this.props.chapter.chapterdetails.icon1} style={{width: "60px",height: "80px",}} alt=""/>)
                                }
                                
                                <Button style={{marginLeft: '100px'}}>
                                    <Icon type="upload" /> Загрузить
                                </Button>
                                
                            </Upload>
                            </Form.Item>

                        <Form.Item>
                            <Button type="primary" htmlType="submit" block>
                                {this.props.chapter.Chaptermode}
                            </Button>
                        </Form.Item>
                    </Form>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    chapter : state.chapter,
    trainer : state.trainer,
    admin : state.admin
});



const NewChapterForm = Form.create({ name: 'register' })(NewChapter);

export default connect(mapStateToProps,{
    ChangeChapterConfirmDirty,
    ChangeChapterModalState,
    ChangeChapterTableData
})(NewChapterForm);
