import React, { Component, useState, useEffect } from 'react';
import { View, StyleSheet } from "react-native";
import Slideshow from "react-native-image-slider-show";
import { Table, Button, Typography, Divider, Icon, Modal, Popconfirm } from 'antd';
import { connect } from 'react-redux';
import { 
    ChangeSlideSearchText,
    ChangeSlideTableData,
    ChangeSlideModalState
} from '../../../actions/chapterAction';
// import '../Division/alldivision.css'
import Alert from '../../../components/common/alert';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';


class Slider extends Component {

    constructor(props){
        super(props);
        this.state={
        SlidetableLoading : false
        }
    }

    componentDidMount(){
        this.props.ChangeSlideTableData(this.props.id);
    }

    render() {
        console.log("A",this.props.admin);
        const dataSource = this.props.admin.Slide;
        const ImageSlider = () => {
            const [position, setPosition] = useState(0);
          
            useEffect(() => {
                const toggle = setInterval(() => {
                    setPosition(position === dataSource.length - 1 ? 0 : position + 1);
                }, 3000);
          
                return () => clearInterval(toggle);
            });
          
        return (
            <View>
              <Slideshow position={position} dataSource={dataSource} />
            </View>
            );
        };

    }
}

const styles = StyleSheet.create({});
          
const mapStateToProps = state => ({
    admin : state.admin,
    chapter : state.chapter
});

export default connect(mapStateToProps,{
    ChangeSlideSearchText,
    ChangeSlideTableData,
    ChangeSlideModalState
})(Slider);
