import React, { Component } from 'react'
import { connect } from 'react-redux';
import { Button } from 'antd';
// import Highlighter from 'react-highlight-words';
import  { css } from 'styled-components/macro'
import { 
  //LoginInfo,
  ChangeTestSearchText,
  ChangeTestTableData,
  ChangeTestModalState,
  ChangeAnswersSearchText,
  ChangeAnswersTableData,
  // ChangeAnswersModalState,
} from '../../../actions/chapterAction';

import Alert from '../../../components/common/alert';
import { SecurePost } from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import TestPage from './testpage';
import styled from "styled-components";
import "react-multi-carousel/lib/styles.css";
//import Counter from './Counter';

import  LocalizedStrings  from  'react-localization' ;
let strings = new LocalizedStrings({
  ru:{
    Succes:"Успешно",
    Warning:"Предупреждение!",
    Err:"Ошибка!",
    ErrorServer:"Ошибка сервера",
    Testing:"Тестирование",
    GoTest:"Начать тест",
    ContTest:"Продолжить тест",
  },
  kz: {
    Succes:"Сәтті",
    Warning:"Ескерту!",
    Err:"Қате!",
    ErrorServer:"Сервердің қатесі",
    Testing:"Тестілеу",
    GoTest:"Тесттi бастау",
    ContTest:"Тесттi жалғастыру",
  }
 });
const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 80vh;
  width: 100%;
  /*background: #7159c1;*/
`;

const ScreenQuiz = styled.div`
  display: flex;
  justify-content: ${props => (props.screen ? "space-between" : "center")};
  align-items: center;
  flex-direction: column;
  max-width: 1200px;
  padding: ${props => (props.screen ? "5rem" : ".5rem")};
  width: 100%;
  height: 80vh;
  background: #fff;
  border-radius: 5px;
  box-shadow: 0px 5px 40px -20px #000;

`;

const Title = styled.h1`
  font-size: 2.5rem;
  // color: #7159c1;
  margin-bottom: 0.5rem;
  text-align: center;
  max-width: 90%;
`;

// const Paragraph = styled.p`
//   font-size: 1.5rem;
// `;

// const Button = styled.button`
//   font-size: 1.2rem;
//   font-weight: bold;
//   width: 40%;
//   height: 3rem;
//   // color: #fff;
//   border: none;
//   // background: #7159c1;
//   border-radius: 5px;
//   box-shadow: 0px 3px 3px 0px #666;

//   @-webkit-keyframes pulsate {
//     0% {transform: scale(0.1, 0.1); opacity: 0.0;}
//     50% {opacity: 1.0;}
//     100% {transform: scale(7, 7); opacity: 0.0;}
// }
// `;

const ButtonScr = styled.button`
  font-size: 1.4rem;
  z-index: 10;
  position: fixed;
  top: 15%;
  ${(props) =>
    props.right
      ? css`
          right: 25rem;
        `
      : css`
          left: 25rem;
        `}
`

const ContainerLabel = styled.div`
  display: flex;
  align-items: flex-start;
  flex-direction: column;
  margin: 0 auto 0.5rem auto;
`;

const Label = styled.label`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Radio = styled.input.attrs({ type: 'checkbox' })`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 15px;
  height: 15px;
  border-radius: 50%;
  border: 7.5px solid black;
  margin: 0 0.5rem 0 0;
  background: ${props => (props.clickedLabel ? "#7159c1" : "#fff")};
`;

// const IntoRadio = styled.div`
//   width: 5px;
//   height: 5px;
//   border-radius: 3px;
//   background: #fff;
// `;

// const Error = styled.div`
//   top: 22rem;
//   color: red;
//   position: absolute;
//   font-weight: bold;
// `;


function Test(props){
  const [loading, setLoading] = useState(false)
  const [screen, setScreen] = useState(true);
  const [count, setCount] = useState(0)
  const [answer, setAnswer] = useState(0);
  const [matId, setMatId] = useState(this.props.id);
  const [disableButton, setDisableButton] = useState(false);
  const [testStarted, setTestStarted] = useState(false);
  const [testEnded, setTestEnded] = useState(false);
  const [testDidNotStart, setTestDidNotStart] = useState(true);

  useEffect(()=>{
    console.log("matId", this.state.matId)
    this.props.ChangeTestTableData(this.props.id);
    this.props.ChangeAnswersTableData(this.props.id);
  }, [])

  const renderInitial = () => {
    // console.log("renderInitial")
    // const { count } = this.state;

    setScreen(false);
    setLoading(false);
    setCount(1);
    setTestStarted(true);
    setTestEnded(false);
    setTestDidNotStart(false);
    // this.setState({
    //   screen: false,
    //   loading: false,
    //   count: 1,
    //   testStarted: true,
    //   testEnded: false,
    //   testDidNotStart: false,
    // });
  };

  const renderScreenInitial = () => {
    // console.log('THIS:',this.props.chapter)
    if((this.props.chapter.testTableData === undefined)){
      console.log("1 CREATE_TEST", this.props.chapter.testId)
      SecurePost({
        url : `${apis.CREATE_TEST}`,
        data : {
          _id: this.props.chapter.testId,
            chapterid : this.props.chid,
            materialid : this.props.id,
            startDate : Date().toLocaleString(),
            complationDate : Date(),
            startTest: false,
            endTest: false,
            result: 0
          }
        })
        .then((response)=>{
          if(response.data.success){
              this.props.ChangeTestModalState(false,null, 'Save'); 
              Alert('success','Успешно',response.data.message);
          }
          else{
              this.props.ChangeTestModalState(false,null, 'Save');
              return Alert('warning','Предупреждение!',response.data.message);
          }
        })
        .catch((error)=>{
            this.props.ChangeTestModalState(false,null, 'Save');
            return Alert('error','Ошибка!','Ошибка сервера');
        })
    }
      console.log("CREATE_ANSWERS", this.props.chapter.testid) 
      console.log("this.props.chapter.testTableData:", this.props.chapter.testTableData)
    if(this.props.chapter.testTableData!==null || this.props.chapter.testTableData!==undefined || this.props.chapter.testTableData!=='null' || this.props.chapter.testTableData!=='undefined'){ 
      console.log("this.props.chapter.testTableData222:", this.props.chapter.testTableData)
      SecurePost({
        url : `${apis.CREATE_ANSWERS}`,
