import React, { Component } from 'react'
import './newquestion.css';
import {
    Form,
    Input,
    Button,
    Select,
    Row,
    Col,
    Checkbox,
    Modal,
    Upload,
    Icon,
    InputNumber 
} from 'antd';
import { connect } from 'react-redux';
import { 
    ChangeQuestionConfirmDirty,
    ChangeQuestionTableData,
    ChangeQuestionModalState
} from '../../../actions/trainerAction';
import { SecurePost,SecureGet } from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import Alert from '../../../components/common/alert';
import auth from '../../../services/AuthServices';



class NewQuestion extends Component {
    constructor(props){
        super(props);
        this.state={
            
            questionDetails:{
                questionimage:null,
                // options :[
                //     {
                //         image :null,
                //         body : null,
                //         isAnswer :false
                //     },
                //     {
                //         image :null,
                //         body : null,
                //         isAnswer :false
                //     },
                //     {
                //         image :null,
                //         body : null,
                //         isAnswer :false
                //     },
                //     {
                //         image :null,
                //         body : null,
                //         isAnswer :false
                //     },
                //     {
                //         image :null,
                //         body : null,
                //         isAnswer :false
                //     }
                // ] ,  
            },
            adding:false,
            submitDisabled:false,
        }
        
    }

    componentDidMount(){
        // console.log("Hello:",this.props.trainer.questioneditdetails);
        this.setState({
            materialId:this.props.qnid
        });
    }

    Customalert = ()=>{
        Modal.confirm({
            title: 'Подтверждать',
            content: 'пустой параметр не может быть установлен в качестве ответа',
            okText: 'Ok',
            cancelText: null,
        });
    }
    
    OptionTextChange =(e,i)=>{
        // console.log("E", e);
        // console.log(this.props);
        var newOptions = [...this.props.trainer.questioneditdetails.options]
        newOptions[i]={
            ...this.props.trainer.questioneditdetails.options[i],
            optbody : e.target.value
        }
        if((newOptions[i].optimg==='undefined' || newOptions[i].optimg===undefined || newOptions[i].optimg===null || newOptions[i].optimg==='null') && 
            (newOptions[i].optbody==='undefined' || newOptions[i].optbody===undefined || newOptions[i].optbody==='null' || newOptions[i].optbody==='' || newOptions[i].optbody===null)){
                newOptions[i]={
                    ...this.props.trainer.questioneditdetails.options[i],
                    isAnswer : false
                }
                this.setState((ps,pp)=>{
                    return({
                        questioneditdetails:{
                            ...pp.questioneditdetails,
                            options:newOptions
                        }
                    })
                })
        }
        this.setState((ps,pp)=>{
            return({
                questioneditdetails:{
                    ...pp.questioneditdetails,
                    options:newOptions
                }
            })
        })     
    }

    AnswerOptionSwitch = (e,i)=>{
        if((this.props.questioneditdetails.options[i].optbody!=='' && this.props.questioneditdetails.options[i].optbody!==null)
            || (this.props.questioneditdetails.options[i].optimg!==null && this.props.questioneditdetails.options[i].optimg!=='undefined' && this.props.questioneditdetails.options[i].optimg!==undefined)
        ){
            var newOptions = [...this.props.questioneditdetails.options]
            newOptions[i]={
                ...this.props.questioneditdetails.options[i],
                isAnswer : e.target.checked
            }
            this.setState((ps,pp)=>{
                return({
                    questioneditdetails:{
                        ...pp.questinoeditdetails,
                        options:newOptions
                    }
                })
            })
        }
        else{   
            this.Customalert()
            return;
        }
    }

    OptionImageonChange = (f,i)=>{
        var newOptions = [...this.props.questioneditdetails.options]
        if(!f){
            delete newOptions[i].optimg
            newOptions[i].optimg=null
        }
        else{
            newOptions[i]={
                ...this.props.questioneditdetails.options[i],
                optimg :`${apis.BASE}/${f.link}`
            }
        }
        this.setState({
            submitDisabled:false
        })
        if((newOptions[i].optimg==='undefined' || newOptions[i].optimg===undefined || newOptions[i].optimg===null || newOptions[i].optimg==='null') && 
            (newOptions[i].optbody==='undefined' || newOptions[i].optbody===undefined || newOptions[i].optbody==='null' || newOptions[i].optbody==='' || newOptions[i].body===null)){
                newOptions[i]={
                    ...this.props.questioneditdetails.options[i],
                    isAnswer : false
                }
        }
        this.setState((ps,pp)=>{
            return({
                questioneditdetails:{
                    ...ps.questioneditdetails,
                    options:newOptions
                }
            })
        })
    }


    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            console.log(values);
            if (!err) {
                var f=1;
                var ans=0;
                var opts=[]
                console.log('Полученные значения формы: ', values);
                this.state.questionDetails.options.forEach((element,i) => {
                    opts.push({
                        optbody:element.body,
                        optimg:element.image,
                        isAnswer:element.isAnswer
                    });
                    if((element.image==='undefined' || element.image===undefined || element.image===null || element.image==='null')&&(element.body==='' ||element.body===null || element.body==='null' || element.body==='undefined' || element.body===undefined )){
                        f=0;
                    }
                    if(element.isAnswer){
                        ans=ans+1
                    }
                })
                if(f){
                    if(!ans){
                        Alert('warning','Предупреждение!','Должен быть хотя бы один правильный ответ');
                    }
                    else{
                        this.setState({
                            adding:true
                        });
                        SecurePost({
                            url:apis.CREATE_QUESTIONS,
                            data:{
                                body:values.questionbody,
                                options:opts,
                                quesimg:this.state.questionDetails.questionimage,
                                //chapter: values.chapter,
                                material: this.state.materialId,
                            }
                        }).then((response)=>{
                            console.log(response);
                            this.setState({
                                adding:false
                            });
                            if(response.data.success){
                                this.props.ChangeQuestionModalState(false,null,'Создать вопрос');
                                Alert('success','Успешно',response.data.message);
                            }
                            else{
                                this.props.ChangeQuestionModalState(false,null,'Создать вопрос');
                                this.props.form.resetFields();
                                return Alert('warning','Предупреждение!',response.data.message);
                            }

                        }).catch((error)=>{
                            console.log(error);
                            this.props.form.resetFields();
                            this.setState({
                                adding:false,
                                questionDetails:{
                                    questionimage:null,
                                    options :[
                                        {
                                            image :null,
                                            body : null,
                                            isAnswer :false
                                        },
                                        {
                                            image :null,
                                            body : null,
                                            isAnswer :false
                                        },
                                        {
                                            image :null,
                                            body : null,
                                            isAnswer :false
                                        },
                                        {
                                            image :null,
                                            body : null,
                                            isAnswer :false
                                        },
                                        {
                                            image :null,
                                            body : null,
                                            isAnswer :false
                                        }
                                    ] ,  
                                }
                            });
                            this.props.ChangeQuestionModalState(false,null,'Создать вопрос');
                            return Alert('error','Ошибка!','Ошибка сервера');
                        })
                        
                    }
                }
                else{
                    Alert('warning','Предупреждение!','Пожалуйста, заполните все варианты');
                }
            }
        });
    };

    changeqImage = (f)=>{
        this.setState((ps,pp)=>{
            return({
                questionDetails:{
                    ...ps.questionDetails,
                    questionimage:(f.link ?`${apis.BASE}/${f.link}`:null)
                },
                submitDisabled:false
            })
        })
    }

    upl=()=>{
        this.setState({
            submitDisabled:true
        })
    }

    render() {
        const { getFieldDecorator } = this.props.form;
        const { Option } = Select;
        const { TextArea } = Input;
        const optn =['A','B','C','D','E']
        const Optiondata = this.props.trainer.questioneditdetails;
        console.log("OptionData:",Optiondata);
        console.log("Opt2",this.state.questionDetails);
        var QuestionImageprops={
            name: 'file',
            action: `${apis.BASE}${apis.FILE_UPLOAD}?Token=${auth.retriveToken()}`,
            listType: 'picture',
        }
        
        return (
            <div className="register-subject-form" >
                <div className="register-trainer-form-body">
                    <Form  onSubmit={this.handleSubmit}>
                        <div>
                            
                            <Row>
                                <Col span={18}>
                                    <Form.Item label="Вопрос" hasFeedback>
                                        {getFieldDecorator('questionbody', {
                                            initialValue : this.props.trainer.questioneditdetails.body,
                                            rules: [{ required: true, message: 'Пожалуйста, введите вопрос!' }],
                                        })(
                                            <TextArea rows={5} />
                                        )}
                                    </Form.Item>
                                </Col>
                                <Col span={6} style={{padding : '0px 20px'}}>
                                    <Form.Item label="Изображение вопроса">
                                        <Upload {...QuestionImageprops} beforeUpload={this.upl} onRemove={this.changeqImage} onSuccess={this.changeqImage}>
                                            <Button>
                                                <Icon type="upload" /> Загрузить
                                            </Button>
                                        </Upload>
                                    </Form.Item>
                                </Col>
                            </Row>
                            
                             
                            <div style={{paddingTop:'20px'}}>
                                
                                { 
                                    Optiondata.options.map((option,i)=>{
                                        return(
                                            <Row key={i} className="">
                                                <Col offset={1} span={13}>
                                                    <Form.Item label={`вариант ${i+1}`}>
                                                    {getFieldDecorator('option.optbody', {
                                                        initialValue : option.optbody,
                                                        rules: [{ required: false, message: 'Пожалуйста, введите вопрос!' }],
                                                    })(
                                                        
                                                        <TextArea value={Optiondata.options[i].optbody} onChange={ (e)=>this.OptionTextChange(e,i)} rows={3} />
                                                    )}
                                                        {/* <TextArea value={this.state.questionDetails.options[i].body} onChange={ (e)=>this.OptionTextChange(e,i)} rows={3} /> */}
                                                    </Form.Item>
                                                </Col>
                                                <Col offset={2} span={6} style={{textAlign:'center'}}>
                                                    <Form.Item label={`Картинка варианта ${i+1}`}>
                                                        <Upload {...QuestionImageprops} beforeUpload={this.upl} onRemove={(f)=>this.OptionImageonChange(null,i)} onSuccess={(f)=>this.OptionImageonChange(f,i)}>
                                                        {getFieldDecorator('option.optimg', {
                                                            initialValue : option.optimg,
                                                            rules: [{ required: false, message: 'Пожалуйста, загрузите ваш аватар!'}],
                                                        })(<img src={option.optimg} style={{width: "60px",height: "80px",}} alt=""/>)
                                                        }
                                                            
                                                            <Button>
                                                                <Icon type="upload" /> Загрузить
                                                            </Button>
                                                        </Upload>
                                                    </Form.Item>
                                                </Col>
                                                <Col span={2} style={{padding : '55px 10px'}}>
                                                    <Form.Item>
                                                    {getFieldDecorator('option.isAnswer', {
                                                        initialValue : option.isAnswer,
                                                        rules: [{ required: true, message: 'Пожалуйста, введите вопрос!' }],
                                                    })(
                                                        <Checkbox checked={Optiondata.options[i].isAnswer} onChange={(e)=>this.AnswerOptionSwitch(e,i)}/>
                                                 
                                                     )}
                                      {/* <Checkbox checked={this.state.questionDetails.options[i].isAnswer} onChange={(e)=>this.AnswerOptionSwitch(e,i)} ></Checkbox> */}
                                                   
                                                        </Form.Item>
                                                </Col>
                                            </Row>                                                
                                        )
                                    })
                                }
                                
                            </div>
                            }
                            
                            <Row>
                                <Col offset={20}  span={4}>
                                    <Form.Item>
                                        {/* <Button type="primary" htmlType="submit" disabled={this.state.submitDisabled} loading={this.state.adding} block>
                                            Создать Вопрос
                                        </Button> */}
                                        <Button type="primary" htmlType="submit" block>
                                            {this.props.trainer.Questionmode}
                                        </Button>
                                    </Form.Item>
                                </Col>
                            </Row>
                            
                        </div>
                    </Form>
                </div>
            </div>
        )
    }
}

