import React from 'react';
import './mainpage.css';
//import './jquery.min.js';
import { Button } from 'antd';
import  LocalizedStrings  from  'react-localization' ;
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Nav, Jumbotron, Container, Row, Col, Card } from 'react-bootstrap';
// import { useLocation, useNavigate } from 'react-router';
import main from './main.png';
import internet from './internet.png';
import group from './group.png';
import monitor from './monitor.png';
import spam from './spam.png';
import header from './header.jpg';
import styled from 'styled-components';
import graduate from './graduation-hat.png';
import rocket from './Rocket.png';
import laptop from './Laptop.jpg'
import learning from './learning.jpg'
import passwd from './password.png';
import cloud from './cloud-computing.png';
import monitor2 from './monitor2.png';
import group2 from './group2.png';
import uk from './uk.png';
import ru from './ru.svg.png';

const Header= styled.div`
   background: linear-gradient(180deg, white, transparent), url(${header}) no-repeat;
   height: 950px;
`

const BeforeHeader = styled.div`
   padding: 50px 50px;
`

const LaptopStyle= styled.div`
   background: url(${laptop}) no-repeat;
   height: 1000px;
   padding: 90px 60px;
`
const Bookstyle= styled.div`
   background: linear-gradient( rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5) ),url(${learning}) no-repeat;
   height: 1030px;
   padding: 90px 60px;
`

const Footer = styled.div`
   background-color:#000;
   height:200px;
`

const Button1 = styled.button`
  font-size: 24px;
  z-index: 10;
  width: 310px;
  height: 70px;
  background-color: #97C222;
  border: none;
  color:white;
  margin-top: 30px;
`


export default function Main(props){
    return(
       <>
       <Navbar collapseonSelect expand="lg" style={{ backgroundColor:'#fff', paddingTop: '20px', paddingBottom:'20px'}}>
       <Navbar.Toggle aria-control="basic-navbar-nav"/>
       <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
             {/* <li class="header-item-lang-eu"><button id='en' class="translate"><img src={uk} alt="ukflag" width="40px" height="30px"/></button></li>
             <li class="header-item-lang-ru"><button id='ru' class="translate"><img src={ru} alt="ruflag" width="40px" height="30px"/></button></li> */}
             <Nav.Link variant="primary" style={{color:'#000', fontSize:'24px', paddingRight:'100px', fontWeight:'500'}} class="lang" key="how" href="/main">Главная</Nav.Link>
             <Nav.Link variant="primary" style={{color:'#000', fontSize:'24px',paddingRight:'100px', fontWeight:'500'}} href="/route3">О проекте</Nav.Link>
             <Nav.Link variant="primary" style={{color:'#000', fontSize:'24px',paddingRight:'100px', fontWeight:'500'}}>Преимущества</Nav.Link>
          </Nav>
       </Navbar.Collapse>
    </Navbar>
 
    <Header>
       <img src={main} alt="company logo" className="c-logo" style={{paddingLeft:'50px', bottom:'20px'}}/>
      
       <div className="jumbo">
          <Container className="jumbo2" style={{paddingTop:'200px', paddingLeft:'130px', position:'absolute'}}>
             <span className="FontBold"><h1><strong>Платформа<br/>
             автоматизированного<br/>
             обучения кибербезопасности</strong></h1>
             <h5>Простой онлайн-инструмент, который поможет вашим сотрудникам<br/>
             овладеть навыками кибербезопасности</h5></span>
             <Button className='RunLearning' type="primary" htmlType="submit" block>
                <a href='/login' style={{textDecoration:'none'}}>Начать обучение</a>
             </Button>
          </Container>
       </div>
    </Header>
    </>
    )
 }
