import React, { Component, useState} from 'react';
import { connect } from 'react-redux';
import { 
    ChangeSlideSearchText,
    ChangeSlideTableData,
    ChangeSlideModalState
} from '../../../actions/chapterAction';

import Slider from 'react-touch-drag-slider'

import styled, { createGlobalStyle, css } from 'styled-components/macro'

// import SliderShow2 from './slidertest';

export default class SliderShow extends Component {
  constructor(props){
    super(props);
    this.state={
      SlidetableLoading : false,
      details : null
    }
  }

  componentDidMount(){
    // this.props.ChangeSlideTableData(this.props.id);
    var ID = this.props.id;
        SecureGet({
            url: `${apis.GET_SINGLE_MATERIAL_DETAILS}/${ID}`,
        }).then((response)=>{
            console.log(response.data.data[0]);
            this.setState({
                details : response.data.data[0],
                loading:false
            })
        }).catch((error)=>{
            console.log(error);
        })
  }
  
    render() {
        return (
            <SliderShow2 details={this.state.details}/>
      )
    }
}

// const mapStateToProps = state => ({
//     chapter : state.chapter
// });

// export default connect(mapStateToProps,{
//     ChangeSlideSearchText,
//     ChangeSlideTableData,
//     ChangeSlideModalState
// })(SliderShow);

const GlobalStyles = createGlobalStyle`
  * {
    box-sizing: border-box;
  }
  html,body {
    padding: 0;
    margin: 0;
  }
`
const AppStyles = styled.main`
  height: 82vh;
  width: 82vw;
`

const Button = styled.button`
  font-size: 2rem;
  z-index: 10;
  position: fixed;
  top: 50%;
  ${(props) =>
    props.right
      ? css`
          right: 0.5rem;
        `
      : css`
          left: 0.5rem;
        `}
`

function SliderShow2(props) {
  const [index, setIndex] = useState(0)
        var data2=[];
        props.details.map((d,i)=>{
            data2[i]=(
                {
                image: d.slimage,
                caption: d.slbody
                }
            )
        })
        console.log(data2);
        const increment = () => {
            if (index < data2.length - 1) setIndex(index + 1)
        }

        const decrement = () => {
            if (index > 0) setIndex(index - 1)
        }

  return (
    <>
      <GlobalStyles />
      <AppStyles>
        <Button onClick={decrement} left disabled={index === 0}>
          〈
        </Button>
        <Button
          onClick={increment}
          right
          disabled={index === data2.length - 1}
        >
          〉
        </Button>
        <Slider
          onSlideComplete={setIndex}
          onSlideStart={(i) => {
            console.log('started dragging on slide', i)
          }}
          activeIndex={index}
          threshHold={100}
          transition={0.3}
          scaleOnDrag={true}
        >
          {data2.map(({ image, caption }, index) => (
            <img src={image} key={index} alt={caption} />
          ))}
        </Slider>
      </AppStyles>
    </>
  )
}
