        //         // }
        //         // else{
        //         // data1.push(
        //         //     {
        //         //     image:d.slimage,
        //         //     caption:d.slbody
        //         //     }
        //         // );}
        // })
        
        console.log(data1);
        const captionStyle = {
            fontSize: '2em',
            fontWeight: 'bold',
        }
        const slideNumberStyle = {
            fontSize: '20px',
            fontWeight: 'bold',
        }
        return (
            <div className="App">
            <div style={{ textAlign: "center" }}>
                <h2>React Carousel Minimal</h2>
                <p>Easy to use, responsive and customizable carousel component for React Projects.</p>
                <div style={{
                padding: "0 20px"
                }}>
                <Carousel
                    data={data1}
                    time={2000}
                    width="850px"
                    height="500px"
                    captionStyle={captionStyle}
                    radius="10px"
                    slideNumber={true}
                    slideNumberStyle={slideNumberStyle}
                    captionPosition="bottom"
                    automatic={true}
                    dots={true}
                    pauseIconColor="white"
                    pauseIconSize="40px"
                    slideBackgroundColor="darkgrey"
                    slideImageFit="cover"
                    thumbnails={true}
                    thumbnailWidth="100px"
                    style={{
                    textAlign: "center",
                    maxWidth: "850px",
                    maxHeight: "500px",
                    margin: "40px auto",
                    }}
                />
                </div>
            </div>
            </div>
        );
        // return (
        //     <div className="admin-table-container">
        //       {/* <Table 
        //         bordered={true} 
        //         dataSource={this.props.chapter.slideTableData} 
        //         size="medium" 
