class Test extends Component {
    constructor(props){
      super(props);
      this.state={
        screen: true,
        count: 0,
        clickedLabel: {},
        answer: 0,
        error:false,
        screenFinish:false,
        second:1
      }
    }
  
    componentDidMount(){
      this.props.ChangeQuestionTableData(this.props.id);
    }

    handleClick = ()=> {
      const { count, questions, clickedLabel, answer, second } = this.state;

      if (questions.length >= count && clickedLabel.label) {
        this.setState({
          count: count + 1,
          clickedLabel: {},
          answer: clickedLabel.currectAnswer ? answer + 1 : answer,
          error: false
          // second: 1
        });
      } 
      else {
        this.setState({
          count: count,
          error: true
        });
      }

      if (questions.length === count) {
        this.setState({
          screenFinish: true
        });
      }
      this.handleCountDown();
    }

    handleCountDown = () => {
      const { second, count } = this.state;
      clearInterval(this.timer);
  
      let counter = 1;
      this.timer = setInterval(() => {
        if (counter >= 10) {
          clearInterval(this.timer);
          if (second >= 10) {
            this.setState({
              count: count + 1
            });
          }
        }
  
        this.setState({
          second: counter
        });
        console.log(counter++);
      }, 1000);
    };

    renderInitial = () => {
      const { second, count } = this.state;
  
      this.setState({
        screen: false,
        count: 1
      });
      this.handleCountDown();
    };

    renderScreenInitial = () => {
      return (
        <div>
          <div screen={this.state.screen}>
            <h2>Country Quiz</h2>
            <h4>Click to start</h4>
            <Button onClick={this.renderInitial}>Begin</Button>
          </div>
        </div>
      );
    };
  
    handleOptions = alternatives => {
      const { clickedLabel } = this.state;
  
      return alternatives.map((alternative, index) => {
        return (
          <Label
            key={index}
            onClick={() =>
              this.setState({
                clickedLabel: alternative,
                error: false
              })
            }
          >
            <Radio clickedLabel={clickedLabel.label === alternative.label}>
              <IntoRadio />
            </Radio>
            <Paragraph>{alternative.label}</Paragraph>
          </Label>
        );
      });
    };
  
    renderQuestions = () => {
      const { questions, count, error, second } = this.state;
  
      const NewQuestions = Array.from(questions);
      const removeQuestions = NewQuestions.slice(count - 1, count);
  
      return removeQuestions.map((quest, index) => (
        <Container key={index}>
          <ScreenQuiz>
            <p>{second}</p>
            <Title>{quest.question}</Title>
            <ContainerLabel>
              {this.handleOptions(quest.alternatives)}
            </ContainerLabel>
            <Button onClick={this.handleClick}>Submit</Button>
            {error && <Error>Please check an alternative.</Error>}
          </ScreenQuiz>
        </Container>
      ));
    };
  
    renderResult = () => {
      const { answer, screenFinish } = this.state;
  
      return (
        <Container>
          <ScreenQuiz screen={screenFinish}>
            <Title>you got {answer} out of 5 questions</Title>
            <Button
              onClick={() =>
                this.setState({
                  count: 0,
                  screenFinish: false,
                  screen: true,
                  answer: 0
                })
              }
            >
              Play again
            </Button>
          </ScreenQuiz>
        </Container>
      );
    };
    
    render() {
      const { screenFinish, screen } = this.state;
      var questions=[];
      props.details.map((d,i)=>{
        questions[i]=(
          {
            questionText : d.body,
            questionImage : d.quesimg,
            answerOptions: [
              { answerText: d.answer1, isCorrect: d.trueans1 },
              { answerText: d.answer2, isCorrect: d.trueans2 },
              { answerText: d.answer3, isCorrect: d.trueans3 },
              { answerText: d.answer4, isCorrect: d.trueans4 },
              { answerText: d.answer5, isCorrect: d.trueans5}
            ]
          }
        )
            
      });
  
      return !screenFinish ? (
        <>{screen ? this.renderScreenInitial() : this.renderQuestions()}</>
      ) : (
        this.renderResult()
      );
    }

