import React, { Component } from 'react'
import './newchapter.css';
import {
    Form,
    Input,
    Button,
    //Select,
    Upload,
    Icon
} from 'antd';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import { connect } from 'react-redux';
import { 
    ChangeChapterConfirmDirty,
    ChangeChapterModalState,
    ChangeChapterTableData
} from '../../../actions/chapterAction';
import Alert from '../../../components/common/alert';
import auth from '../../../services/AuthServices';
//const { Option } = Select;
class NewChapter extends Component {

    constructor(props){
        super(props);
        this.state={
            chapterDetails:{
                chapterimage1: this.props.chapter.chapterdetails.icon1,
                chapterimage2: this.props.chapter.chapterdetails.icon2,
                chapterimage3: this.props.chapter.chapterdetails.icon3,
                chapterimage4: this.props.chapter.chapterdetails.icon4,
            },
            submitDisabled:false,
        }
    }

    ChapterImageonChange = (f1, f2, f3, f4)=>{
        var newchapimg1 = this.state.chapterDetails.chapterimage1
        var newchapimg2 = this.state.chapterDetails.chapterimage2
        var newchapimg3 = this.state.chapterDetails.chapterimage3
        var newchapimg4 = this.state.chapterDetails.chapterimage4
        if(!f1 || !f2 || !f3 || !f4){
            newchapimg1=null
            newchapimg2=null
            newchapimg3=null
            newchapimg4=null
        }
        else{
            newchapimg1=`${apis.BASE}/${f1.link}`
            newchapimg2=`${apis.BASE}/${f2.link}`
            newchapimg3=`${apis.BASE}/${f3.link}`
            newchapimg4=`${apis.BASE}/${f4.link}`
        }
        this.setState({
            submitDisabled:false
        })
        if( newchapimg1==='undefined' || newchapimg1===undefined || newchapimg1===null || newchapimg1==='null' ||
            newchapimg2==='undefined' || newchapimg2===undefined || newchapimg2===null || newchapimg2==='null' ||
            newchapimg3==='undefined' || newchapimg3===undefined || newchapimg3===null || newchapimg3==='null' ||
            newchapimg4==='undefined' || newchapimg4===undefined || newchapimg4===null || newchapimg4==='null') {
            //(newOptions[i].body==='undefined' || newOptions[i].body===undefined || newOptions[i].body==='null' || newOptions[i].body==='' || newOptions[i].body===null)){
                newchapimg1=this.state.chapterDetails.chapterimage1;
                newchapimg2=this.state.chapterDetails.chapterimage2;
                newchapimg3=this.state.chapterDetails.chapterimage3;
                newchapimg4=this.state.chapterDetails.chapterimage4;    
        }
        this.setState((ps,pp)=>{
            return({
                chapterDetails:{
                    ...ps.chapterDetails,
                    chapterimage1:newchapimg1,
                    chapterimage2:newchapimg2,
                    chapterimage3:newchapimg3,
                    chapterimage4:newchapimg4,
                }
            })
        })
    }

    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
                console.log('Полученные значения формы: ', values);
                
                    SecurePost({
                        url : `${apis.CREATE_CHAPTER}`,
                        data : {
                            _id : this.props.chapter.chapterId,
                            name :values.name,                            
                            descr : values.descr,
                            icon1: this.state.chapterDetails.chapterimage1,
                            icon2: this.state.chapterDetails.chapterimage2,
                            icon3: this.state.chapterDetails.chapterimage3,
                            icon4: this.state.chapterDetails.chapterimage4,
                            materials : this.state.materialId
                        }
                    }).then((response)=>{
                        if(response.data.success){
                            this.props.ChangeChapterModalState(false,null,'Регистрация');
                            Alert('success','Успешно',response.data.message);
                            this.props.ChangeChapterTableData();
                        }
                        else{
                            console.log(response.data);
                            this.props.ChangeChapterModalState(false,null,'Регистрация');
                            return Alert('warning','Предупреждение!',response.data.message);
                        }
                    }).catch((error)=>{
                        console.log(error);
                        this.setState({
                            chapterDetails: {
                                chapterimage1: null,
                                chapterimage2: null,
                                chapterimage3: null,
                                chapterimage4: null,
                            }
                        })
                        this.props.ChangeChapterModalState(false,null,'Регистрация');
                        return Alert('error','Ошибка!','Ошибка сервера');
                    })
                
            }
        });
    };


    changeqImage = (f1,f2,f3,f4)=>{
        this.setState((ps,pp)=>{
            return({
                chapterDetails:{
                    ...ps.chapterDetails,
                    chapterimage1:(f1.link ?`${apis.BASE}/${f1.link}`:null),
                    chapterimage2:(f2.link ?`${apis.BASE}/${f2.link}`:null),
                    chapterimage3:(f3.link ?`${apis.BASE}/${f3.link}`:null),
                    chapterimage4:(f4.link ?`${apis.BASE}/${f4.link}`:null)
                },
                submitDisabled:false
            })
        })
    }

    upl=()=>{
        this.setState({
            submitDisabled: false
        })
    }
    render() {
        const { getFieldDecorator } = this.props.form;
        
        var ChapterImageprops={
            name: 'file',
            action: `${apis.BASE}${apis.FILE_UPLOAD}?Token=${auth.retriveToken()}`,
            listType: 'picture',
        }
        return (
            <div className="register-chapter-form">
                <div className="register-chapter-form-body">
                    <Form  onSubmit={this.handleSubmit}>
                        <Form.Item label="Наименование" hasFeedback className="input-admin-chapter">
                            {getFieldDecorator('name', {
                                initialValue : this.props.chapter.chapterdetails.name,
                                rules: [{ required: true, message: 'Пожалуйста, введите наименование!', whitespace: true }],
                            })(<Input />)}
                        </Form.Item>
                    
                        <Form.Item label="Описание" hasFeedback className="input-admin-chapter">
                            {getFieldDecorator('descr', {
                                initialValue : this.props.chapter.chapterdetails.descr,
                                rules: [{ required: true, message: 'Описание!', whitespace: true }],
                            })(<Input />)}
                        </Form.Item>
                        
                        <div>
                            <h3 className="input-admin-chapter">Достижения:</h3>
                        <Form.Item label="Серый" className="input-admin-chapter">
                            <Upload {...ChapterImageprops} beforeUpload={this.upl} onRemove={(f1)=>this.ChapterImageonChange(null)} onSuccess={(f1)=>this.ChapterImageonChange(f1)}>
                                {getFieldDecorator('icon1', {
                                    initialValue : this.props.chapter.chapterdetails.icon1,
                                    rules: [{ required: false, message: 'Пожалуйста, загрузите ваш аватар!'}],
                                })(<img src={this.props.chapter.chapterdetails.icon1} style={{width: "60px",height: "60px",}} alt=""/>)
                                }
                                <Button style={{marginLeft: '100px'}}>
                                    <Icon type="upload" /> Загрузить
                                </Button>
                            </Upload>
                        </Form.Item>

                        <Form.Item label="Бронза" className="input-admin-chapter">
                            <Upload {...ChapterImageprops} beforeUpload={this.upl} onRemove={(f2)=>this.ChapterImageonChange(null)} onSuccess={(f2)=>this.ChapterImageonChange(f2)}>
                                {getFieldDecorator('icon2', {
                                    initialValue : this.props.chapter.chapterdetails.icon2,
                                    rules: [{ required: false, message: 'Пожалуйста, загрузите ваш аватар!'}],
                                })(<img src={this.props.chapter.chapterdetails.icon2} style={{width: "60px",height: "60px",}} alt=""/>)
                                }
                                <Button style={{marginLeft: '100px'}}>
                                    <Icon type="upload" /> Загрузить
                                </Button>
                            </Upload>
                        </Form.Item>

                        <Form.Item label="Серебро" className="input-admin-chapter">
                            <Upload {...ChapterImageprops} beforeUpload={this.upl} onRemove={(f)=>this.ChapterImageonChange(null)} onSuccess={(f)=>this.ChapterImageonChange(f)}>
                                {getFieldDecorator('icon3', {
                                    initialValue : this.props.chapter.chapterdetails.icon3,
                                    rules: [{ required: false, message: 'Пожалуйста, загрузите достижения!'}],
                                })(<img src={this.props.chapter.chapterdetails.icon3} style={{width: "60px",height: "60px",}} alt=""/>)
                                }
                                <Button style={{marginLeft: '100px'}}>
                                    <Icon type="upload" /> Загрузить
                                </Button>
                            </Upload>
                        </Form.Item>

                        <Form.Item label="Золото" className="input-admin-chapter">
                            <Upload {...ChapterImageprops} beforeUpload={this.upl} onRemove={(f)=>this.ChapterImageonChange(null)} onSuccess={(f)=>this.ChapterImageonChange(f)}>
                                {getFieldDecorator('icon4', {
                                    initialValue : this.props.chapter.chapterdetails.icon4,
                                    rules: [{ required: false, message: 'Пожалуйста, загрузите ваш аватар!'}],
                                })(<img src={this.props.chapter.chapterdetails.icon4} style={{width: "60px",height: "60px",}} alt=""/>)
                                }
                                <Button style={{marginLeft: '100px'}}>
                                    <Icon type="upload" /> Загрузить
                                </Button>
                            </Upload>
                        </Form.Item>
                        </div>

                        <Form.Item>
                            <Button type="primary" htmlType="submit" block>
                                {this.props.chapter.Chaptermode}
                            </Button>
                        </Form.Item>
                    </Form>
                </div>
            </div>
        )
    }
}

