import React, { useState } from 'react'
import Slider from 'react-touch-drag-slider'
import styled, { createGlobalStyle, css } from 'styled-components/macro'
// import images from './images'

// define some basic styles
const GlobalStyles = createGlobalStyle`
  * {
    box-sizing: border-box;
  }
  html,body {
    padding: 0;
    margin: 0;
  }
`
// The slider will fit any size container, lets go full screen...
const AppStyles = styled.main`
  height: 100vh;
  width: 100vw;
`

// create some buttons for none drag slide change
const Button = styled.button`
  font-size: 2rem;
  z-index: 10;
  position: fixed;
  top: 50%;
  ${(props) =>
    props.right
      ? css`
          right: 0.5rem;
        `
      : css`
          left: 0.5rem;
        `}
`

function SliderShow() {

  const [index, setIndex] = useState(1)
  var data1=[{image: 'https://images.unsplash.com/photo-1609952048180-7b35ea6b083b?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80', caption: 'assas'},
        {image: 'https://images.unsplash.com/photo-1608241175281-722a1c6111be?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=634&q=80', caption: 'asa'}];

        var data2=[];

        this.props.chapter.slideTableData.map((d,i)=>{
            
            data2[i]=(
                {
                image: d.slimage,
                caption: d.slbody
                }
            )
        })
        console.log(data2);
  const increment = () => {
    if (index < data1.length - 1) setIndex(index + 1)
  }

  const decrement = () => {
    if (index > 0) setIndex(index - 1)
  }

  return (
    <>
      <GlobalStyles />
      <AppStyles>
        <Button onClick={decrement} left disabled={index === 0}>
          〈
        </Button>
        <Button
          onClick={increment}
          right
          disabled={index === data1.length - 1}
        >
          〉
        </Button>
        <Slider
          onSlideComplete={setIndex}
          onSlideStart={(i) => {
            console.log('started dragging on slide', i)
          }}
          activeIndex={index}
          threshHold={100}
          transition={0.3}
          scaleOnDrag={true}
        >
          {data1.map(({ image, caption }, index) => (
            <img src={iamge} key={index} alt={caption} />
          ))}
        </Slider>
      </AppStyles>
    </>
  )
}

export default SliderShow
