import React, { Component } from 'react';
import { Table, Input, Button, Typography, Divider, Icon, Modal, Popconfirm } from 'antd';
import Highlighter from 'react-highlight-words';
import { connect } from 'react-redux';
import { 
    ChangeSlideSearchText,
    ChangeSlideTableData,
    ChangeSlideModalState
} from '../../../actions/chapterAction';
import '../Division/alldivision.css'
import Alert from '../../../components/common/alert';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import { Carousel } from 'react-carousel-minimal';

class Slider extends Component {
  constructor(props){
    super(props);
    this.state={
      SlidetableLoading : false,
      data2 : [],
    //   carouselData : [
    //       {
            
    //       }
    //   ]
    }
  }

//   openModal = (id,mode)=>{
//     this.props.ChangeSlideModalState(true,id,mode);
//   }

//   closeModal = ()=>{
//     this.props.ChangeSlideModalState(false,null,'Регистрация');
//   }

  componentDidMount(){
    this.props.ChangeSlideTableData(this.props.id);
    this.props.chapter.slideTableData.map((d,i)=>{
                
        //if(i==0){
                 //   data1[0].image = d.slimage;
                 //   data1[0].caption = d.slbody
               // }
               // else{


//if(i==0){
                this.state.data2[i]=(
                    {
                    image:d.slimage,
                    caption:d.slbody
                    }
                )//} 
                
        })
  }


    
    render() {
        const data = this.state.data2;
        // var data1 = [...this.state.carouselData];
        var data1=[{image: 'http://localhost:5000/uploads/file-1656324298779-pr_1530560406.jpg', caption: 'assas'},
        {image: 'http://localhost:5000/uploads/file-1656324960654-pr_1530560406.jpg', caption: 'asa'}];

        
        // console.log('df',data1);

        // const data = [...this.state.carouselData];
        // {
        //     Object.entries(data).map((d,i)=>
        //         `${d.image} : dsds`,
        //     )
        // }
        // console.log('dsd',this.state.carouselData);
        // {data1.map((d,i)=>
        //  data.push(data1)
        // )}

        // var data = [this.props.chapter.slideTableData];
        // data1 = {
        //     image:data.slimage,
        //     caption:data.slbody
        // }
        
            
        
        // console.log('12',data1);
        // console.log('34',data2);
        const captionStyle = {
            fontSize: '2em',
            fontWeight: 'bold',
        }
        const slideNumberStyle = {
            fontSize: '20px',
            fontWeight: 'bold',
        }
        return (
            <div style={{ textAlign: "center" }}>
                <h2>React Carousel Minimal</h2>
                <p>Easy to use, responsive and customizable carousel component for React Projects.</p>
                {/* <div style={{
                padding: "0 20px"
                }}> */}
                <Carousel
                    data={data}
                    time={2000}
                    width="850px"
                    height="500px"
                    captionStyle={captionStyle}
                    radius="10px"
                    infiniteLoop={true}
                    slideNumber={true}
                    slideNumberStyle={slideNumberStyle}
                    captionPosition="bottom"
                    //automatic={true}
                   // dots={true}
                    pauseIconColor="white"
                    pauseIconSize="40px"
                    slideBackgroundColor="darkgrey"
                    slideImageFit="cover"
                    //thumbnails={true}
                    thumbnailWidth="100px"
                    style={{
                    textAlign: "center",
                    maxWidth: "850px",
                    maxHeight: "500px",
                    margin: "40px auto",
                    }}
                >
                    <div>dgf</div>
                </Carousel>
                {/* </div> */}
            </div>
      
        );
        // return (
        //     <div className="admin-table-container">
        //       {/* <Table 
        //         bordered={true} 
        //         dataSource={this.props.chapter.slideTableData} 
        //         size="medium" 
        //         pagination={{ pageSize: 5 }}
        //         loading={this.props.chapter.slideTableLoadingStatus}
        //         rowKey="_id" 
        //       />; */}
        //     </div>
        // )
    }
}

const mapStateToProps = state => ({
    admin : state.admin,
    chapter : state.chapter
});

export default connect(mapStateToProps,{
    ChangeSlideSearchText,
    ChangeSlideTableData,
    ChangeSlideModalState
})(Slider);
