        })     
    }

    AnswerOptionSwitch4 = (e)=>{
        if((this.state.questionDetails.answer4!=='' && this.state.questionDetails.answer5!==null)){
            var newOption4 = this.state.questionDetails.trueans4
            newOption4 = e.target.checked
            this.setState((ps,pp)=>{
                return({
                    questionDetails:{
                        ...ps.questionDetails,
