import React, { Component, useState } from 'react'
import Slider from 'react-touch-drag-slider'
import { connect } from 'react-redux';
import { Table, Input, Button, Typography, Divider, Icon, Modal, Popconfirm } from 'antd';
import Highlighter from 'react-highlight-words';
import styled, { createGlobalStyle, css } from 'styled-components/macro'
import { 
    ChangeQuestionSearchText,
    ChangeQuestionTableData,
    ChangeQuestionModalState
} from '../../../actions/trainerAction';
import Alert from '../../../components/common/alert';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import Test2 from './test2';
import Test3 from './test3';


class Test extends Component {
    constructor(props){
      super(props);
      this.state={
        details : this.props.trainer.QuestionTableData,
        isStarted: false,
      }
    }
  
    componentDidMount(){
      this.props.ChangeQuestionTableData(this.props.id);
    }

    handleClick = ()=> {
      console.log("A:", this.state.isStarted);
      this.setState(!this.state.isStarted);
    }
    
    render() {
      console.log('qas',this.props.trainer.QuestionTableData);
      console.log('qaz',this.state);
      return (
        <div>
          <h1>Quiz Game!</h1>
          {this.state.isStarted ? null : (
            <div id="button">
              <Button variant="contained" color="secondary" onClick={()=>this.handleClick}>
                Click To Start
              </Button>
            </div>
          )}
        </div>
        // <Test2 details={this.props.trainer.QuestionTableData}/>
      )
    }
  }
  
  const mapStateToProps = state => ({
      chapter : state.chapter,
      trainer : state.trainer
  });
  
  export default connect(mapStateToProps,{
      ChangeQuestionSearchText,
      ChangeQuestionTableData,
      ChangeQuestionModalState
  })(Test);
